import React, { useState, useContext } from 'react';
import { Eye, XCircle } from 'lucide-react';
import { ContextoAuth } from '../../context/AuthContext';
import { Button, Modal, Table } from '../../components/ui/ComponentesUI';
import { useFiltrosYBusqueda } from '../../hooks/useFiltrosYBusqueda';
import { toast } from 'sonner';

const API_URL = 'http://127.0.0.1:8000/api';

// --- Helper para formatear la moneda consistentemente ---
const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-AR', {
        style: 'currency',
        currency: 'ARS'
    }).format(amount);
};

// --- COMPONENTES Y LÓGICA DE FILTRADO (Con retoques de estilo) ---
const FiltrosVentas = ({ setFiltros, filtros, resetFiltros }) => {
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFiltros(prev => ({ ...prev, [name]: value }));
    };
    return (
        // --- ESTILO MODIFICADO: Panel más definido con borde y sombra sutil ---
        <div className="flex flex-wrap items-end gap-4 mb-4 p-4 bg-white border rounded-lg shadow-sm">
            <div className="flex-grow">
                <label htmlFor="fechaDesde" className="block text-sm font-medium text-gray-700">Desde</label>
                <input id="fechaDesde" type="date" name="fechaDesde" value={filtros.fechaDesde} onChange={handleInputChange} className="mt-1 p-2 border rounded-lg w-full"/>
            </div>
            <div className="flex-grow">
                <label htmlFor="fechaHasta" className="block text-sm font-medium text-gray-700">Hasta</label>
                <input id="fechaHasta" type="date" name="fechaHasta" value={filtros.fechaHasta} onChange={handleInputChange} className="mt-1 p-2 border rounded-lg w-full"/>
            </div>
            <div className="flex-grow">
                <label htmlFor="estado" className="block text-sm font-medium text-gray-700">Estado</label>
                <select id="estado" name="estado" value={filtros.estado} onChange={handleInputChange} className="mt-1 p-2 border rounded-lg w-full">
                    <option value="Todos">Todos</option>
                    <option value="Completada">Completada</option>
                    <option value="Cancelada">Cancelada</option>
                </select>
            </div>
            <Button onClick={resetFiltros} variant="secondary">Limpiar Filtros</Button>
        </div>
    );
};
const logicaFiltroVentas = (ventas, filtros) => {
    const { fechaDesde, fechaHasta, estado } = filtros;
    return ventas.filter(venta => {
        const fechaVenta = new Date(venta.date_time);
        const desde = fechaDesde ? new Date(fechaDesde + 'T00:00:00') : null;
        const hasta = fechaHasta ? new Date(fechaHasta + 'T23:59:59') : null;
        if (desde && fechaVenta < desde) return false;
        if (hasta && fechaVenta > hasta) return false;
        if (estado !== 'Todos' && venta.status !== estado) return false;
        return true;
    });
};

// --- COMPONENTE PRINCIPAL ---
const GestionVentas = ({ ventas, obtenerDatos }) => {
    const [ventaSeleccionada, setVentaSeleccionada] = useState(null);
    const { tokensAuth } = useContext(ContextoAuth);

    const { datosPaginados, FiltrosUI, PaginacionUI } = useFiltrosYBusqueda({
        items: ventas,
        itemsPorPagina: 15,
        logicaDeFiltro: logicaFiltroVentas,
        ComponenteFiltros: FiltrosVentas,
        filtrosIniciales: { fechaDesde: '', fechaHasta: '', estado: 'Todos' }
    });

    const cancelarVenta = async (idVenta) => {
        const ejecutarCancelacion = () => {
            const promesa = fetch(`${API_URL}/sales/${idVenta}/cancel/`, {
                method: 'PATCH',
                headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) }
            }).then(res => {
                if (!res.ok) return res.json().then(err => Promise.reject(err));
                return res.json();
            });

            toast.promise(promesa, {
                loading: 'Cancelando venta...',
                success: (data) => {
                    obtenerDatos();
                    return data.detail || 'Venta cancelada con éxito.';
                },
                error: (err) => `Error: ${err.detail || 'No se pudo cancelar la venta.'}`
            });
        };

        toast("Confirmar Cancelación", {
            description: `¿Seguro que quieres cancelar la venta #${idVenta}? El stock de los productos será restaurado.`,
            action: { label: "Sí, cancelar", onClick: () => ejecutarCancelacion() },
            cancel: { label: "No" },
        });
    };

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Historial de Ventas</h1>
            
            {FiltrosUI}

            <div className="bg-white rounded-lg border shadow-sm overflow-hidden">
                {datosPaginados.length > 0 ? (
                    <>
                        <Table
                            // --- ESTILO MODIFICADO: Se añade un renderizador para la cabecera ---
                            renderHeader={() => (
                                <thead className="bg-gray-50">
                                    <tr>
                                        {['ID', 'Fecha y Hora', 'Usuario', 'Items', 'Total', 'Estado', 'Acciones'].map(h => (
                                            <th key={h} className="px-6 py-3 text-left text-xs font-bold text-gray-500 uppercase tracking-wider">{h}</th>
                                        ))}
                                    </tr>
                                </thead>
                            )}
                            data={datosPaginados}
                            renderRow={(venta) => (
                                // --- ESTILO MODIFICADO: Filas con colores alternos y hover más sutil ---
                                <tr key={venta.id} className="border-b last:border-b-0 even:bg-gray-50/50 hover:bg-blue-50/50">
                                    <td className="px-6 py-4 font-medium text-gray-600">#{venta.id}</td>
                                    <td className="px-6 py-4 text-gray-500">{new Date(venta.date_time).toLocaleString('es-AR')}</td>
                                    <td className="px-6 py-4 text-gray-800">{venta.user}</td>
                                    <td className="px-6 py-4 text-gray-500 text-center">{venta.details.length}</td>
                                    <td className="px-6 py-4 font-bold text-gray-800">{formatCurrency(parseFloat(venta.final_amount))}</td>
                                    <td className="px-6 py-4">
                                        <span className={`px-2 py-1 rounded-full text-xs font-semibold ${
                                            venta.status === 'Completada' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                                        }`}>{venta.status}</span>
                                    </td>
                                    <td className="px-6 py-4 flex gap-1">
                                        <Button onClick={() => setVentaSeleccionada(venta)} variant="icon" title="Ver Detalle"><Eye className="h-5 w-5"/></Button>
                                        {venta.status === 'Completada' && (
                                            <Button onClick={() => cancelarVenta(venta.id)} variant="icon-danger" title="Cancelar Venta"><XCircle className="h-5 w-5"/></Button>
                                        )}
                                    </td>
                                </tr>
                            )}
                        />
                        {PaginacionUI}
                    </>
                ) : (
                    <div className="text-center py-16 px-6">
                        <h3 className="text-lg font-semibold text-gray-700">No se encontraron ventas</h3>
                        <p className="text-gray-500 mt-1">Intenta cambiar los filtros para encontrar resultados.</p>
                    </div>
                )}
            </div>

            {/* --- ESTILO MODIFICADO: Modal reorganizado para mayor claridad --- */}
            {ventaSeleccionada && (
                <Modal isOpen={!!ventaSeleccionada} onClose={() => setVentaSeleccionada(null)} title={`Detalle de Venta #${ventaSeleccionada.id}`}>
                    <div className="space-y-4">
                        <div className="p-3 bg-gray-50 rounded-lg border">
                            <p className="text-sm text-gray-600"><strong>Fecha:</strong> {new Date(ventaSeleccionada.date_time).toLocaleString('es-AR')}</p>
                            <p className="text-sm text-gray-600"><strong>Vendido por:</strong> {ventaSeleccionada.user}</p>
                            <p className="text-sm text-gray-600"><strong>Método de Pago:</strong> {ventaSeleccionada.payment_method}</p>
                        </div>
                        <div>
                            <h4 className="font-semibold text-gray-800 mb-2">Detalle de Productos</h4>
                            <div className="space-y-2">
                                {ventaSeleccionada.details.map(detalle => (
                                    <div key={detalle.product.id} className="flex justify-between items-center p-2 bg-gray-50 rounded-md text-sm">
                                        <div>
                                            <span className="font-semibold text-gray-700">{detalle.product.name}</span>
                                            <span className="text-gray-500 ml-2">({detalle.quantity} x {formatCurrency(parseFloat(detalle.unit_price))})</span>
                                        </div>
                                        <span className="font-medium text-gray-800">{formatCurrency(detalle.quantity * parseFloat(detalle.unit_price))}</span>
                                    </div>
                                ))}
                            </div>
                        </div>
                        <div className="mt-4 pt-4 border-t text-right space-y-1">
                             <p className="text-md">Subtotal: <span className="font-medium">{formatCurrency(parseFloat(ventaSeleccionada.total_amount))}</span></p>
                             <p className="text-md text-gray-600">Ajuste/Descuento: <span className="font-medium">{formatCurrency(parseFloat(ventaSeleccionada.final_amount) - parseFloat(ventaSeleccionada.total_amount))}</span></p>
                             <p className="font-bold text-xl text-blue-600">Total Final: {formatCurrency(parseFloat(ventaSeleccionada.final_amount))}</p>
                        </div>
                    </div>
                </Modal>
            )}
        </div>
    );
};

export default GestionVentas;
