import React, { useState, useMemo, useEffect } from 'react';
import { Search, ShoppingCart, Trash2, CheckCircle, XCircle, Plus, Minus, Star } from 'lucide-react';
import { Button, Modal, Card } from '../../components/ui/ComponentesUI';
import { toast } from 'sonner';

const ItemCarrito = ({ item, onUpdate, onRemove }) => {
    const handleQuantityChange = (e) => {
        const value = e.target.value;
        onUpdate(item.id, value === '' ? '' : parseInt(value, 10));
    };

    return (
        <div className="flex items-center gap-2 py-3">
            <div className="flex-grow">
                <p className="font-semibold text-gray-800 leading-tight">{item.name}</p>
                <p className="text-sm text-gray-500">${item.price.toFixed(2)}</p>
            </div>
            <div className="flex items-center border border-gray-300 rounded-lg">
                <Button onClick={() => onUpdate(item.id, (item.quantity || 1) - 1)} variant="ghost" size="icon" className="h-8 w-8 text-gray-600 rounded-r-none hover:bg-gray-200"><Minus size={16} /></Button>
                <input 
                    type="number"
                    value={item.quantity}
                    onChange={handleQuantityChange}
                    onFocus={(e) => e.target.select()}
                    className="w-12 text-center font-bold border-y-0 border-x text-gray-800 focus:ring-0"
                    style={{boxShadow: 'none'}}
                />
                <Button onClick={() => onUpdate(item.id, (item.quantity || 0) + 1)} variant="ghost" size="icon" className="h-8 w-8 text-gray-600 rounded-l-none hover:bg-gray-200"><Plus size={16} /></Button>
            </div>
            <p className="font-bold w-24 text-right">${(item.price * (item.quantity || 0)).toFixed(2)}</p>
            <Button onClick={onRemove} variant="ghost" size="icon" className="text-gray-400 hover:text-red-500"><Trash2 size={18} /></Button>
        </div>
    );
};

const PuntoDeVenta = ({ productosActivos, metodosDePago, onVentaCompleta, productosPopulares }) => {
    const [carrito, setCarrito] = useState([]);
    const [terminoBusqueda, setTerminoBusqueda] = useState('');
    const [modalPagoAbierto, setModalPagoAbierto] = useState(false);
    const [efectivoRecibido, setEfectivoRecibido] = useState(0);
    const [idMetodoPagoSeleccionado, setIdMetodoPagoSeleccionado] = useState('');

    useEffect(() => { if (metodosDePago && metodosDePago.length > 0) { const metodoEfectivo = metodosDePago.find(m => m.name.toLowerCase().includes('efectivo')) || metodosDePago[0]; setIdMetodoPagoSeleccionado(metodoEfectivo.id); } else { setIdMetodoPagoSeleccionado(''); } }, [metodosDePago]);
    
    const agregarACarrito = (producto) => { if (producto.stock <= 0) { toast.error(`"${producto.name}" no tiene stock.`); return; } setCarrito(carritoActual => { const itemExistente = carritoActual.find(item => item.id === producto.id); if (itemExistente) { if (itemExistente.quantity >= producto.stock) { toast.warning('Stock máximo alcanzado.'); return carritoActual; } return carritoActual.map(item => item.id === producto.id ? { ...item, quantity: item.quantity + 1 } : item); } return [...carritoActual, { ...producto, quantity: 1, price: parseFloat(producto.sale_price) }]; }); };
    
    const actualizarCantidad = (productoId, cantidad) => {
        setCarrito(carritoActual => {
            const itemAjustar = carritoActual.find(item => item.id === productoId);
            if (!itemAjustar) return carritoActual;
            if (cantidad === '' || isNaN(cantidad)) { return carritoActual.map(item => item.id === productoId ? { ...item, quantity: '' } : item); }
            if (cantidad <= 0) { return carritoActual.filter(item => item.id !== productoId); }
            if (cantidad > itemAjustar.stock) { toast.warning(`Stock máximo (${itemAjustar.stock}) alcanzado.`); return carritoActual.map(item => item.id === productoId ? { ...item, quantity: itemAjustar.stock } : item); }
            return carritoActual.map(item => item.id === productoId ? { ...item, quantity: cantidad } : item);
        });
    };

    const productosFiltrados = useMemo(() => { if (!terminoBusqueda) return []; return productosActivos.filter(p => p.name.toLowerCase().includes(terminoBusqueda.toLowerCase()) || p.sku.toLowerCase().includes(terminoBusqueda.toLowerCase())); }, [productosActivos, terminoBusqueda]);

    const handleKeyDown = (event) => { if (event.key === 'Enter') { event.preventDefault(); if (productosFiltrados.length === 1) { agregarACarrito(productosFiltrados[0]); setTerminoBusqueda(''); } else if (productosFiltrados.length > 1) { toast.info("Múltiples productos encontrados. Selecciona uno de la lista."); } else { toast.error("Ningún producto encontrado."); } } };
    
    const limpiarVenta = () => { if (carrito.length === 0) return; toast("Limpiar Venta", { description: "¿Estás seguro de que quieres limpiar la venta actual? Se vaciará el carrito.", action: { label: "Sí, limpiar", onClick: () => { setCarrito([]); setTerminoBusqueda(''); toast.success("Venta limpiada."); } }, cancel: { label: "No" }, }); };
    const procesarPago = () => { if (carrito.length === 0) { toast.error("El carrito está vacío."); return; } if (!idMetodoPagoSeleccionado) { toast.error("Por favor, selecciona un método de pago."); return; } onVentaCompleta(carrito, totalFinal, idMetodoPagoSeleccionado); setCarrito([]); setTerminoBusqueda(''); setModalPagoAbierto(false); setEfectivoRecibido(0); };
    const subtotal = useMemo(() => carrito.reduce((sum, item) => sum + item.price * (item.quantity || 0), 0), [carrito]);
    const metodoSeleccionado = useMemo(() => metodosDePago.find(pm => pm.id === parseInt(idMetodoPagoSeleccionado)), [metodosDePago, idMetodoPagoSeleccionado]);
    const porcentajeAjuste = useMemo(() => metodoSeleccionado ? parseFloat(metodoSeleccionado.adjustment_percentage) : 0, [metodoSeleccionado]);
    const totalFinal = useMemo(() => subtotal * (1 + porcentajeAjuste / 100), [subtotal, porcentajeAjuste]);
    const vuelto = useMemo(() => efectivoRecibido > totalFinal ? efectivoRecibido - totalFinal : 0, [efectivoRecibido, totalFinal]);

    return (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-full">
            <div className="lg:col-span-2 bg-white p-4 rounded-xl shadow-md flex flex-col">
                <div className="relative mb-4">
                    <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={20}/>
                    <input type="text" placeholder="Buscar o escanear producto por nombre o SKU..." className="w-full pl-10 pr-4 py-3 border rounded-lg text-lg" value={terminoBusqueda} onChange={(e) => setTerminoBusqueda(e.target.value)} onKeyDown={handleKeyDown}/>
                </div>
                <h3 className="font-semibold text-gray-600 px-2 mb-2 flex items-center gap-2">{terminoBusqueda ? <Search size={16}/> : <Star size={16} className="text-yellow-500"/>} {terminoBusqueda ? 'Resultados de Búsqueda' : 'Productos Populares'}</h3>

                <div className="flex-grow overflow-y-auto pr-2">
                    {(terminoBusqueda ? productosFiltrados : productosPopulares).length > 0 ? 
                        (terminoBusqueda ? productosFiltrados : productosPopulares).map(producto => (
                            <div key={producto.id} onClick={() => agregarACarrito(producto)} className={`flex justify-between items-center p-3 mb-2 rounded-lg border-b text-left ${producto.stock > 0 ? 'cursor-pointer hover:bg-blue-50' : 'bg-gray-200 opacity-60'}`}>
                                <div><p className="font-bold text-gray-800">{producto.name}</p><p className={`text-xs ${producto.stock <= 5 ? 'text-red-500 font-semibold' : 'text-gray-500'}`}>Stock: {producto.stock}</p></div>
                                <p className="text-lg font-semibold text-gray-900">${parseFloat(producto.sale_price).toFixed(2)}</p>
                            </div>
                        )) : 
                        <div className="text-center py-16"><h3 className="text-lg font-semibold text-gray-700">No se encontraron productos</h3>{terminoBusqueda && <p className="text-gray-500 mt-1">Intenta con otro término.</p>}</div>
                    }
                </div>
            </div>

            <Card className="flex flex-col">
                <div className="flex justify-between items-center mb-4"><h2 className="text-2xl font-bold text-gray-800 flex items-center gap-2"><ShoppingCart size={24}/> Venta Actual</h2><Button onClick={limpiarVenta} variant="ghost" size="icon" className="text-gray-500 hover:text-red-600"><XCircle size={22} /></Button></div>
                <div className="flex-grow overflow-y-auto border-t border-b -mx-6 px-6 py-2 divide-y min-h-0">
                    {carrito.length === 0 ? <p className="text-gray-500 text-center py-10">El carrito está vacío</p> : carrito.map(item => <ItemCarrito key={item.id} item={item} onUpdate={actualizarCantidad} onRemove={() => setCarrito(carrito.filter(c => c.id !== item.id))} />)}
                </div>
                <div className="mt-auto pt-4">
                    <div className="space-y-2 text-lg mb-4"><div className="flex justify-between"><span className="text-gray-600">Subtotal:</span><span className="font-semibold">${subtotal.toFixed(2)}</span></div>{porcentajeAjuste !== 0 && (<div className={`flex justify-between ${porcentajeAjuste < 0 ? 'text-green-600' : 'text-red-600'}`}><span>Ajuste ({porcentajeAjuste > 0 ? '+' : ''}{porcentajeAjuste}%):</span><span className="font-semibold">${(totalFinal - subtotal).toFixed(2)}</span></div>)}<div className="flex justify-between items-center text-3xl font-bold border-t pt-2 mt-2"><span className="text-gray-900">TOTAL:</span><span className="text-blue-600">${totalFinal.toFixed(2)}</span></div></div>
                    <Button onClick={() => setModalPagoAbierto(true)} className="w-full text-lg py-3" variant="success" icon={CheckCircle} disabled={carrito.length === 0}>Cobrar</Button>
                </div>
            </Card>

            <Modal isOpen={modalPagoAbierto} onClose={() => setModalPagoAbierto(false)} title="Procesar Pago">
                <div className="space-y-4 text-gray-800">
                    <p className="text-right text-lg">Subtotal: <span className="font-semibold">${subtotal.toFixed(2)}</span></p>
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">Método de Pago</label>
                        <select value={idMetodoPagoSeleccionado} onChange={(e) => setIdMetodoPagoSeleccionado(e.target.value)} className="w-full mt-1 p-2 border rounded-lg bg-gray-50 text-gray-900">
                            {metodosDePago.length === 0 ? (<option value="">No hay métodos</option>) : (metodosDePago.map(pm => <option key={pm.id} value={pm.id}>{pm.name} ({pm.adjustment_percentage > 0 ? '+' : ''}{pm.adjustment_percentage}%)</option>))}
                        </select>
                    </div>
                    {porcentajeAjuste !== 0 && (<p className={`text-right text-lg font-medium ${porcentajeAjuste < 0 ? 'text-green-600' : 'text-red-600'}`}>Ajuste: {porcentajeAjuste > 0 ? '+' : ''}{porcentajeAjuste.toFixed(2)}%</p>)}
                    <div className="text-center bg-blue-50 text-blue-700 p-4 rounded-lg my-4"><p className="text-sm uppercase font-bold">Total a Pagar</p><p className="text-5xl font-bold">${totalFinal.toFixed(2)}</p></div>
                    {metodoSeleccionado?.name.toLowerCase().includes('efectivo') && (
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">Efectivo Recibido</label>
                            <input type="number" value={efectivoRecibido || ''} onChange={(e) => setEfectivoRecibido(parseFloat(e.target.value) || 0)} className="w-full mt-1 p-2 border rounded-lg text-lg text-gray-900" placeholder="0.00" />
                            {efectivoRecibido >= totalFinal && <p className="text-xl font-semibold mt-3 text-green-600 text-center">Vuelto: ${vuelto.toFixed(2)}</p>}
                        </div>
                    )}
                    <div className="flex justify-end gap-3 pt-4 border-t mt-4"><Button onClick={() => setModalPagoAbierto(false)} variant="secondary">Cancelar</Button><Button onClick={procesarPago} variant="success">Finalizar Venta</Button></div>
                </div>
            </Modal>
        </div>
    );
};

export default PuntoDeVenta;
