import React, { useState, useMemo, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Search, PlusCircle, Trash2, Edit, Users, ShoppingCart, DollarSign, Package, Truck, BarChart2, AlertCircle, UserPlus, LogOut, ChevronDown, CheckCircle } from 'lucide-react';

// --- URL de nuestra API de Django ---
const API_URL = 'http://127.0.0.1:8000/api';

// --- Componentes de la UI (Reutilizables, sin cambios) ---

const Card = ({ children, className = '' }) => (
  <div className={`bg-white rounded-xl shadow-md p-6 ${className}`}>
    {children}
  </div>
);

const Button = ({ children, onClick, className = '', variant = 'primary', icon: Icon }) => {
  const baseClasses = 'px-4 py-2 rounded-lg font-semibold flex items-center justify-center gap-2 transition-transform transform hover:scale-105';
  const variants = {
    primary: 'bg-indigo-600 text-white hover:bg-indigo-700',
    secondary: 'bg-gray-200 text-gray-800 hover:bg-gray-300',
    danger: 'bg-red-500 text-white hover:bg-red-600',
    success: 'bg-green-500 text-white hover:bg-green-600',
  };
  return (
    <button onClick={onClick} className={`${baseClasses} ${variants[variant]} ${className}`}>
      {Icon && <Icon size={18} />}
      {children}
    </button>
  );
};

const Modal = ({ children, isOpen, onClose, title }) => {
    if (!isOpen) return null;
    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center">
            <div className="bg-white rounded-xl shadow-2xl w-full max-w-lg p-6 relative animate-fade-in-down">
                <h2 className="text-2xl font-bold text-gray-800 mb-4">{title}</h2>
                <button onClick={onClose} className="absolute top-4 right-4 text-gray-500 hover:text-gray-800">
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path></svg>
                </button>
                {children}
            </div>
        </div>
    );
};

// --- Módulo 1: Punto de Venta (El Frente de Batalla) ---
const PuntoDeVenta = ({ products, onSaleComplete }) => {
  const [cart, setCart] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('Efectivo');
  const [isPaymentModalOpen, setPaymentModalOpen] = useState(false);
  const [cashReceived, setCashReceived] = useState(0);

  const filteredProducts = products.filter(p => 
    p.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
    p.sku.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const addToCart = (product) => {
    setCart(currentCart => {
      const existingItem = currentCart.find(item => item.id === product.id);
      if (existingItem) {
        return currentCart.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...currentCart, { ...product, quantity: 1, price: parseFloat(product.sale_price) }];
    });
  };

  const removeFromCart = (productId) => {
    setCart(currentCart => {
        const item = currentCart.find(item => item.id === productId);
        if (item && item.quantity > 1) {
            return currentCart.map(i => i.id === productId ? {...i, quantity: i.quantity - 1} : i);
        }
        return currentCart.filter(item => item.id !== productId);
    });
  };

  const total = useMemo(() => cart.reduce((sum, item) => sum + item.price * item.quantity, 0), [cart]);
  const change = useMemo(() => cashReceived > total ? cashReceived - total : 0, [cashReceived, total]);

  const handleProcessPayment = () => {
    if (cart.length === 0) {
        alert("El carrito está vacío.");
        return;
    }
    // Lógica para completar la venta
    onSaleComplete(cart, total);
    setCart([]);
    setSearchTerm('');
    setPaymentModalOpen(false);
    setCashReceived(0);
    // Aquí se enviaría el ticket por email/whatsapp o se imprimiría.
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[calc(100vh-100px)]">
      {/* Columna de Productos */}
      <div className="lg:col-span-2 bg-gray-50 p-4 rounded-xl">
        <div className="relative mb-4">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={20}/>
          <input
            type="text"
            placeholder="Buscar producto por nombre o SKU..."
            className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 overflow-y-auto h-[calc(100%-60px)] pr-2">
          {filteredProducts.map(product => (
            <div key={product.id} onClick={() => addToCart(product)} className="bg-white p-3 rounded-lg shadow-sm cursor-pointer hover:shadow-lg hover:border-indigo-500 border-2 border-transparent transition-all text-center">
              <p className="font-bold text-gray-800 truncate">{product.name}</p>
              <p className="text-sm text-gray-600">${parseFloat(product.sale_price).toFixed(2)}</p>
              <p className={`text-xs ${product.stock <= 5 ? 'text-red-500 font-bold' : 'text-gray-500'}`}>Stock: {product.stock}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Columna de Carrito y Pago */}
      <Card className="flex flex-col">
        <h2 className="text-2xl font-bold text-gray-800 mb-4 flex items-center gap-2"><ShoppingCart size={24}/> Venta Actual</h2>
        <div className="flex-grow overflow-y-auto border-t border-b py-2">
          {cart.length === 0 ? (
            <p className="text-gray-500 text-center py-10">El carrito está vacío</p>
          ) : (
            cart.map(item => (
              <div key={item.id} className="flex justify-between items-center mb-2 p-2 rounded-md hover:bg-gray-100">
                <div>
                  <p className="font-semibold">{item.name}</p>
                  <p className="text-sm text-gray-600">${item.price.toFixed(2)} x {item.quantity}</p>
                </div>
                <div className="flex items-center gap-2">
                    <p className="font-bold">${(item.price * item.quantity).toFixed(2)}</p>
                    <button onClick={() => removeFromCart(item.id)} className="text-red-500 hover:text-red-700">
                        <Trash2 size={18} />
                    </button>
                </div>
              </div>
            ))
          )}
        </div>
        <div className="mt-auto pt-4">
          <div className="flex justify-between text-2xl font-bold mb-4">
            <span>TOTAL:</span>
            <span>${total.toFixed(2)}</span>
          </div>
          <Button onClick={() => setPaymentModalOpen(true)} className="w-full text-lg" variant="success" icon={CheckCircle}>
            Cobrar
          </Button>
        </div>
      </Card>

      {/* Modal de Pago */}
      <Modal isOpen={isPaymentModalOpen} onClose={() => setPaymentModalOpen(false)} title="Procesar Pago">
          <div className="space-y-4">
              <div className="text-center text-4xl font-bold text-indigo-600 mb-4">${total.toFixed(2)}</div>
              <div>
                  <label className="font-semibold text-gray-700">Método de Pago</label>
                  <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)} className="w-full mt-1 p-2 border rounded-lg">
                      <option>Efectivo</option>
                      <option>Tarjeta de Crédito/Débito</option>
                      <option>Mercado Pago/QR</option>
                  </select>
              </div>
              {paymentMethod === 'Efectivo' && (
                  <div>
                      <label className="font-semibold text-gray-700">Efectivo Recibido</label>
                      <input 
                        type="number" 
                        value={cashReceived}
                        onChange={(e) => setCashReceived(parseFloat(e.target.value) || 0)}
                        className="w-full mt-1 p-2 border rounded-lg"
                        placeholder="0.00"
                      />
                      {cashReceived > total && (
                          <p className="text-lg font-semibold mt-2 text-green-600">Vuelto: ${change.toFixed(2)}</p>
                      )}
                  </div>
              )}
              <div className="flex justify-end gap-3 pt-4">
                  <Button onClick={() => setPaymentModalOpen(false)} variant="secondary">Cancelar</Button>
                  <Button onClick={handleProcessPayment} variant="success">Finalizar Venta</Button>
              </div>
          </div>
      </Modal>
    </div>
  );
};


// --- Módulo 2: Panel de Administración (El Cerebro) ---
const StatCard = ({ title, value, icon: Icon, color }) => (
    <Card className={`relative overflow-hidden`}>
        <div className={`absolute -top-4 -right-4 text-white p-4 rounded-full opacity-20 ${color}`}>
            <Icon size={60} />
        </div>
        <h3 className="text-gray-500 font-semibold">{title}</h3>
        <p className="text-3xl font-bold text-gray-800">{value}</p>
    </Card>
);

const AdminDashboard = ({ setView, products, sales }) => {
    const totalSales = useMemo(() => sales.reduce((sum, sale) => sum + parseFloat(sale.total_amount), 0), [sales]);
    const lowStockItems = useMemo(() => products.filter(p => p.stock <= 5).length, [products]);

    // Datos para el gráfico de ventas por día
    const salesByDay = sales.reduce((acc, sale) => {
        const date = new Date(sale.date_time).toLocaleDateString('es-AR', { weekday: 'short' });
        acc[date] = (acc[date] || 0) + parseFloat(sale.total_amount);
        return acc;
    }, {});
    const chartData = Object.keys(salesByDay).map(day => ({ name: day, Ventas: salesByDay[day] }));

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Panel de Administración</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <StatCard title="Ventas Totales (Histórico)" value={`$${totalSales.toFixed(2)}`} icon={DollarSign} color="bg-green-500"/>
                <StatCard title="Transacciones" value={sales.length} icon={ShoppingCart} color="bg-blue-500"/>
                <StatCard title="Productos Activos" value={products.length} icon={Package} color="bg-purple-500"/>
                <StatCard title="Alertas de Stock Bajo" value={lowStockItems} icon={AlertCircle} color="bg-red-500"/>
            </div>
            <div className="grid grid-cols-1 lg:grid-cols-5 gap-6">
                <div className="lg:col-span-3">
                    <Card>
                        <h2 className="text-xl font-bold text-gray-800 mb-4">Ventas de la Semana</h2>
                        <ResponsiveContainer width="100%" height={300}>
                            <BarChart data={chartData}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="name" />
                                <YAxis />
                                <Tooltip formatter={(value) => `$${value.toFixed(2)}`} />
                                <Legend />
                                <Bar dataKey="Ventas" fill="#4f46e5" />
                            </BarChart>
                        </ResponsiveContainer>
                    </Card>
                </div>
                <div className="lg:col-span-2">
                    <Card>
                        <h2 className="text-xl font-bold text-gray-800 mb-4">Productos con Stock Bajo</h2>
                        <ul className="space-y-2">
                           {products.filter(p => p.stock <= 5).map(p => (
                               <li key={p.id} className="flex justify-between items-center p-2 bg-red-50 rounded-lg">
                                   <span>{p.name}</span>
                                   <span className="font-bold text-red-600">{p.stock} u.</span>
                               </li>
                           ))}
                           {lowStockItems === 0 && <p className="text-gray-500">No hay productos con stock bajo.</p>}
                        </ul>
                    </Card>
                </div>
            </div>
        </div>
    );
};


const Table = ({ headers, data, renderRow }) => (
    <div className="overflow-x-auto bg-white rounded-lg shadow">
        <table className="w-full text-sm text-left text-gray-500">
            <thead className="text-xs text-gray-700 uppercase bg-gray-50">
                <tr>
                    {headers.map(header => <th key={header} scope="col" className="px-6 py-3">{header}</th>)}
                </tr>
            </thead>
            <tbody>
                {data.map((item, index) => renderRow(item, index))}
            </tbody>
        </table>
    </div>
);


const GestionProductos = ({ products, setProducts, providers }) => {
    // La lógica de creación/edición aquí necesitaría conectarse a la API
    // Por ahora, solo mostramos la tabla con datos reales.
    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center">
                <h1 className="text-3xl font-bold text-gray-800">Gestión de Productos</h1>
                <Button icon={PlusCircle}>Nuevo Producto</Button>
            </div>

            <Table
                headers={['SKU', 'Nombre', 'Precio Venta', 'Stock', 'Categoría', 'Acciones']}
                data={products}
                renderRow={(product) => (
                    <tr key={product.id} className="bg-white border-b hover:bg-gray-50">
                        <td className="px-6 py-4 font-medium text-gray-900">{product.sku}</td>
                        <td className="px-6 py-4">{product.name}</td>
                        <td className="px-6 py-4">${parseFloat(product.sale_price).toFixed(2)}</td>
                        <td className="px-6 py-4">
                            <span className={`px-2 py-1 rounded-full text-xs font-semibold ${product.stock <= 5 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
                                {product.stock}
                            </span>
                        </td>
                        <td className="px-6 py-4">{product.category}</td>
                        <td className="px-6 py-4 flex gap-2">
                           <button className="text-indigo-600 hover:text-indigo-900"><Edit size={20}/></button>
                           <button className="text-red-600 hover:text-red-900"><Trash2 size={20}/></button>
                        </td>
                    </tr>
                )}
            />
        </div>
    );
};

const GestionVentas = ({ sales }) => {
    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Historial de Ventas</h1>
             <Table
                headers={['ID Venta', 'Fecha', 'Usuario', 'Items', 'Total', 'Detalle']}
                data={sales}
                renderRow={(sale) => (
                    <tr key={sale.id} className="bg-white border-b hover:bg-gray-50">
                        <td className="px-6 py-4 font-medium">#{sale.id}</td>
                        <td className="px-6 py-4">{new Date(sale.date_time).toLocaleString('es-AR')}</td>
                        <td className="px-6 py-4">{sale.user}</td>
                        <td className="px-6 py-4">{sale.details.length}</td>
                        <td className="px-6 py-4 font-bold">${parseFloat(sale.total_amount).toFixed(2)}</td>
                        <td className="px-6 py-4">
                           <button className="text-indigo-600 hover:underline">Ver detalle</button>
                        </td>
                    </tr>
                )}
            />
        </div>
    );
}

// placeholders para el resto de módulos
const GestionProveedores = ({providers}) => (
    <div className="space-y-6">
        <h1 className="text-3xl font-bold text-gray-800">Gestión de Proveedores</h1>
         <Table
            headers={['ID', 'Nombre', 'Contacto', 'Teléfono']}
            data={providers}
            renderRow={(provider) => (
                <tr key={provider.id} className="bg-white border-b hover:bg-gray-50">
                    <td className="px-6 py-4 font-medium">{provider.id}</td>
                    <td className="px-6 py-4">{provider.name}</td>
                    <td className="px-6 py-4">{provider.contact_person}</td>
                    <td className="px-6 py-4">{provider.phone_number}</td>
                </tr>
            )}
        />
    </div>
);
const GestionClientes = ({ clients }) => (
    <div className="space-y-6">
        <h1 className="text-3xl font-bold text-gray-800">Gestión de Clientes (CRM)</h1>
        <Table
            headers={['Nombre', 'Email', 'Teléfono']}
            data={clients}
            renderRow={(client) => (
                <tr key={client.id} className="bg-white border-b hover:bg-gray-50">
                    <td className="px-6 py-4 font-medium">{client.name}</td>
                    <td className="px-6 py-4">{client.email}</td>
                    <td className="px-6 py-4">{client.phone_number}</td>
                </tr>
            )}
        />
    </div>
);
const ReportesYEstadisticas = ({ products, sales }) => (
    <div className="space-y-6"><h1 className="text-3xl font-bold text-gray-800">Reportes y Estadísticas</h1><p>Esta sección se puede construir usando los datos reales de ventas y productos.</p></div>
);
const GestionUsuarios = () => (<div className="space-y-6"><h1 className="text-3xl font-bold text-gray-800">Gestión de Usuarios</h1><p>Funcionalidad para crear usuarios (administradores, vendedores)...</p></div>);

// --- Componente Principal y Navegación ---
export default function App() {
  const [view, setView] = useState('pos'); // 'pos' o 'admin'
  const [adminView, setAdminView] = useState('dashboard'); // 'dashboard', 'products', etc.
  const [user, setUser] = useState({ name: 'Vendedor 1', role: 'vendedor' });

  // --- Estados para los datos reales de la API ---
  const [products, setProducts] = useState([]);
  const [sales, setSales] = useState([]);
  const [providers, setProviders] = useState([]);
  const [clients, setClients] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // --- useEffect para cargar todos los datos de la API al iniciar ---
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const endpoints = ['products', 'sales', 'providers', 'clients', 'categories'];
        const requests = endpoints.map(endpoint => fetch(`${API_URL}/${endpoint}/`).then(res => res.json()));

        const [productsData, salesData, providersData, clientsData, categoriesData] = await Promise.all(requests);

        setProducts(productsData);
        setSales(salesData);
        setProviders(providersData);
        setClients(clientsData);
        setCategories(categoriesData);
        setError(null);
      } catch (err) {
        console.error("Error al obtener los datos:", err);
        setError("No se pudo conectar con el servidor. Asegúrate de que el backend de Django esté funcionando.");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []); // El array vacío [] significa que este efecto se ejecuta solo una vez, cuando el componente se monta.


  const handleSaleComplete = (cart, total) => {
    // --- Lógica de venta futura ---
    // Aquí es donde haríamos una petición POST a /api/sales/ para crear la venta en la BD.
    // Esto es más complejo porque requiere enviar datos y luego actualizar el estado.
    // Por ahora, simulamos que se agregó.
    console.log("Venta a registrar:", { cart, total });
    alert("Funcionalidad de guardar venta en desarrollo. ¡Los datos se leen correctamente de la API!");
  }

  // --- Lógica de Login y Navegación (sin cambios) ---
  const loginAsAdmin = () => {
      setUser({ name: 'Admin', role: 'admin' });
      setView('admin');
      setAdminView('dashboard');
  }

  const loginAsVendedor = () => {
      setUser({ name: 'Vendedor 1', role: 'vendedor' });
      setView('pos');
  }

  const renderAdminView = () => {
    switch (adminView) {
      case 'dashboard': return <AdminDashboard setView={setAdminView} products={products} sales={sales}/>;
      case 'products': return <GestionProductos products={products} setProducts={setProducts} providers={providers} />;
      case 'sales': return <GestionVentas sales={sales} />;
      case 'providers': return <GestionProveedores providers={providers} />;
      case 'clients': return <GestionClientes clients={clients} />;
      case 'reports': return <ReportesYEstadisticas products={products} sales={sales} />;
      case 'users': return <GestionUsuarios />;
      default: return <AdminDashboard setView={setAdminView} products={products} sales={sales}/>;
    }
  };

  const NavItem = ({ icon: Icon, children, onClick, active }) => (
    <li 
        onClick={onClick}
        className={`flex items-center gap-3 px-4 py-3 cursor-pointer rounded-lg transition-colors ${active ? 'bg-indigo-600 text-white shadow-lg' : 'text-gray-600 hover:bg-gray-200'}`}
    >
        <Icon size={22} />
        <span className="font-medium">{children}</span>
    </li>
  );

  if (!user.role) {
      return (
          <div className="bg-gray-100 min-h-screen flex items-center justify-center">
              <Card className="text-center w-full max-w-sm">
                  <h1 className="text-2xl font-bold mb-6">Iniciar Sesión</h1>
                  <div className="space-y-4">
                      <Button onClick={loginAsAdmin} className="w-full" variant="primary">Entrar como Administrador</Button>
                      <Button onClick={loginAsVendedor} className="w-full" variant="secondary">Entrar como Vendedor</Button>
                  </div>
              </Card>
          </div>
      )
  }

  if (loading) {
    return <div className="min-h-screen flex items-center justify-center">Cargando datos desde el servidor...</div>
  }

  if (error) {
    return <div className="min-h-screen flex items-center justify-center text-red-500 font-bold p-8 text-center">{error}</div>
  }

  return (
    <div className="flex h-screen bg-gray-100 font-sans">
      {view === 'admin' && (
        <aside className="w-64 bg-white shadow-xl flex flex-col p-4">
          <h1 className="text-2xl font-bold text-indigo-600 px-4 mb-6">MiNegocio<span className="font-light">PRO</span></h1>
          <nav className="flex-grow">
            <ul className="space-y-2">
              <NavItem icon={BarChart2} active={adminView === 'dashboard'} onClick={() => setAdminView('dashboard')}>Dashboard</NavItem>
              <NavItem icon={Package} active={adminView === 'products'} onClick={() => setAdminView('products')}>Productos</NavItem>
              <NavItem icon={DollarSign} active={adminView === 'sales'} onClick={() => setAdminView('sales')}>Ventas</NavItem>
              <NavItem icon={Truck} active={adminView === 'providers'} onClick={() => setAdminView('providers')}>Proveedores</NavItem>
              <NavItem icon={Users} active={adminView === 'clients'} onClick={() => setAdminView('clients')}>Clientes</NavItem>
              <NavItem icon={BarChart} active={adminView === 'reports'} onClick={() => setAdminView('reports')}>Reportes</NavItem>
              <NavItem icon={UserPlus} active={adminView === 'users'} onClick={() => setAdminView('users')}>Usuarios</NavItem>
            </ul>
          </nav>
          <div className="mt-auto">
             <NavItem icon={ShoppingCart} onClick={() => setView('pos')}>Ir al Punto de Venta</NavItem>
          </div>
        </aside>
      )}

      <main className="flex-1 flex flex-col">
        <header className="bg-white shadow-sm p-4 flex justify-between items-center z-10">
          <div>
            {view === 'pos' && user.role === 'admin' && (
                 <Button onClick={() => setView('admin')} variant="secondary">Volver al Panel</Button>
            )}
          </div>
          <div className="flex items-center gap-3">
              <span className="font-semibold text-gray-700">{user.name} ({user.role})</span>
              <button onClick={() => setUser({role: null})} className="text-gray-500 hover:text-red-600">
                  <LogOut size={20} />
              </button>
          </div>
        </header>
        <div className="flex-1 p-6 overflow-y-auto">
          {view === 'pos' ? <PuntoDeVenta products={products} onSaleComplete={handleSaleComplete} /> : renderAdminView()}
        </div>
      </main>

      <style jsx global>{`
        @keyframes fade-in-down {
            0% {
                opacity: 0;
                transform: translateY(-20px);
            }
            100% {
                opacity: 1;
                transform: translateY(0);
            }
        }
        .animate-fade-in-down {
            animation: fade-in-down 0.3s ease-out forwards;
        }
      `}</style>
    </div>
  );
}
