import React, { useState, useEffect, useCallback } from 'react';
import { Button } from '../components/ui/ComponentesUI'; // Asegúrate que la ruta sea correcta

// Este hook reemplaza a 'useFiltrosYBusqueda' para interactuar con el backend
export const useServerSidePagination = ({
    endpoint,
    initialFilters = {},
    itemsPorPagina = 10,
    ComponenteFiltros,
    tokensAuth, // Necesitamos los tokens para la autorización
}) => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
    // Estado para la paginación y conteo total
    const [paginaActual, setPaginaActual] = useState(1);
    const [totalItems, setTotalItems] = useState(0);
    const [totalPaginas, setTotalPaginas] = useState(1);

    // Estado para los filtros
    const [filtros, setFiltros] = useState(initialFilters);

    // Función para obtener los datos desde el backend
    const fetchData = useCallback(async (page, currentFilters) => {
        setLoading(true);
        setError(null);

        // Construimos los parámetros de la URL
        const params = new URLSearchParams({
            page: page,
            page_size: itemsPorPagina,
        });

        // Añadimos los filtros a los parámetros
        Object.entries(currentFilters).forEach(([key, value]) => {
            if (value !== '' && value !== null) {
                params.append(key, value);
            }
        });

        const url = `http://127.0.0.1:8000/api/${endpoint}/?${params.toString()}`;

        try {
            const response = await fetch(url, {
                headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) },
            });
            if (!response.ok) {
                throw new Error(`Error ${response.status}: No se pudieron obtener los datos.`);
            }
            const result = await response.json();
            
            setData(result.results); // DRF devuelve los items en la clave 'results'
            setTotalItems(result.count); // DRF devuelve el conteo total en 'count'
            setTotalPaginas(Math.ceil(result.count / itemsPorPagina));

        } catch (err) {
            setError(err.message);
            console.error(err);
        } finally {
            setLoading(false);
        }
    }, [endpoint, itemsPorPagina, tokensAuth]);

    // Efecto para volver a cargar los datos si la página o los filtros cambian
    useEffect(() => {
        fetchData(paginaActual, filtros);
    }, [paginaActual, filtros, fetchData]);

    // --- MANEJADORES ---
    const irAPagina = (page) => {
        const nuevaPagina = Math.max(1, Math.min(page, totalPaginas || 1));
        setPaginaActual(nuevaPagina);
    };

    const handleSetFiltros = (nuevosFiltros) => {
        setPaginaActual(1); // Siempre reseteamos a la página 1 al aplicar un filtro
        setFiltros(nuevosFiltros);
    };

    const resetFiltros = () => {
        setPaginaActual(1);
        setFiltros(initialFilters);
    };

    // --- RENDERIZADO DE UI ---
    // Estos componentes son devueltos por el hook para ser usados en la página
    const FiltrosUI = ComponenteFiltros ? (
        <ComponenteFiltros setFiltros={handleSetFiltros} filtros={filtros} resetFiltros={resetFiltros} />
    ) : null;

    const PaginacionUI = (
        <div className="flex justify-between items-center mt-4">
            <span className="text-sm text-gray-700">
                Mostrando {data.length > 0 ? ((paginaActual - 1) * itemsPorPagina) + 1 : 0}
                - {Math.min(paginaActual * itemsPorPagina, totalItems)} de {totalItems} resultados
            </span>
            <div className="flex gap-2">
                <Button onClick={() => irAPagina(paginaActual - 1)} disabled={paginaActual === 1 || loading}>
                    Anterior
                </Button>
                <span className="self-center px-4 py-2 text-sm">
                    Página {paginaActual} de {totalPaginas > 0 ? totalPaginas : 1}
                </span>
                <Button onClick={() => irAPagina(paginaActual + 1)} disabled={paginaActual >= totalPaginas || loading}>
                    Siguiente
                </Button>
            </div>
        </div>
    );
    
    // El hook devuelve los datos y el estado, junto con los componentes de UI listos para usar
    return { datosPaginados: data, loading, error, FiltrosUI, PaginacionUI };
};
