import React, { useState, useEffect, useRef } from 'react';
import { Input, Button } from '../../components/ui/ComponentesUI';

const FiltrosProveedores = ({ setFiltros, filtros, resetFiltros }) => {
    const [searchTerm, setSearchTerm] = useState(filtros.search || '');
    const isInitialMount = useRef(true);

    useEffect(() => {
        if (isInitialMount.current) {
            isInitialMount.current = false;
            return;
        }

        const handler = setTimeout(() => {
            setFiltros(prevFiltros => ({ ...prevFiltros, search: searchTerm }));
        }, 500);

        return () => {
            clearTimeout(handler);
        };
    // El linter de React nos pide incluir 'setFiltros' porque se usa dentro del efecto.
    // Es una buena pr치ctica para evitar bugs si la funci칩n cambiara.
    }, [searchTerm, setFiltros]);

    const handleReset = () => {
        setSearchTerm('');
        resetFiltros();
    };

    // Este efecto sincroniza el input si el filtro se resetea desde fuera
    useEffect(() => {
        setSearchTerm(filtros.search || '');
    }, [filtros.search]);

    return (
        <div className="flex flex-wrap items-center gap-4 p-4 bg-gray-50 rounded-lg border">
            <div className="flex-grow min-w-[250px]">
                <Input
                    type="text"
                    placeholder="Buscar por nombre, email, CUIT..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full"
                />
            </div>
            
            <div className="flex-shrink-0">
                <Button onClick={handleReset} variant="outline">Limpiar</Button>
            </div>
        </div>
    );
};

export default FiltrosProveedores;
