import React, { useState, useContext, useMemo } from 'react';
import { Eye, XCircle, Download } from 'lucide-react';
import { ContextoAuth } from '../../context/AuthContext';
import { Button, Modal, Table } from '../../components/ui/ComponentesUI';
import { useFiltrosYBusqueda } from '../../hooks/useFiltrosYBusqueda';
import { toast } from 'sonner'; 

const API_URL = 'http://127.0.0.1:8000/api';

// --- NUEVO: Helper para formatear moneda ---
const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-AR', {
        style: 'currency',
        currency: 'ARS'
    }).format(amount);
};

// --- COMPONENTES Y LÓGICA DE FILTRADO (MODIFICADO) ---
const FiltrosVentas = ({ setFiltros, filtros, resetFiltros, onExport }) => {
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFiltros(prev => ({ ...prev, [name]: value }));
    };
    return (
        // --- MODIFICADO: Panel de filtros con título y botón de exportar ---
        <div className="p-4 bg-white rounded-lg border shadow-sm">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Filtrar Ventas</h3>
            <div className="flex flex-wrap items-end gap-4">
                <div>
                    <label htmlFor="fechaDesde" className="block text-sm font-medium text-gray-700">Desde</label>
                    <input id="fechaDesde" type="date" name="fechaDesde" value={filtros.fechaDesde} onChange={handleInputChange} className="mt-1 p-2 border rounded-lg w-full"/>
                </div>
                <div>
                    <label htmlFor="fechaHasta" className="block text-sm font-medium text-gray-700">Hasta</label>
                    <input id="fechaHasta" type="date" name="fechaHasta" value={filtros.fechaHasta} onChange={handleInputChange} className="mt-1 p-2 border rounded-lg w-full"/>
                </div>
                <div>
                    <label htmlFor="estado" className="block text-sm font-medium text-gray-700">Estado</label>
                    <select id="estado" name="estado" value={filtros.estado} onChange={handleInputChange} className="mt-1 p-2 border rounded-lg w-full">
                        <option value="Todos">Todos</option>
                        <option value="Completada">Completada</option>
                        <option value="Cancelada">Cancelada</option>
                    </select>
                </div>
                <Button onClick={resetFiltros} variant="secondary">Limpiar Filtros</Button>
                <Button onClick={onExport} variant="outline" icon={Download}>Exportar CSV</Button>
            </div>
        </div>
    );
};

const logicaFiltroVentas = (ventas, filtros) => {
    const { fechaDesde, fechaHasta, estado } = filtros;
    return ventas.filter(venta => {
        // Corrección para comparar fechas correctamente en diferentes zonas horarias
        const fechaVenta = new Date(venta.date_time);
        const desde = fechaDesde ? new Date(fechaDesde + 'T00:00:00') : null;
        const hasta = fechaHasta ? new Date(fechaHasta + 'T23:59:59') : null;

        if (desde && fechaVenta < desde) return false;
        if (hasta && fechaVenta > hasta) return false;
        if (estado !== 'Todos' && venta.status !== estado) return false;
        return true;
    });
};

// --- NUEVO: Componente para las tarjetas de KPIs ---
const TarjetasKPI = ({ ventasFiltradas }) => {
    const kpis = useMemo(() => {
        const ventasCompletadas = ventasFiltradas.filter(v => v.status === 'Completada');
        const montoTotal = ventasCompletadas.reduce((sum, v) => sum + parseFloat(v.final_amount), 0);
        const numeroVentas = ventasFiltradas.length;
        const ticketPromedio = ventasCompletadas.length > 0 ? montoTotal / ventasCompletadas.length : 0;
        
        return { montoTotal, numeroVentas, ticketPromedio };
    }, [ventasFiltradas]);

    return (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="bg-white p-4 rounded-lg border shadow-sm">
                <h4 className="text-sm font-medium text-gray-500">Monto Total (Completadas)</h4>
                <p className="text-2xl font-bold text-gray-800">{formatCurrency(kpis.montoTotal)}</p>
            </div>
            <div className="bg-white p-4 rounded-lg border shadow-sm">
                <h4 className="text-sm font-medium text-gray-500">Ventas Realizadas (Filtradas)</h4>
                <p className="text-2xl font-bold text-gray-800">{kpis.numeroVentas}</p>
            </div>
            <div className="bg-white p-4 rounded-lg border shadow-sm">
                <h4 className="text-sm font-medium text-gray-500">Ticket Promedio (Completadas)</h4>
                <p className="text-2xl font-bold text-gray-800">{formatCurrency(kpis.ticketPromedio)}</p>
            </div>
        </div>
    );
};


// --- COMPONENTE PRINCIPAL (MODIFICADO) ---
const GestionVentas = ({ ventas, obtenerDatos }) => {
    const [ventaSeleccionada, setVentaSeleccionada] = useState(null);
    const { tokensAuth } = useContext(ContextoAuth);

    const { datosPaginados, datosFiltrados, FiltrosUI, PaginacionUI } = useFiltrosYBusqueda({
        items: ventas,
        itemsPorPagina: 15,
        logicaDeFiltro: logicaFiltroVentas,
        ComponenteFiltros: FiltrosVentas,
        filtrosIniciales: { fechaDesde: '', fechaHasta: '', estado: 'Todos' },
        // --- NUEVO: Pasamos la función de exportar a los filtros ---
        propsAdicionalesFiltros: { onExport: () => exportarACSV(datosFiltrados) }
    });

    const cancelarVenta = (idVenta) => { /* ... (sin cambios en esta función) ... */ };
    
    // --- NUEVO: Función para exportar a CSV ---
    const exportarACSV = (datosParaExportar) => {
        if (datosParaExportar.length === 0) {
            toast.error("No hay datos para exportar.");
            return;
        }
        const headers = ["ID Venta", "Fecha", "Usuario", "Estado", "Metodo de Pago", "Total"];
        const rows = datosParaExportar.map(venta => [
            venta.id,
            new Date(venta.date_time).toLocaleString('es-AR'),
            venta.user,
            venta.status,
            venta.payment_method,
            parseFloat(venta.final_amount).toFixed(2)
        ].join(','));
        
        const csvContent = "data:text/csv;charset=utf-8," + [headers.join(','), ...rows].join('\n');
        const encodedUri = encodeURI(csvContent);
        const link = document.createElement("a");
        link.setAttribute("href", encodedUri);
        link.setAttribute("download", `reporte_ventas_${new Date().toISOString().split('T')[0]}.csv`);
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        toast.success("La descarga del reporte ha comenzado.");
    };

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Historial de Ventas</h1>

            {/* --- NUEVO: Tarjetas de KPI --- */}
            <TarjetasKPI ventasFiltradas={datosFiltrados} />
            
            {FiltrosUI}

            <div className="bg-white p-4 rounded-lg border shadow-sm">
                {datosPaginados.length > 0 ? (
                    <>
                        <Table
                            headers={['ID', 'Fecha', 'Usuario', 'Items', 'Total', 'Estado', 'Acciones']}
                            // --- MODIFICADO: Añadimos estilo a la cabecera de la tabla ---
                            renderHeader={() => (
                                <thead className="bg-gray-100">
                                    <tr>
                                        {['ID', 'Fecha y Hora', 'Usuario', 'Items', 'Total', 'Estado', 'Acciones'].map(h => (
                                            <th key={h} className="px-6 py-3 text-left text-xs font-bold text-gray-600 uppercase tracking-wider">{h}</th>
                                        ))}
                                    </tr>
                                </thead>
                            )}
                            data={datosPaginados}
                            renderRow={(venta) => (
                                // --- MODIFICADO: Añadimos "zebra-striping" para mejor legibilidad ---
                                <tr key={venta.id} className="border-b even:bg-gray-50 hover:bg-blue-50">
                                    <td className="px-6 py-4 font-medium text-gray-700">#{venta.id}</td>
                                    <td className="px-6 py-4 text-gray-600">{new Date(venta.date_time).toLocaleString('es-AR')}</td>
                                    <td className="px-6 py-4 text-gray-600">{venta.user}</td>
                                    <td className="px-6 py-4 text-gray-600">{venta.details.length}</td>
                                    <td className="px-6 py-4 font-bold text-gray-800">{formatCurrency(parseFloat(venta.final_amount))}</td>
                                    <td className="px-6 py-4">
                                        <span className={`px-2.5 py-1 rounded-full text-xs font-semibold ${
                                            venta.status === 'Completada' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                                        }`}>{venta.status}</span>
                                    </td>
                                    <td className="px-6 py-4 flex gap-2">
                                        <Button onClick={() => setVentaSeleccionada(venta)} variant="icon" title="Ver Detalle"><Eye className="h-5 w-5"/></Button>
                                        {venta.status === 'Completada' && (
                                            <Button onClick={() => cancelarVenta(venta.id)} variant="icon-danger" title="Cancelar Venta"><XCircle className="h-5 w-5"/></Button>
                                        )}
                                    </td>
                                </tr>
                            )}
                        />
                        {PaginacionUI}
                    </>
                ) : (
                    <div className="text-center py-16 px-6">
                        <h3 className="text-lg font-semibold text-gray-700">No se encontraron ventas</h3>
                        <p className="text-gray-500 mt-1">Intenta cambiar los filtros para encontrar resultados.</p>
                    </div>
                )}
            </div>

            {/* --- MODIFICADO: Modal de detalles rediseñado --- */}
            {ventaSeleccionada && (
                <Modal isOpen={!!ventaSeleccionada} onClose={() => setVentaSeleccionada(null)} title={`Detalle de Venta #${ventaSeleccionada.id}`}>
                    <div className="space-y-4">
                        <div className="p-3 bg-gray-50 rounded-lg border">
                            <h4 className="font-semibold text-gray-800 mb-2">Resumen de la Venta</h4>
                            <p className="text-sm text-gray-600"><strong>Fecha:</strong> {new Date(ventaSeleccionada.date_time).toLocaleString('es-AR')}</p>
                            <p className="text-sm text-gray-600"><strong>Usuario:</strong> {ventaSeleccionada.user}</p>
                            <p className="text-sm text-gray-600"><strong>Estado:</strong> {ventaSeleccionada.status}</p>
                            <p className="text-sm text-gray-600"><strong>Método de Pago:</strong> {ventaSeleccionada.payment_method}</p>
                        </div>
                        <div>
                            <h4 className="font-semibold text-gray-800 mb-2">Productos</h4>
                            <ul className="space-y-2">
                                {ventaSeleccionada.details.map(detalle => (
                                    <li key={detalle.product.id} className="flex justify-between items-center p-2 bg-gray-50 rounded-md text-sm">
                                        <div>
                                            <span className="font-semibold text-gray-700">{detalle.product.name}</span>
                                            <span className="text-gray-500 ml-2">({detalle.quantity} x {formatCurrency(parseFloat(detalle.unit_price))})</span>
                                        </div>
                                        <span className="font-medium text-gray-800">{formatCurrency(detalle.quantity * parseFloat(detalle.unit_price))}</span>
                                    </li>
                                ))}
                            </ul>
                        </div>
                        <div className="mt-4 pt-4 border-t text-right space-y-1">
                             <p className="text-md">Subtotal: <span className="font-medium">{formatCurrency(parseFloat(ventaSeleccionada.total_amount))}</span></p>
                             <p className="text-md">Ajuste/Descuento: <span className="font-medium">{formatCurrency(parseFloat(ventaSeleccionada.final_amount) - parseFloat(ventaSeleccionada.total_amount))}</span></p>
                             <p className="font-bold text-xl text-blue-600">Total Final: {formatCurrency(parseFloat(ventaSeleccionada.final_amount))}</p>
                        </div>
                    </div>
                </Modal>
            )}
        </div>
    );
};

export default GestionVentas;
