import React, { useState, useContext } from 'react';
import { PlusCircle, Edit, Trash2 } from 'lucide-react';
import { ContextoAuth } from '../../context/AuthContext';
import { Button, Modal, Table } from '../../components/ui/ComponentesUI';

const API_URL = 'http://127.0.0.1:8000/api';

const FormularioProducto = ({ producto, onGuardar, onCancelar, categorias, proveedores }) => {
    const [formData, setFormData] = useState({
        ...producto,
        // Aseguramos que el valor inicial sea el ID, no el objeto completo
        category: producto.category || '', 
        provider: producto.provider || null
    });
    
    const handleChange = (e) => { 
        const { name, value } = e.target; 
        setFormData(prev => ({ ...prev, [name]: value })); 
    };

    return (
        <form onSubmit={(e) => { e.preventDefault(); onGuardar(formData); }} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <input name="name" value={formData.name} onChange={handleChange} placeholder="Nombre del producto" className="p-2 border rounded-lg w-full" required/>
                <input name="sku" value={formData.sku} onChange={handleChange} placeholder="SKU / Código de Barras" className="p-2 border rounded-lg w-full" required/>
            </div>
            <textarea name="description" value={formData.description || ''} onChange={handleChange} placeholder="Descripción (opcional)" className="p-2 border rounded-lg w-full min-h-[80px]"/>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <input name="cost_price" type="number" step="0.01" value={formData.cost_price} onChange={handleChange} placeholder="Precio de costo" className="p-2 border rounded-lg w-full" required/>
                <input name="sale_price" type="number" step="0.01" value={formData.sale_price} onChange={handleChange} placeholder="Precio de venta" className="p-2 border rounded-lg w-full" required/>
                <input name="stock" type="number" value={formData.stock} onChange={handleChange} placeholder="Stock actual" className="p-2 border rounded-lg w-full" required/>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <select name="category" value={formData.category} onChange={handleChange} className="p-2 border rounded-lg w-full" required>
                    <option value="">Seleccionar Categoría</option>
                    {categorias.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                </select>
                <select name="provider" value={formData.provider || ''} onChange={handleChange} className="p-2 border rounded-lg w-full">
                    <option value="">Seleccionar Proveedor (opcional)</option>
                    {proveedores.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
                </select>
            </div>
            <div className="flex justify-end gap-3 pt-4">
                <Button type="button" onClick={onCancelar} variant="secondary">Cancelar</Button>
                <Button type="submit" variant="primary">Guardar</Button>
            </div>
        </form>
    );
};

const GestionProductos = ({ productos, proveedores, categorias, obtenerDatos }) => {
    const [modalAbierto, setModalAbierto] = useState(false);
    const [productoEditando, setProductoEditando] = useState(null);
    const { tokensAuth } = useContext(ContextoAuth);

    const abrirModalNuevo = () => { 
        setProductoEditando({ name: '', sku: '', description: '', cost_price: '', sale_price: '', stock: '', category: '', provider: null }); 
        setModalAbierto(true); 
    };
    
    const abrirModalEditar = (producto) => { 
        setProductoEditando({
            ...producto,
            provider: producto.provider || null // Asegurarse de que el valor sea null si no existe
        }); 
        setModalAbierto(true); 
    };

    const guardarProducto = async (datosProducto) => {
        const esEditando = !!datosProducto.id;
        const url = esEditando ? `${API_URL}/products/${datosProducto.id}/` : `${API_URL}/products/`;
        const method = esEditando ? 'PUT' : 'POST';
        
        // El backend espera 'provider', no 'provider_name'
        const payload = { ...datosProducto };
        delete payload.provider_name;
        delete payload.category_name;
        if (payload.provider === '') payload.provider = null;
        
        try {
            const response = await fetch(url, { 
                method: method, 
                headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) }, 
                body: JSON.stringify(payload) 
            });
            if (!response.ok) { 
                const errorData = await response.json(); 
                throw new Error(JSON.stringify(errorData)); 
            }
            alert(`Producto ${esEditando ? 'actualizado' : 'creado'}.`); 
            setModalAbierto(false); 
            obtenerDatos();
        } catch (err) { 
            alert(`Error: ${err.message}`); 
        }
    };

    const borrarProducto = async (idProducto) => {
        if (window.confirm("¿Estás seguro de que quieres eliminar este producto?")) {
            try {
                await fetch(`${API_URL}/products/${idProducto}/`, { method: 'DELETE', headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) }});
                alert('Producto eliminado.'); 
                obtenerDatos();
            } catch (err) { 
                alert(`Error: ${err.message}`); 
            }
        }
    };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center">
                <h1 className="text-3xl font-bold text-gray-800">Gestión de Productos</h1>
                <Button onClick={abrirModalNuevo} icon={PlusCircle}>Nuevo Producto</Button>
            </div>
            <Table 
                headers={['SKU', 'Nombre', 'Precio Venta', 'Stock', 'Categoría', 'Acciones']} 
                data={productos} 
                renderRow={(p) => (
                    <tr key={p.id} className="bg-white border-b hover:bg-gray-50">
                        <td className="px-6 py-4">{p.sku}</td>
                        <td className="px-6 py-4">{p.name}</td>
                        <td className="px-6 py-4">${parseFloat(p.sale_price).toFixed(2)}</td>
                        <td className="px-6 py-4">
                            <span className={`px-2 py-1 rounded-full text-xs font-semibold ${p.stock <= 5 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
                                {p.stock}
                            </span>
                        </td>
                        <td className="px-6 py-4">{p.category_name}</td>
                        <td className="px-6 py-4 flex gap-2">
                            <Button onClick={() => abrirModalEditar(p)} variant="secondary" icon={Edit} />
                            <Button onClick={() => borrarProducto(p.id)} variant="danger" icon={Trash2} />
                        </td>
                    </tr>
                )}
            />
            <Modal isOpen={modalAbierto} onClose={() => setModalAbierto(false)} title={productoEditando?.id ? 'Editar Producto' : 'Nuevo Producto'}>
                {productoEditando && <FormularioProducto producto={productoEditando} onGuardar={guardarProducto} onCancelar={() => setModalAbierto(false)} categorias={categorias} proveedores={proveedores}/>}
            </Modal>
        </div>
    );
};

export default GestionProductos;
