import React, { useState, useMemo } from 'react';
import { Button } from '../components/ui/ComponentesUI';

/**
 * Un Hook personalizado y flexible para manejar filtros, búsqueda y paginación.
 * @param {Array} items - El array completo de items.
 * @param {Object} config - Un objeto de configuración.
 * @param {number} [config.itemsPorPagina=10] - Items por página.
 * @param {function} config.logicaDeFiltro - Una función que recibe los items y el estado de los filtros, y devuelve los items filtrados.
 * @param {React.Component} config.ComponenteFiltros - Un componente de React que renderiza los controles de filtro y recibe `setFiltros` y `filtros`.
 * @param {Object} config.filtrosIniciales - El estado inicial para los filtros.
 * @returns Un objeto con los datos paginados y los componentes de UI.
 */
export const useFiltrosYBusqueda = ({
    items,
    itemsPorPagina = 10,
    logicaDeFiltro,
    ComponenteFiltros,
    filtrosIniciales,
}) => {
    const [paginaActual, setPaginaActual] = useState(1);
    const [filtros, setFiltros] = useState(filtrosIniciales);

    const itemsFiltrados = useMemo(() => {
        // CORRECCIÓN: Se añade una comprobación para asegurar que logicaDeFiltro es una función.
        // Esto evita que la aplicación se rompa si la función no se pasa correctamente.
        if (typeof logicaDeFiltro !== 'function') {
            console.error("Hook useFiltrosYBusqueda: La 'logicaDeFiltro' proporcionada no es una función. Se devolverán todos los items para evitar un error.");
            return items;
        }
        return logicaDeFiltro(items, filtros);
    }, [items, filtros, logicaDeFiltro]);

    const totalPaginas = Math.ceil(itemsFiltrados.length / itemsPorPagina);

    const datosPaginados = useMemo(() => {
        const inicio = (paginaActual - 1) * itemsPorPagina;
        const fin = inicio + itemsPorPagina;
        return itemsFiltrados.slice(inicio, fin);
    }, [paginaActual, itemsFiltrados, itemsPorPagina]);

    const irAPagina = (pagina) => {
        setPaginaActual(Math.max(1, Math.min(pagina, totalPaginas || 1)));
    };
    
    const handleSetFiltros = (nuevosFiltros) => {
        setFiltros(nuevosFiltros);
        irAPagina(1);
    };

    const FiltrosUI = (
        <ComponenteFiltros setFiltros={handleSetFiltros} filtros={filtros} />
    );

    const PaginacionUI = (
        <div className="flex justify-between items-center mt-4">
            <span className="text-sm text-gray-700">
                Mostrando {itemsFiltrados.length > 0 ? ((paginaActual - 1) * itemsPorPagina) + 1 : 0}
                - {Math.min(paginaActual * itemsPorPagina, itemsFiltrados.length)} de {itemsFiltrados.length} resultados
            </span>
            <div className="flex gap-2">
                <Button onClick={() => irAPagina(paginaActual - 1)} disabled={paginaActual === 1}>
                    Anterior
                </Button>
                <span className="self-center px-4 py-2 text-sm">
                    Página {paginaActual} de {totalPaginas > 0 ? totalPaginas : 1}
                </span>
                <Button onClick={() => irAPagina(paginaActual + 1)} disabled={paginaActual >= totalPaginas}>
                    Siguiente
                </Button>
            </div>
        </div>
    );

    return { datosPaginados, FiltrosUI, PaginacionUI };
};
