import React, { useState, useEffect, useCallback } from 'react';
import { Button, Input } from '../../components/ui/ComponentesUI';
import { toast } from 'sonner';

const FormularioProveedor = ({ proveedorInicial, onSave, onCancel }) => {
    // Usamos useCallback para que la función que devuelve el estado inicial no se
    // cree en cada render, solucionando el warning del linter de forma correcta.
    const getInitialState = useCallback(() => ({
        name: '',
        email: '',
        phone: '',
        address: '',
        cuit: '',
        is_active: true,
    }), []);

    const [formData, setFormData] = useState(getInitialState());

    useEffect(() => {
        if (proveedorInicial) {
            setFormData({
                name: proveedorInicial.name || '',
                email: proveedorInicial.email || '',
                phone: proveedorInicial.phone || '',
                address: proveedorInicial.address || '',
                cuit: proveedorInicial.cuit || '',
                // Usamos '??' para asegurarnos de que si 'is_active' es undefined, se ponga 'true'
                is_active: proveedorInicial.is_active ?? true,
            });
        } else {
            setFormData(getInitialState());
        }
    }, [proveedorInicial, getInitialState]); // <-- CORRECCIÓN: Se añade la dependencia

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!formData.name.trim()) {
            toast.error('El nombre del proveedor es obligatorio.');
            return;
        }
        if (!formData.cuit.trim()) {
            toast.error('El CUIT del proveedor es obligatorio.');
            return;
        }
        onSave(formData);
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <div>
                <label htmlFor="name" className="block text-sm font-medium text-gray-700">Nombre del Proveedor</label>
                <Input type="text" name="name" id="name" value={formData.name} onChange={handleChange} required className="mt-1" />
            </div>
            <div>
                <label htmlFor="cuit" className="block text-sm font-medium text-gray-700">CUIT</label>
                <Input type="text" name="cuit" id="cuit" value={formData.cuit} onChange={handleChange} required className="mt-1" />
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                    <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email</label>
                    <Input type="email" name="email" id="email" value={formData.email} onChange={handleChange} className="mt-1" />
                </div>
                <div>
                    <label htmlFor="phone" className="block text-sm font-medium text-gray-700">Teléfono</label>
                    <Input type="text" name="phone" id="phone" value={formData.phone} onChange={handleChange} className="mt-1" />
                </div>
            </div>
            <div>
                <label htmlFor="address" className="block text-sm font-medium text-gray-700">Dirección</label>
                <Input type="text" name="address" id="address" value={formData.address} onChange={handleChange} className="mt-1" />
            </div>
            <div className="flex items-center">
                <input
                    id="is_active"
                    name="is_active"
                    type="checkbox"
                    checked={formData.is_active}
                    onChange={handleChange}
                    className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <label htmlFor="is_active" className="ml-2 block text-sm text-gray-900">
                    Proveedor Activo
                </label>
            </div>
            <div className="pt-5">
                <div className="flex justify-end gap-3">
                    <Button type="button" variant="secondary" onClick={onCancel}>
                        Cancelar
                    </Button>
                    <Button type="submit" variant="primary">
                        Guardar Proveedor
                    </Button>
                </div>
            </div>
        </form>
    );
};

export default FormularioProveedor;
