import React, { useState, useContext } from 'react';
// Importamos los íconos que vamos a usar
import { PlusCircle, Edit, Trash2, PackagePlus } from 'lucide-react';
import { ContextoAuth } from '../../context/AuthContext';
import { Button, Modal, Table } from '../../components/ui/ComponentesUI';
import { useFiltrosYBusqueda } from '../../hooks/useFiltrosYBusqueda';
import { toast } from 'sonner';

// Los componentes de formulario y la lógica de filtrado no cambian
const API_URL = 'http://127.0.0.1:8000/api';

const FormularioProducto = ({ producto, onGuardar, onCancelar, categorias, proveedores }) => {
    const [formData, setFormData] = useState({ 
        ...producto, 
        category: producto.category || '', 
        provider: producto.provider || null,
        estado: producto.estado || 'activo'
    });
    
    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (name === 'estado') {
            setFormData(prev => ({ ...prev, estado: checked ? 'activo' : 'inactivo' }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
    };

    return (
        <form onSubmit={(e) => { e.preventDefault(); onGuardar(formData); }} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                    <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-1">Nombre del Producto</label>
                    <input id="name" name="name" value={formData.name} onChange={handleChange} placeholder="Ej: Coca-Cola 1.5L" className="p-2 border rounded-lg w-full" required/>
                </div>
                <div>
                    <label htmlFor="sku" className="block text-sm font-medium text-gray-700 mb-1">SKU / Código de Barras</label>
                    <input id="sku" name="sku" value={formData.sku} onChange={handleChange} placeholder="Ej: 7790895000991" className="p-2 border rounded-lg w-full" required/>
                </div>
            </div>
            <div>
                <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">Descripción (opcional)</label>
                <textarea id="description" name="description" value={formData.description || ''} onChange={handleChange} placeholder="Cualquier detalle adicional..." className="p-2 border rounded-lg w-full min-h-[80px]"/>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                    <label htmlFor="cost_price" className="block text-sm font-medium text-gray-700 mb-1">Precio de Costo</label>
                    <input id="cost_price" name="cost_price" type="number" step="0.01" value={formData.cost_price} onChange={handleChange} placeholder="0.00" className="p-2 border rounded-lg w-full" required/>
                </div>
                <div>
                    <label htmlFor="sale_price" className="block text-sm font-medium text-gray-700 mb-1">Precio de Venta</label>
                    <input id="sale_price" name="sale_price" type="number" step="0.01" value={formData.sale_price} onChange={handleChange} placeholder="0.00" className="p-2 border rounded-lg w-full" required/>
                </div>
                <div>
                    <label htmlFor="stock" className="block text-sm font-medium text-gray-700 mb-1">Stock Actual</label>
                    <input id="stock" name="stock" type="number" value={formData.stock} onChange={handleChange} placeholder="0" className="p-2 border rounded-lg w-full" required/>
                </div>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                    <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">Categoría</label>
                    <select id="category" name="category" value={formData.category || ''} onChange={handleChange} className="p-2 border rounded-lg w-full" required>
                        <option value="">Seleccionar Categoría</option>
                        {categorias.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                    </select>
                </div>
                <div>
                    <label htmlFor="provider" className="block text-sm font-medium text-gray-700 mb-1">Proveedor (opcional)</label>
                    <select id="provider" name="provider" value={formData.provider || ''} onChange={handleChange} className="p-2 border rounded-lg w-full">
                        <option value="">Seleccionar Proveedor</option>
                        {proveedores.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
                    </select>
                </div>
            </div>
            <div className="flex items-center gap-2 mt-4">
                <input
                    id="estado"
                    name="estado"
                    type="checkbox"
                    checked={formData.estado === 'activo'}
                    onChange={handleChange}
                    className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <label htmlFor="estado" className="text-sm font-medium text-gray-700">Activo</label>
            </div>
            <div className="flex justify-end gap-3 pt-4">
                <Button type="button" onClick={onCancelar} variant="secondary">Cancelar</Button>
                <Button type="submit" variant="primary">Guardar</Button>
            </div>
        </form>
    );
};
const FormularioStock = ({ onGuardar, onCancelar }) => { /* ...sin cambios... */
    const [stockToAdd, setStockToAdd] = useState(1);
    const handleSubmit = (e) => { e.preventDefault(); onGuardar({ stock: stockToAdd }); };
    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <div>
                <label htmlFor="stock" className="block text-sm font-medium text-gray-700 mb-1">Cantidad a Añadir</label>
                <input id="stock" name="stock" type="number" value={stockToAdd} onChange={(e) => setStockToAdd(e.target.value)} placeholder="0" className="p-2 border rounded-lg w-full" required min="1"/>
            </div>
            <div className="flex justify-end gap-3 pt-4">
                <Button type="button" onClick={onCancelar} variant="secondary">Cancelar</Button>
                <Button type="submit" variant="primary">Añadir Stock</Button>
            </div>
        </form>
    );
};
const FiltroBusquedaProducto = ({ setFiltros, filtros }) => { /* ...sin cambios... */
    const handleBusquedaChange = (e) => setFiltros({ ...filtros, busqueda: e.target.value });
    return (
        <div className="w-full">
            <input type="text" value={filtros.busqueda} onChange={handleBusquedaChange} placeholder="Buscar por Nombre, SKU o Categoría..." className="p-2 border rounded-lg w-full md:w-1/2 lg:w-1/3"/>
        </div>
    );
};
const logicaFiltroProductos = (productos, filtros) => { /* ...sin cambios... */
    const { busqueda } = filtros;
    if (!busqueda) return productos;
    const terminoBusquedaLower = busqueda.toLowerCase();
    return productos.filter(p => 
        p.name.toLowerCase().includes(terminoBusquedaLower) ||
        p.sku.toLowerCase().includes(terminoBusquedaLower) ||
        (p.category_name && p.category_name.toLowerCase().includes(terminoBusquedaLower))
    );
};


// --- COMPONENTE PRINCIPAL ---
const GestionProductos = ({ productos, proveedores, categorias, obtenerDatos, roles }) => {
    const [modalAbierto, setModalAbierto] = useState(false);
    const [stockModalAbierto, setStockModalAbierto] = useState(false);
    const [productoSeleccionado, setProductoSeleccionado] = useState(null);
    const { tokensAuth } = useContext(ContextoAuth);
    const puedeGestionar = roles?.esAdmin || roles?.esSuperAdmin;

    const { datosPaginados, FiltrosUI, PaginacionUI } = useFiltrosYBusqueda({
        items: productos,
        itemsPorPagina: 12, // Un número divisible por 2 y 3 para la vista de tarjetas
        logicaDeFiltro: logicaFiltroProductos,
        ComponenteFiltros: FiltroBusquedaProducto,
        filtrosIniciales: { busqueda: '' }
    });

    // La lógica de negocio no cambia
    const abrirModalNuevo = () => { /* ...sin cambios... */ };
    const abrirModalEditar = (producto) => { /* ...sin cambios... */ };
    const abrirModalStock = (producto) => { /* ...sin cambios... */ };
    const guardarProducto = async (datosProducto) => { /* ...sin cambios... */ };
    const guardarStock = async (datosStock) => { /* ...sin cambios... */ };
    const borrarProducto = async (idProducto) => { /* ...sin cambios... */ };
    
    // Oculto la lógica repetida para mayor claridad
    abrirModalNuevo = () => { setProductoSeleccionado({ name: '', sku: '', description: '', cost_price: '', sale_price: '', stock: 1, category: '', provider: null, estado: 'activo' }); setModalAbierto(true); };
    abrirModalEditar = (producto) => { setProductoSeleccionado({ ...producto, provider: producto.provider || null }); setModalAbierto(true); };
    abrirModalStock = (producto) => { setProductoSeleccionado(producto); setStockModalAbierto(true); };
    guardarProducto = async (datosProducto) => { const esEditando = !!datosProducto.id; const url = esEditando ? `${API_URL}/products/${datosProducto.id}/` : `${API_URL}/products/`; const method = esEditando ? 'PUT' : 'POST'; const payload = { ...datosProducto }; delete payload.provider_name; delete payload.category_name; if (payload.provider === '') payload.provider = null; const promesaDeGuardado = fetch(url, { method, headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) }, body: JSON.stringify(payload) }).then(res => { if (!res.ok) return res.json().then(err => Promise.reject(err)); return res.json(); }); toast.promise(promesaDeGuardado, { loading: 'Guardando producto...', success: () => { setModalAbierto(false); obtenerDatos(); return `Producto ${esEditando ? 'actualizado' : 'creado'} con éxito.`; }, error: (err) => `Error al guardar: ${JSON.stringify(err)}` }); };
    guardarStock = async (datosStock) => { if (!productoSeleccionado) return; const url = `${API_URL}/products/${productoSeleccionado.id}/update-stock/`; const promesaDeStock = fetch(url, { method: 'PATCH', headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) }, body: JSON.stringify({ stock: datosStock.stock }) }).then(res => { if (!res.ok) return res.json().then(err => Promise.reject(err)); return res.json(); }); toast.promise(promesaDeStock, { loading: 'Añadiendo stock...', success: () => { setStockModalAbierto(false); obtenerDatos(); return 'Stock añadido con éxito.'; }, error: (err) => `Error: ${JSON.stringify(err)}` }); };
    borrarProducto = async (idProducto) => { const confirmacion = window.confirm("¿Estás seguro de que quieres eliminar este producto? Esta acción no se puede deshacer."); if (!confirmacion) return; const promesaDeBorrado = new Promise(async (resolve, reject) => { try { const response = await fetch(`${API_URL}/products/${idProducto}/`, { method: 'DELETE', headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) } }); if (response.status === 204 || response.ok) { resolve("Producto eliminado con éxito."); } else { const errorData = await response.json(); reject(new Error(JSON.stringify(errorData))); } } catch (err) { reject(err); } }); toast.promise(promesaDeBorrado, { loading: 'Eliminando producto...', success: (mensaje) => { obtenerDatos(); return mensaje; }, error: (err) => `Error al eliminar: ${err.message}` }); };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center">
                <h1 className="text-3xl font-bold text-gray-800">Gestión de Productos</h1>
                {puedeGestionar && (
                    <Button onClick={abrirModalNuevo} icon={PlusCircle}>Nuevo Producto</Button>
                )}
            </div>
            
            <div className="flex justify-between items-center">
                {FiltrosUI}
            </div>

            {datosPaginados.length > 0 ? (
                <div>
                    {/* VISTA DE TABLA: Visible en pantallas medianas (md) y superiores */}
                    <div className="hidden md:block">
                        <Table 
                            headers={['SKU', 'Nombre', 'Precio Venta', 'Stock', 'Categoría', 'Estado', 'Acciones']} 
                            data={datosPaginados} 
                            renderRow={(p) => (
                                <tr key={p.id} className={`border-b hover:bg-gray-50 ${p.estado === 'inactivo' ? 'bg-gray-100 text-gray-400' : 'bg-white'}`}>
                                    <td className="px-4 py-3">{p.sku}</td>
                                    {/* --- TEXTO TRUNCADO CON TOOLTIP --- */}
                                    <td className="px-4 py-3 font-medium text-gray-900 max-w-xs truncate" title={p.name}>{p.name}</td>
                                    <td className="px-4 py-3 text-green-600 font-bold">${parseFloat(p.sale_price).toFixed(2)}</td>
                                    <td className="px-4 py-3">
                                        <span className={`px-2 py-1 rounded-full text-xs font-semibold ${p.stock <= 5 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>{p.stock}</span>
                                    </td>
                                    <td className="px-4 py-3">{p.category_name}</td>
                                    <td className="px-4 py-3">
                                        <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${p.estado === 'activo' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}`}>{p.estado === 'activo' ? 'Activo' : 'Inactivo'}</span>
                                    </td>
                                    <td className="px-4 py-3 flex gap-2">
                                        <Button onClick={() => abrirModalStock(p)} variant="secondary" size="sm" icon={PackagePlus} title="Añadir Stock" />
                                        {puedeGestionar && (
                                            <>
                                                <Button onClick={() => abrirModalEditar(p)} variant="secondary" size="sm" icon={Edit} title="Editar Producto"/>
                                                <Button onClick={() => borrarProducto(p.id)} variant="danger" size="sm" icon={Trash2} title="Borrar Producto"/>
                                            </>
                                        )}
                                    </td>
                                </tr>
                            )}
                        />
                    </div>

                    {/* VISTA DE TARJETAS: Visible por defecto, oculta en pantallas medianas (md) y superiores */}
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 md:hidden">
                        {datosPaginados.map(p => (
                            <div key={p.id} className={`bg-white rounded-lg shadow-md p-4 flex flex-col ${p.estado === 'inactivo' ? 'opacity-60' : ''}`}>
                                <div className="flex-grow mb-4">
                                    <div className="flex justify-between items-start">
                                        <h3 className="font-bold text-base text-gray-800 pr-2">{p.name}</h3>
                                        <span className={`flex-shrink-0 px-2 py-1 rounded-full text-xs font-semibold ${p.stock <= 5 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
                                            Stock: {p.stock}
                                        </span>
                                    </div>
                                    <p className="text-sm text-gray-500">{p.category_name}</p>
                                    <p className="text-sm text-gray-400 mt-1">SKU: {p.sku}</p>
                                    <p className="text-xl font-bold text-green-600 mt-2 text-right">${parseFloat(p.sale_price).toFixed(2)}</p>
                                </div>
                                <div className="pt-3 border-t flex gap-2">
                                    <Button onClick={() => abrirModalStock(p)} variant="secondary" size="sm" className="flex-1">Stock</Button>
                                    {puedeGestionar && (
                                        <>
                                            <Button onClick={() => abrirModalEditar(p)} variant="secondary" size="sm" icon={Edit} />
                                            <Button onClick={() => borrarProducto(p.id)} variant="danger" size="sm" icon={Trash2} />
                                        </>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* La paginación está fuera de las vistas para que siempre sea visible */}
                    <div className="mt-6">
                        {PaginacionUI}
                    </div>
                </div>
            ) : (
                <div className="text-center py-16 px-6 bg-white rounded-lg shadow-md">
                    <h3 className="text-lg font-semibold text-gray-700">No se encontraron productos</h3>
                    <p className="text-gray-500 mt-1">Intenta ajustar los términos de tu búsqueda o crea un producto nuevo.</p>
                </div>
            )}

            {/* Los modales no cambian y funcionan para ambas vistas */}
            {puedeGestionar && (
                <Modal isOpen={modalAbierto} onClose={() => setModalAbierto(false)} title={productoSeleccionado?.id ? 'Editar Producto' : 'Nuevo Producto'}>
                    {productoSeleccionado && <FormularioProducto producto={productoSeleccionado} onGuardar={guardarProducto} onCancelar={() => setModalAbierto(false)} categorias={categorias} proveedores={proveedores}/>}
                </Modal>
            )}
            <Modal isOpen={stockModalAbierto} onClose={() => setStockModalAbierto(false)} title={`Añadir Stock a: ${productoSeleccionado?.name}`}>
                {productoSeleccionado && <FormularioStock onGuardar={guardarStock} onCancelar={() => setStockModalAbierto(false)} />}
            </Modal>
        </div>
    );
};

export default GestionProductos;
