import React, { useState, useMemo, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Search, PlusCircle, Trash2, Edit, Users, ShoppingCart, DollarSign, Package, Truck, BarChart2, AlertCircle, UserPlus, LogOut, ChevronDown, CheckCircle } from 'lucide-react';

const API_URL = 'http://127.0.0.1:8000/api';

const Card = ({ children, className = '' }) => <div className={`bg-white rounded-xl shadow-md p-6 ${className}`}>{children}</div>;

const Button = ({ children, onClick, className = '', variant = 'primary', icon: Icon }) => {
  const baseClasses = 'px-4 py-2 rounded-lg font-semibold flex items-center justify-center gap-2 transition-transform transform hover:scale-105';
  const variants = {
    primary: 'bg-indigo-600 text-white hover:bg-indigo-700',
    secondary: 'bg-gray-200 text-gray-800 hover:bg-gray-300',
    danger: 'bg-red-500 text-white hover:bg-red-600',
    success: 'bg-green-500 text-white hover:bg-green-600',
  };
  return <button onClick={onClick} className={`${baseClasses} ${variants[variant]} ${className}`}>{Icon && <Icon size={18} />}{children}</button>;
};

const Modal = ({ children, isOpen, onClose, title }) => {
    if (!isOpen) return null;
    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center p-4">
            <div className="bg-white rounded-xl shadow-2xl w-full max-w-2xl p-6 relative">
                <h2 className="text-2xl font-bold text-gray-800 mb-4">{title}</h2>
                <button onClick={onClose} className="absolute top-4 right-4 text-gray-500 hover:text-gray-800">
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path></svg>
                </button>
                {children}
            </div>
        </div>
    );
};

const PuntoDeVenta = ({ products, onSaleComplete }) => {
  const [cart, setCart] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('Efectivo');
  const [isPaymentModalOpen, setPaymentModalOpen] = useState(false);
  const [cashReceived, setCashReceived] = useState(0);

  const filteredProducts = products.filter(p => p.name.toLowerCase().includes(searchTerm.toLowerCase()) || p.sku.toLowerCase().includes(searchTerm.toLowerCase()));

  const addToCart = (product) => {
    if (product.stock <= 0) {
      alert(`El producto "${product.name}" no tiene stock.`);
      return;
    }
    setCart(currentCart => {
      const existingItem = currentCart.find(item => item.id === product.id);
      if (existingItem) {
        if (existingItem.quantity >= product.stock) {
          alert(`No puedes agregar más unidades de "${product.name}". Stock máximo alcanzado.`);
          return currentCart;
        }
        return currentCart.map(item => item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item);
      }
      return [...currentCart, { ...product, quantity: 1, price: parseFloat(product.sale_price) }];
    });
  };

  const removeFromCart = (productId) => {
    setCart(currentCart => {
        const item = currentCart.find(item => item.id === productId);
        if (item && item.quantity > 1) {
            return currentCart.map(i => i.id === productId ? {...i, quantity: i.quantity - 1} : i);
        }
        return currentCart.filter(item => item.id !== productId);
    });
  };

  const total = useMemo(() => cart.reduce((sum, item) => sum + item.price * item.quantity, 0), [cart]);
  const change = useMemo(() => cashReceived > total ? cashReceived - total : 0, [cashReceived, total]);

  const handleProcessPayment = () => {
    if (cart.length === 0) {
        alert("El carrito está vacío.");
        return;
    }
    onSaleComplete(cart, total);
    setCart([]);
    setSearchTerm('');
    setPaymentModalOpen(false);
    setCashReceived(0);
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[calc(100vh-100px)]">
      <div className="lg:col-span-2 bg-gray-50 p-4 rounded-xl">
        <div className="relative mb-4">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={20}/>
          <input type="text" placeholder="Buscar producto por nombre o SKU..." className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
        </div>
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 overflow-y-auto h-[calc(100%-60px)] pr-2">
          {filteredProducts.map(product => (
            <div key={product.id} onClick={() => addToCart(product)} className={`p-3 rounded-lg shadow-sm border-2 transition-all text-center ${product.stock > 0 ? 'cursor-pointer hover:shadow-lg hover:border-indigo-500 border-transparent bg-white' : 'bg-gray-200 border-gray-300 opacity-60'}`}>
              <p className="font-bold text-gray-800 truncate">{product.name}</p>
              <p className="text-sm text-gray-600">${parseFloat(product.sale_price).toFixed(2)}</p>
              <p className={`text-xs ${product.stock <= 5 ? 'text-red-500 font-bold' : 'text-gray-500'}`}>Stock: {product.stock}</p>
            </div>
          ))}
        </div>
      </div>
      <Card className="flex flex-col">
        <h2 className="text-2xl font-bold text-gray-800 mb-4 flex items-center gap-2"><ShoppingCart size={24}/> Venta Actual</h2>
        <div className="flex-grow overflow-y-auto border-t border-b py-2">
          {cart.length === 0 ? <p className="text-gray-500 text-center py-10">El carrito está vacío</p> : cart.map(item => (
            <div key={item.id} className="flex justify-between items-center mb-2 p-2 rounded-md hover:bg-gray-100">
              <div>
                <p className="font-semibold">{item.name}</p>
                <p className="text-sm text-gray-600">${item.price.toFixed(2)} x {item.quantity}</p>
              </div>
              <div className="flex items-center gap-2">
                <p className="font-bold">${(item.price * item.quantity).toFixed(2)}</p>
                <button onClick={() => removeFromCart(item.id)} className="text-red-500 hover:text-red-700"><Trash2 size={18} /></button>
              </div>
            </div>
          ))}
        </div>
        <div className="mt-auto pt-4">
          <div className="flex justify-between text-2xl font-bold mb-4"><span>TOTAL:</span><span>${total.toFixed(2)}</span></div>
          <Button onClick={() => setPaymentModalOpen(true)} className="w-full text-lg" variant="success" icon={CheckCircle}>Cobrar</Button>
        </div>
      </Card>
      <Modal isOpen={isPaymentModalOpen} onClose={() => setPaymentModalOpen(false)} title="Procesar Pago">
        <div className="space-y-4">
          <div className="text-center text-4xl font-bold text-indigo-600 mb-4">${total.toFixed(2)}</div>
          <div>
            <label className="font-semibold text-gray-700">Método de Pago</label>
            <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)} className="w-full mt-1 p-2 border rounded-lg">
              <option>Efectivo</option>
              <option>Tarjeta de Crédito/Débito</option>
              <option>Mercado Pago/QR</option>
            </select>
          </div>
          {paymentMethod === 'Efectivo' && (
            <div>
              <label className="font-semibold text-gray-700">Efectivo Recibido</label>
              <input type="number" value={cashReceived} onChange={(e) => setCashReceived(parseFloat(e.target.value) || 0)} className="w-full mt-1 p-2 border rounded-lg" placeholder="0.00" />
              {cashReceived > total && <p className="text-lg font-semibold mt-2 text-green-600">Vuelto: ${change.toFixed(2)}</p>}
            </div>
          )}
          <div className="flex justify-end gap-3 pt-4">
            <Button onClick={() => setPaymentModalOpen(false)} variant="secondary">Cancelar</Button>
            <Button onClick={handleProcessPayment} variant="success">Finalizar Venta</Button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

// --- Módulo 2: Panel de Administración (El Cerebro) ---
const StatCard = ({ title, value, icon: Icon, color }) => (
    <Card className={`relative overflow-hidden`}>
        <div className={`absolute -top-4 -right-4 text-white p-4 rounded-full opacity-20 ${color}`}><Icon size={60} /></div>
        <h3 className="text-gray-500 font-semibold">{title}</h3>
        <p className="text-3xl font-bold text-gray-800">{value}</p>
    </Card>
);

const AdminDashboard = ({ setView, products, sales }) => {
    const totalSales = useMemo(() => sales.reduce((sum, sale) => sum + parseFloat(sale.total_amount), 0), [sales]);
    const lowStockItems = useMemo(() => products.filter(p => p.stock <= 5).length, [products]);
    const salesByDay = sales.reduce((acc, sale) => {
        const date = new Date(sale.date_time).toLocaleDateString('es-AR', { weekday: 'short' });
        acc[date] = (acc[date] || 0) + parseFloat(sale.total_amount);
        return acc;
    }, {});
    const chartData = Object.keys(salesByDay).map(day => ({ name: day, Ventas: salesByDay[day] }));
    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Panel de Administración</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <StatCard title="Ventas Totales (Histórico)" value={`$${totalSales.toFixed(2)}`} icon={DollarSign} color="bg-green-500"/>
                <StatCard title="Transacciones" value={sales.length} icon={ShoppingCart} color="bg-blue-500"/>
                <StatCard title="Productos Activos" value={products.length} icon={Package} color="bg-purple-500"/>
                <StatCard title="Alertas de Stock Bajo" value={lowStockItems} icon={AlertCircle} color="bg-red-500"/>
            </div>
            <div className="grid grid-cols-1 lg:grid-cols-5 gap-6">
                <div className="lg:col-span-3">
                    <Card>
                        <h2 className="text-xl font-bold text-gray-800 mb-4">Ventas de la Semana</h2>
                        <ResponsiveContainer width="100%" height={300}><BarChart data={chartData}><CartesianGrid strokeDasharray="3 3" /><XAxis dataKey="name" /><YAxis /><Tooltip formatter={(value) => `$${value.toFixed(2)}`} /><Legend /><Bar dataKey="Ventas" fill="#4f46e5" /></BarChart></ResponsiveContainer>
                    </Card>
                </div>
                <div className="lg:col-span-2">
                    <Card>
                        <h2 className="text-xl font-bold text-gray-800 mb-4">Productos con Stock Bajo</h2>
                        <ul className="space-y-2">{products.filter(p => p.stock <= 5 && p.stock > 0).map(p => <li key={p.id} className="flex justify-between items-center p-2 bg-amber-50 rounded-lg"><span>{p.name}</span><span className="font-bold text-amber-600">{p.stock} u.</span></li>)}{products.filter(p => p.stock === 0).map(p => <li key={p.id} className="flex justify-between items-center p-2 bg-red-50 rounded-lg"><span>{p.name}</span><span className="font-bold text-red-600">Agotado</span></li>)}{products.every(p => p.stock > 5) && <p className="text-gray-500">No hay productos con stock bajo.</p>}</ul>
                    </Card>
                </div>
            </div>
        </div>
    );
};

const Table = ({ headers, data, renderRow }) => (
    <div className="overflow-x-auto bg-white rounded-lg shadow">
        <table className="w-full text-sm text-left text-gray-500">
            <thead className="text-xs text-gray-700 uppercase bg-gray-50"><tr>{headers.map(header => <th key={header} scope="col" className="px-6 py-3">{header}</th>)}</tr></thead>
            <tbody>{data.map((item, index) => renderRow(item, index))}</tbody>
        </table>
    </div>
);

// --- Componente de Gestión de Productos (ACTUALIZADO) ---
const GestionProductos = ({ products, providers, categories, fetchData }) => {
    const [isModalOpen, setModalOpen] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);

    const openModalForNew = () => {
        setEditingProduct({ name: '', sku: '', description: '', cost_price: '', sale_price: '', stock: '', category: '', provider: '' });
        setModalOpen(true);
    };

    const openModalForEdit = (product) => {
        setEditingProduct(product);
        setModalOpen(true);
    };

    const handleSave = async (productData) => {
        const isEditing = !!productData.id;
        const url = isEditing ? `${API_URL}/products/${productData.id}/` : `${API_URL}/products/`;
        const method = isEditing ? 'PUT' : 'POST';

        try {
            const response = await fetch(url, {
                method: method,
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(productData)
            });
            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(JSON.stringify(errorData));
            }
            alert(`Producto ${isEditing ? 'actualizado' : 'creado'} con éxito.`);
            setModalOpen(false);
            fetchData(); // Recargamos los datos para ver los cambios
        } catch (err) {
            console.error("Error al guardar el producto:", err);
            alert(`Error: ${err.message}`);
        }
    };

    const handleDelete = async (productId) => {
        if (window.confirm("¿Estás seguro de que quieres eliminar este producto? Esta acción no se puede deshacer.")) {
            try {
                const response = await fetch(`${API_URL}/products/${productId}/`, { method: 'DELETE' });
                if (!response.ok) throw new Error('No se pudo eliminar el producto.');
                alert('Producto eliminado con éxito.');
                fetchData();
            } catch (err) {
                console.error("Error al eliminar el producto:", err);
                alert(`Error: ${err.message}`);
            }
        }
    };

    const ProductForm = ({ product, onSave, onCancel }) => {
        const [formData, setFormData] = useState(product);
        const handleChange = (e) => {
            const { name, value } = e.target;
            setFormData(prev => ({ ...prev, [name]: value }));
        };

        return (
            <form onSubmit={(e) => { e.preventDefault(); onSave(formData); }} className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <input name="name" value={formData.name} onChange={handleChange} placeholder="Nombre del producto" className="p-2 border rounded-lg w-full" required/>
                    <input name="sku" value={formData.sku} onChange={handleChange} placeholder="SKU / Código de Barras" className="p-2 border rounded-lg w-full" required/>
                </div>
                <textarea name="description" value={formData.description || ''} onChange={handleChange} placeholder="Descripción (opcional)" className="p-2 border rounded-lg w-full min-h-[80px]"/>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <input name="cost_price" type="number" step="0.01" value={formData.cost_price} onChange={handleChange} placeholder="Precio de costo" className="p-2 border rounded-lg w-full" required/>
                    <input name="sale_price" type="number" step="0.01" value={formData.sale_price} onChange={handleChange} placeholder="Precio de venta" className="p-2 border rounded-lg w-full" required/>
                    <input name="stock" type="number" value={formData.stock} onChange={handleChange} placeholder="Stock actual" className="p-2 border rounded-lg w-full" required/>
                </div>
                 <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <select name="category" value={formData.category} onChange={handleChange} className="p-2 border rounded-lg w-full" required>
                        <option value="">Seleccionar Categoría</option>
                        {categories.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                    </select>
                    <select name="provider" value={formData.provider || ''} onChange={handleChange} className="p-2 border rounded-lg w-full">
                        <option value="">Seleccionar Proveedor (opcional)</option>
                        {providers.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
                    </select>
                </div>
                <div className="flex justify-end gap-3 pt-4">
                    <Button type="button" onClick={onCancel} variant="secondary">Cancelar</Button>
                    <Button type="submit" variant="primary">Guardar Producto</Button>
                </div>
            </form>
        )
    };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center">
                <h1 className="text-3xl font-bold text-gray-800">Gestión de Productos</h1>
                <Button onClick={openModalForNew} icon={PlusCircle}>Nuevo Producto</Button>
            </div>

            <Table
                headers={['SKU', 'Nombre', 'Precio Venta', 'Stock', 'Categoría', 'Acciones']}
                data={products}
                renderRow={(product) => (
                    <tr key={product.id} className="bg-white border-b hover:bg-gray-50">
                        <td className="px-6 py-4 font-medium text-gray-900">{product.sku}</td>
                        <td className="px-6 py-4">{product.name}</td>
                        <td className="px-6 py-4">${parseFloat(product.sale_price).toFixed(2)}</td>
                        <td className="px-6 py-4"><span className={`px-2 py-1 rounded-full text-xs font-semibold ${product.stock <= 5 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>{product.stock}</span></td>
                        <td className="px-6 py-4">{product.category_name}</td>
                        <td className="px-6 py-4 flex gap-2"><button onClick={() => openModalForEdit(product)} className="text-indigo-600 hover:text-indigo-900"><Edit size={20}/></button><button onClick={() => handleDelete(product.id)} className="text-red-600 hover:text-red-900"><Trash2 size={20}/></button></td>
                    </tr>
                )}
            />

            <Modal isOpen={isModalOpen} onClose={() => setModalOpen(false)} title={editingProduct?.id ? 'Editar Producto' : 'Nuevo Producto'}>
                {editingProduct && <ProductForm product={editingProduct} onSave={handleSave} onCancel={() => setModalOpen(false)} />}
            </Modal>
        </div>
    );
};

const GestionVentas = ({ sales }) => {
    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Historial de Ventas</h1>
             <Table headers={['ID Venta', 'Fecha', 'Usuario', 'Items', 'Total', 'Detalle']} data={sales} renderRow={(sale) => (
                <tr key={sale.id} className="bg-white border-b hover:bg-gray-50">
                    <td className="px-6 py-4 font-medium">#{sale.id}</td>
                    <td className="px-6 py-4">{new Date(sale.date_time).toLocaleString('es-AR')}</td>
                    <td className="px-6 py-4">{sale.user}</td>
