const ReportesYEstadisticas = ({ products, sales }) => <div className="space-y-6"><h1 className="text-3xl font-bold text-gray-800">Reportes y Estadísticas</h1><p>Esta sección se puede construir usando los datos reales de ventas y productos.</p></div>;
const GestionUsuarios = () => <div className="space-y-6"><h1 className="text-3xl font-bold text-gray-800">Gestión de Usuarios</h1><p>Funcionalidad para crear usuarios (administradores, vendedores)...</p></div>;

// --- Componente Principal y Navegación ---
export default function App() {
  const [view, setView] = useState('pos');
  const [adminView, setAdminView] = useState('dashboard');
  const [user, setUser] = useState({ name: 'Vendedor 1', role: 'vendedor' });

  const [products, setProducts] = useState([]);
  const [sales, setSales] = useState([]);
  const [providers, setProviders] = useState([]);
  const [clients, setClients] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchData = async () => {
    try {
      setLoading(true);
      const endpoints = ['products', 'sales', 'providers', 'clients', 'categories'];
      const requests = endpoints.map(endpoint => fetch(`${API_URL}/${endpoint}/`).then(res => res.json()));
      const [productsData, salesData, providersData, clientsData, categoriesData] = await Promise.all(requests);
      setProducts(productsData);
      setSales(salesData);
      setProviders(providersData);
      setClients(clientsData);
      setCategories(categoriesData);
      setError(null);
    } catch (err) {
      console.error("Error al obtener los datos:", err);
      setError("No se pudo conectar con el servidor. Asegúrate de que el backend de Django esté funcionando.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchData(); }, []);

  const handleSaleComplete = async (cart, total) => {
    const saleData = {
      total_amount: total.toFixed(2),
      details: cart.map(item => ({ product_id: item.id, quantity: item.quantity, unit_price: item.price.toFixed(2) }))
    };
    try {
      const response = await fetch(`${API_URL}/sales/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(saleData),
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData[0] || 'Ocurrió un error al registrar la venta.');
      }
      alert('¡Venta registrada con éxito!');
      fetchData();
    } catch (err) {
      console.error("Error al registrar la venta:", err);
      alert(`Error: ${err.message}`);
    }
  };

  const loginAsAdmin = () => { setUser({ name: 'Admin', role: 'admin' }); setView('admin'); setAdminView('dashboard'); }
  const loginAsVendedor = () => { setUser({ name: 'Vendedor 1', role: 'vendedor' }); setView('pos'); }

  const renderAdminView = () => {
    switch (adminView) {
      case 'dashboard': return <AdminDashboard setView={setAdminView} products={products} sales={sales}/>;
      case 'products': return <GestionProductos products={products} providers={providers} categories={categories} fetchData={fetchData} />;
      case 'sales': return <GestionVentas sales={sales} />;
      case 'providers': return <GestionProveedores providers={providers} />;
      case 'clients': return <GestionClientes clients={clients} />;
      case 'reports': return <ReportesYEstadisticas products={products} sales={sales} />;
      case 'users': return <GestionUsuarios />;
      default: return <AdminDashboard setView={setAdminView} products={products} sales={sales}/>;
    }
  };

  const NavItem = ({ icon: Icon, children, onClick, active }) => (
    <li onClick={onClick} className={`flex items-center gap-3 px-4 py-3 cursor-pointer rounded-lg transition-colors ${active ? 'bg-indigo-600 text-white shadow-lg' : 'text-gray-600 hover:bg-gray-200'}`}><Icon size={22} /><span className="font-medium">{children}</span></li>
  );

  if (!user.role) {
      return (<div className="bg-gray-100 min-h-screen flex items-center justify-center"><Card className="text-center w-full max-w-sm"><h1 className="text-2xl font-bold mb-6">Iniciar Sesión</h1><div className="space-y-4"><Button onClick={loginAsAdmin} className="w-full" variant="primary">Entrar como Administrador</Button><Button onClick={loginAsVendedor} className="w-full" variant="secondary">Entrar como Vendedor</Button></div></Card></div>)
  }

  if (loading) return <div className="min-h-screen flex items-center justify-center">Cargando datos desde el servidor...</div>
  if (error) return <div className="min-h-screen flex items-center justify-center text-red-500 font-bold p-8 text-center">{error}</div>

  return (
    <div className="flex h-screen bg-gray-100 font-sans">
      {view === 'admin' && (
        <aside className="w-64 bg-white shadow-xl flex flex-col p-4">
          <h1 className="text-2xl font-bold text-indigo-600 px-4 mb-6">MiNegocio<span className="font-light">PRO</span></h1>
          <nav className="flex-grow"><ul className="space-y-2">
              <NavItem icon={BarChart2} active={adminView === 'dashboard'} onClick={() => setAdminView('dashboard')}>Dashboard</NavItem>
              <NavItem icon={Package} active={adminView === 'products'} onClick={() => setAdminView('products')}>Productos</NavItem>
              <NavItem icon={DollarSign} active={adminView === 'sales'} onClick={() => setAdminView('sales')}>Ventas</NavItem>
              <NavItem icon={Truck} active={adminView === 'providers'} onClick={() => setAdminView('providers')}>Proveedores</NavItem>
              <NavItem icon={Users} active={adminView === 'clients'} onClick={() => setAdminView('clients')}>Clientes</NavItem>
              <NavItem icon={BarChart} active={adminView === 'reports'} onClick={() => setAdminView('reports')}>Reportes</NavItem>
              <NavItem icon={UserPlus} active={adminView === 'users'} onClick={() => setAdminView('users')}>Usuarios</NavItem>
          </ul></nav>
          <div className="mt-auto"><NavItem icon={ShoppingCart} onClick={() => setView('pos')}>Ir al Punto de Venta</NavItem></div>
        </aside>
      )}
      <main className="flex-1 flex flex-col">
        <header className="bg-white shadow-sm p-4 flex justify-between items-center z-10">
          <div>{view === 'pos' && user.role === 'admin' && <Button onClick={() => setView('admin')} variant="secondary">Volver al Panel</Button>}</div>
          <div className="flex items-center gap-3"><span className="font-semibold text-gray-700">{user.name} ({user.role})</span><button onClick={() => setUser({role: null})} className="text-gray-500 hover:text-red-600"><LogOut size={20} /></button></div>
        </header>
        <div className="flex-1 p-6 overflow-y-auto">{view === 'pos' ? <PuntoDeVenta products={products} onSaleComplete={handleSaleComplete} /> : renderAdminView()}</div>
      </main>
    </div>
  );
}
