import React, { useState, useEffect, useContext, createContext, useCallback } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Search, PlusCircle, Trash2, Edit, ShoppingCart, DollarSign, Package, Truck, BarChart2, UserPlus, LogOut, CheckCircle, Eye, EyeOff, Users, Activity, TrendingUp } from 'lucide-react';

const API_URL = 'http://127.0.0.1:8000/api';

// --- Función para decodificar JWT de forma segura ---
const decodeToken = (token) => {
    try {
        return JSON.parse(atob(token.split('.')[1]));
    } catch (e) {
        console.error("Error decoding token", e);
        return null;
    }
}

// --- Contexto de Autenticación ---
const AuthContext = createContext();

// --- Componentes de la UI ---
const Card = ({ children, className = '' }) => <div className={`bg-white rounded-xl shadow-md p-6 ${className}`}>{children}</div>;
const Button = ({ children, onClick, className = '', variant = 'primary', icon: Icon, type = 'button', disabled = false }) => {
  const baseClasses = 'px-4 py-2 rounded-lg font-semibold flex items-center justify-center gap-2 transition-transform transform hover:scale-105';
  const variants = { primary: 'bg-indigo-600 text-white hover:bg-indigo-700', secondary: 'bg-gray-200 text-gray-800 hover:bg-gray-300', danger: 'bg-red-500 text-white hover:bg-red-600', success: 'bg-green-500 text-white hover:bg-green-600' };
  return <button type={type} onClick={onClick} disabled={disabled} className={`${baseClasses} ${variants[variant]} ${className} ${disabled ? 'opacity-50 cursor-not-allowed' : ''}`}>{Icon && <Icon size={18} />}{children}</button>;
};
const Modal = ({ children, isOpen, onClose, title }) => {
    if (!isOpen) return null;
    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center p-4">
            <div className="bg-white rounded-xl shadow-2xl w-full max-w-lg p-6 relative">
                <h2 className="text-2xl font-bold text-gray-800 mb-4">{title}</h2>
                <button onClick={onClose} className="absolute top-4 right-4 text-gray-500 hover:text-gray-800"><svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path></svg></button>
                {children}
            </div>
        </div>
    );
};
const Table = ({ headers, data, renderRow }) => (
    <div className="overflow-x-auto bg-white rounded-lg shadow">
        <table className="w-full text-sm text-left text-gray-500"><thead className="text-xs text-gray-700 uppercase bg-gray-50"><tr>{headers.map(header => <th key={header} scope="col" className="px-6 py-3">{header}</th>)}</tr></thead><tbody>{data.map((item, index) => renderRow(item, index))}</tbody></table>
    </div>
);
const StatCard = ({ title, value, icon: Icon, color, subtitle }) => (
    <Card className={`relative overflow-hidden`}>
        <div className={`absolute -top-4 -right-4 p-4 rounded-full opacity-20 ${color}`}><Icon size={60} /></div>
        <h3 className="text-gray-500 font-semibold">{title}</h3>
        <p className="text-3xl font-bold text-gray-800 truncate" title={value}>{value}</p>
        {subtitle && <p className="text-sm text-gray-500">{subtitle}</p>}
    </Card>
);

// --- Componentes específicos ---
const LoginPage = () => {
    const { loginUser } = useContext(AuthContext);
    const [passwordVisible, setPasswordVisible] = useState(false);
    const handleSubmit = (e) => { e.preventDefault(); loginUser(e.target.username.value, e.target.password.value); };
    return (
        <div className="bg-gray-100 min-h-screen flex items-center justify-center">
            <Card className="text-center w-full max-w-sm">
                <h1 className="text-3xl font-bold mb-2 text-indigo-600">MiNegocio<span className="font-light">PRO</span></h1>
                <p className="text-gray-500 mb-6">Inicia sesión para continuar</p>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input type="text" name="username" required className="w-full p-3 border rounded-lg" placeholder="Nombre de usuario" />
                    <div className="relative">
                        <input type={passwordVisible ? "text" : "password"} name="password" required className="w-full p-3 border rounded-lg pr-10" placeholder="Contraseña" />
                        <span onClick={() => setPasswordVisible(!passwordVisible)} className="absolute top-1/2 right-3 -translate-y-1/2 cursor-pointer text-gray-400 hover:text-gray-600">
                            {passwordVisible ? <EyeOff size={20} /> : <Eye size={20} />}
                        </span>
                    </div>
                    <Button type="submit" className="w-full text-lg" variant="primary">Ingresar</Button>
                </form>
            </Card>
        </div>
    );
};
const PuntoDeVenta = ({ products, onSaleComplete }) => {
    const [cart, setCart] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [isPaymentModalOpen, setPaymentModalOpen] = useState(false);
    const [cashReceived, setCashReceived] = useState(0);
    const [paymentMethod, setPaymentMethod] = useState('Efectivo');
    const filteredProducts = products.filter(p => p.name.toLowerCase().includes(searchTerm.toLowerCase()) || p.sku.toLowerCase().includes(searchTerm.toLowerCase()));
    const addToCart = (product) => {
        if (product.stock <= 0) { alert(`El producto "${product.name}" no tiene stock.`); return; }
        setCart(currentCart => {
            const existingItem = currentCart.find(item => item.id === product.id);
            if (existingItem) {
                if (existingItem.quantity >= product.stock) { alert(`No puedes agregar más unidades.`); return currentCart; }
                return currentCart.map(item => item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item);
            }
            return [...currentCart, { ...product, quantity: 1, price: parseFloat(product.sale_price) }];
        });
    };
    const handleProcessPayment = () => {
        if (cart.length === 0) { alert("El carrito está vacío."); return; }
        onSaleComplete(cart, cart.reduce((sum, item) => sum + item.price * item.quantity, 0));
        setCart([]); setSearchTerm(''); setPaymentModalOpen(false); setCashReceived(0);
    };
    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    const change = cashReceived > total ? cashReceived - total : 0;
    return (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[calc(100vh-100px)]">
            <div className="lg:col-span-2 bg-gray-50 p-4 rounded-xl"><div className="relative mb-4"><Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={20}/><input type="text" placeholder="Buscar producto por nombre o SKU..." className="w-full pl-10 pr-4 py-2 border rounded-lg" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} /></div><div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 overflow-y-auto h-[calc(100%-60px)] pr-2">{filteredProducts.map(product => (<div key={product.id} onClick={() => addToCart(product)} className={`p-3 rounded-lg shadow-sm border-2 text-center ${product.stock > 0 ? 'cursor-pointer hover:shadow-lg bg-white' : 'bg-gray-200 opacity-60'}`}><p className="font-bold text-gray-800 truncate">{product.name}</p><p className="text-sm text-gray-600">${parseFloat(product.sale_price).toFixed(2)}</p><p className={`text-xs ${product.stock <= 5 ? 'text-red-500 font-bold' : 'text-gray-500'}`}>Stock: {product.stock}</p></div>))}</div></div>
            <Card className="flex flex-col"><h2 className="text-2xl font-bold text-gray-800 mb-4 flex items-center gap-2"><ShoppingCart size={24}/> Venta Actual</h2><div className="flex-grow overflow-y-auto border-t border-b py-2">{cart.length === 0 ? <p className="text-gray-500 text-center py-10">El carrito está vacío</p> : cart.map(item => <div key={item.id} className="flex justify-between items-center mb-2 p-2 rounded-md hover:bg-gray-100"><div><p className="font-semibold">{item.name}</p><p className="text-sm text-gray-600">${item.price.toFixed(2)} x {item.quantity}</p></div><div className="flex items-center gap-2"><p className="font-bold">${(item.price * item.quantity).toFixed(2)}</p><button onClick={() => setCart(cart.filter(c => c.id !== item.id))} className="text-red-500 hover:text-red-700"><Trash2 size={18} /></button></div></div>)}</div><div className="mt-auto pt-4"><div className="flex justify-between text-2xl font-bold mb-4"><span>TOTAL:</span><span>${total.toFixed(2)}</span></div><Button onClick={() => setPaymentModalOpen(true)} className="w-full text-lg" variant="success" icon={CheckCircle}>Cobrar</Button></div></Card>
            <Modal isOpen={isPaymentModalOpen} onClose={() => setPaymentModalOpen(false)} title="Procesar Pago">
                <div className="space-y-4">
                    <div className="text-center text-4xl font-bold text-indigo-600 mb-4">${total.toFixed(2)}</div>
                    <div>
                        <label className="font-semibold text-gray-700">Método de Pago</label>
                        <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)} className="w-full mt-1 p-2 border rounded-lg">
                            <option>Efectivo</option>
                            <option>Tarjeta de Crédito/Débito</option>
                            <option>Mercado Pago/QR</option>
                        </select>
                    </div>
                    {paymentMethod === 'Efectivo' && (
                        <div>
                            <label className="font-semibold text-gray-700">Efectivo Recibido</label>
                            <input type="number" value={cashReceived} onChange={(e) => setCashReceived(parseFloat(e.target.value) || 0)} className="w-full mt-1 p-2 border rounded-lg" placeholder="0.00" />
                            {cashReceived > total && <p className="text-lg font-semibold mt-2 text-green-600">Vuelto: ${change.toFixed(2)}</p>}
                        </div>
                    )}
                    <div className="flex justify-end gap-3 pt-4"><Button onClick={() => setPaymentModalOpen(false)} variant="secondary">Cancelar</Button><Button onClick={handleProcessPayment} variant="success">Finalizar Venta</Button></div>
                </div>
            </Modal>
        </div>
    );
};
const GestionProductos = ({ products, providers, categories, fetchData }) => {
    const [isModalOpen, setModalOpen] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const { authTokens } = useContext(AuthContext);
    const openModalForNew = () => { setEditingProduct({ name: '', sku: '', description: '', cost_price: '', sale_price: '', stock: '', category: '', provider: null }); setModalOpen(true); };
    const openModalForEdit = (product) => { setEditingProduct(product); setModalOpen(true); };
    const handleSave = async (productData) => {
        const isEditing = !!productData.id;
        const url = isEditing ? `${API_URL}/products/${productData.id}/` : `${API_URL}/products/`;
        const method = isEditing ? 'PUT' : 'POST';
        if (productData.provider === '') productData.provider = null;
        try {
            const response = await fetch(url, { method: method, headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(authTokens.access) }, body: JSON.stringify(productData) });
            if (!response.ok) { const errorData = await response.json(); throw new Error(JSON.stringify(errorData)); }
            alert(`Producto ${isEditing ? 'actualizado' : 'creado'}.`); setModalOpen(false); fetchData();
        } catch (err) { alert(`Error: ${err.message}`); }
    };
    const handleDelete = async (productId) => {
        if (window.confirm("¿Estás seguro de que quieres eliminar este producto?")) {
            try {
                await fetch(`${API_URL}/products/${productId}/`, { method: 'DELETE', headers: { 'Authorization': 'Bearer ' + String(authTokens.access) }});
                alert('Producto eliminado.'); fetchData();
            } catch (err) { alert(`Error: ${err.message}`); }
        }
    };
    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center"><h1 className="text-3xl font-bold text-gray-800">Gestión de Productos</h1><Button onClick={openModalForNew} icon={PlusCircle}>Nuevo Producto</Button></div>
            <Table headers={['SKU', 'Nombre', 'Precio Venta', 'Stock', 'Categoría', 'Acciones']} data={products} renderRow={(p) => (<tr key={p.id} className="bg-white border-b hover:bg-gray-50"><td className="px-6 py-4">{p.sku}</td><td className="px-6 py-4">{p.name}</td><td className="px-6 py-4">${parseFloat(p.sale_price).toFixed(2)}</td><td className="px-6 py-4"><span className={`px-2 py-1 rounded-full text-xs ${p.stock <= 5 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>{p.stock}</span></td><td className="px-6 py-4">{p.category_name}</td><td className="px-6 py-4 flex gap-2"><button onClick={() => openModalForEdit(p)} className="text-indigo-600"><Edit size={20}/></button><button onClick={() => handleDelete(p.id)} className="text-red-600"><Trash2 size={20}/></button></td></tr>)}/>
            <Modal isOpen={isModalOpen} onClose={() => setModalOpen(false)} title={editingProduct?.id ? 'Editar Producto' : 'Nuevo Producto'}>{editingProduct && <ProductForm product={editingProduct} onSave={handleSave} onCancel={() => setModalOpen(false)} categories={categories} providers={providers}/>}</Modal>
        </div>
    );
};
const ProductForm = ({ product, onSave, onCancel, categories, providers }) => {
    const [formData, setFormData] = useState(product);
    const handleChange = (e) => { const { name, value } = e.target; setFormData(prev => ({ ...prev, [name]: value })); };
    return (
        <form onSubmit={(e) => { e.preventDefault(); onSave(formData); }} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4"><input name="name" value={formData.name} onChange={handleChange} placeholder="Nombre del producto" className="p-2 border rounded-lg w-full" required/><input name="sku" value={formData.sku} onChange={handleChange} placeholder="SKU" className="p-2 border rounded-lg w-full" required/></div><textarea name="description" value={formData.description || ''} onChange={handleChange} placeholder="Descripción" className="p-2 border rounded-lg w-full min-h-[80px]"/><div className="grid grid-cols-1 md:grid-cols-3 gap-4"><input name="cost_price" type="number" step="0.01" value={formData.cost_price} onChange={handleChange} placeholder="Costo" className="p-2 border rounded-lg w-full" required/><input name="sale_price" type="number" step="0.01" value={formData.sale_price} onChange={handleChange} placeholder="Venta" className="p-2 border rounded-lg w-full" required/><input name="stock" type="number" value={formData.stock} onChange={handleChange} placeholder="Stock" className="p-2 border rounded-lg w-full" required/></div><div className="grid grid-cols-1 md:grid-cols-2 gap-4"><select name="category" value={formData.category} onChange={handleChange} className="p-2 border rounded-lg w-full" required><option value="">Categoría</option>{categories.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}</select><select name="provider" value={formData.provider || ''} onChange={handleChange} className="p-2 border rounded-lg w-full"><option value="">Proveedor</option>{providers.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}</select></div><div className="flex justify-end gap-3 pt-4"><Button type="button" onClick={onCancel} variant="secondary">Cancelar</Button><Button type="submit" variant="primary">Guardar</Button></div>
        </form>
    )
};
const AdminDashboard = ({ products, sales }) => {
    const totalSales = sales.reduce((sum, sale) => sum + parseFloat(sale.total_amount), 0);
    const chartData = sales.reduce((acc, sale) => { const date = new Date(sale.date_time).toLocaleDateString('es-AR', { weekday: 'short' }); acc[date] = (acc[date] || 0) + parseFloat(sale.total_amount); return acc; }, {});
    const chartDataFormatted = Object.keys(chartData).map(day => ({ name: day, Ventas: chartData[day] }));
    return <div className="space-y-6"><h1 className="text-3xl font-bold text-gray-800">Dashboard</h1><div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6"><StatCard title="Ventas Totales" value={`$${totalSales.toFixed(2)}`} icon={DollarSign} color="bg-green-500" /><StatCard title="Transacciones" value={sales.length} icon={ShoppingCart} color="bg-blue-500" /><StatCard title="Productos Activos" value={products.length} icon={Package} color="bg-purple-500" /></div><Card><h2 className="text-xl font-bold text-gray-800 mb-4">Ventas de la Semana</h2><ResponsiveContainer width="100%" height={300}><BarChart data={chartDataFormatted}><CartesianGrid strokeDasharray="3 3" /><XAxis dataKey="name" /><YAxis /><Tooltip formatter={(value) => `$${value.toFixed(2)}`} /><Legend /><Bar dataKey="Ventas" fill="#4f46e5" /></BarChart></ResponsiveContainer></Card></div>;
};
const GestionVentas = ({ sales }) => <div className="space-y-6"><h1 className="text-3xl font-bold text-gray-800">Historial de Ventas</h1><Table headers={['ID', 'Fecha', 'Usuario', 'Items', 'Total']} data={sales} renderRow={(s) => <tr key={s.id} className="bg-white border-b hover:bg-gray-50"><td className="px-6 py-4">#{s.id}</td><td className="px-6 py-4">{new Date(s.date_time).toLocaleString('es-AR')}</td><td className="px-6 py-4">{s.user}</td><td className="px-6 py-4">{s.details.length}</td><td className="px-6 py-4 font-bold">${parseFloat(s.total_amount).toFixed(2)}</td></tr>} /></div>;
const GestionProveedores = ({providers, fetchData}) => {
    return <div className="space-y-6"><h1 className="text-3xl font-bold text-gray-800">Proveedores</h1><p>Sección en construcción</p></div>;
};
const GestionClientes = ({ clients, fetchData }) => {
    return <div className="space-y-6"><h1 className="text-3xl font-bold text-gray-800">Clientes</h1><p>Sección en construcción</p></div>;
};
const ReportesYEstadisticas = () => {
    const [reportData, setReportData] = useState(null);
    const { authTokens } = useContext(AuthContext);
    useEffect(() => {
        const fetchReportData = async () => {
            try {
                const response = await fetch(`${API_URL}/reports/`, { headers: { 'Authorization': 'Bearer ' + String(authTokens.access) } });
                if (!response.ok) throw new Error('No se pudieron cargar los reportes.');
                const data = await response.json(); setReportData(data);
            } catch (error) { console.error(error); }
        };
        fetchReportData();
    }, [authTokens]);
    if (!reportData) return <div className="p-6">Cargando reportes...</div>;
    return (
        <div className="space-y-6"><h1 className="text-3xl font-bold text-gray-800">Reportes y Estadísticas</h1><div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"><StatCard title="Producto Más Vendido" value={reportData.most_sold_product.name} subtitle={`${reportData.most_sold_product.total_sold} u.`} icon={Package} color="bg-indigo-500" /><StatCard title="Producto Más Rentable" value={reportData.most_profitable_product.name} subtitle={`$${parseFloat(reportData.most_profitable_product.profit_margin).toFixed(2)} de margen`} icon={TrendingUp} color="bg-green-500" /><StatCard title="Hora Pico de Ventas" value={reportData.peak_hour.hour !== 'N/A' ? `${reportData.peak_hour.hour}:00 - ${reportData.peak_hour.hour + 1}:00` : 'N/A'} subtitle={`${reportData.peak_hour.count} ventas`} icon={Activity} color="bg-amber-500" /></div></div>
    );
};
const GestionUsuarios = ({ allUsers, groups, fetchData }) => {
    const [isModalOpen, setModalOpen] = useState(false);
    const { authTokens, user: currentUser } = useContext(AuthContext);

    const handleSave = async (userData) => {
        try {
            const response = await fetch(`${API_URL}/users/`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(authTokens.access) },
                body: JSON.stringify(userData),
            });
            if (!response.ok) { const errorData = await response.json(); throw new Error(JSON.stringify(errorData)); }
            alert('Usuario creado con éxito.'); setModalOpen(false); fetchData();
        } catch (err) { alert(`Error al crear usuario: ${err.message}`); }
    };

    const handleDelete = async (userId) => {
        if (userId === currentUser.user_id) { alert("No puedes eliminar tu propia cuenta."); return; }
        if (window.confirm("¿Estás seguro de que quieres eliminar este usuario?")) {
            try {
                await fetch(`${API_URL}/users/${userId}/`, { method: 'DELETE', headers: { 'Authorization': 'Bearer ' + String(authTokens.access) }});
                alert('Usuario eliminado con éxito.'); fetchData();
            } catch (err) { alert(`Error al eliminar: ${err.message}`); }
        }
    };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center"><h1 className="text-3xl font-bold text-gray-800">Gestión de Usuarios</h1><Button onClick={() => setModalOpen(true)} icon={UserPlus}>Nuevo Usuario</Button></div>
            <Table headers={['ID', 'Username', 'Grupos', 'Acciones']} data={allUsers} renderRow={(user) => (<tr key={user.id} className="bg-white border-b hover:bg-gray-50"><td className="px-6 py-4">{user.id}</td><td className="px-6 py-4">{user.username}</td><td className="px-6 py-4">{user.groups.join(', ')}</td><td className="px-6 py-4"><Button onClick={() => handleDelete(user.id)} variant="danger" disabled={user.id === currentUser.user_id} icon={Trash2}/></td></tr>)}/>
            <Modal isOpen={isModalOpen} onClose={() => setModalOpen(false)} title="Crear Nuevo Usuario"><UserForm onSave={handleSave} onCancel={() => setModalOpen(false)} groups={groups} /></Modal>
        </div>
    );
};
const UserForm = ({ onSave, onCancel, groups }) => {
    const [formData, setFormData] = useState({ username: '', password: '', groups: [] });
    const handleChange = (e) => { const { name, value } = e.target; setFormData(prev => ({ ...prev, [name]: value })); };
    const handleGroupChange = (e) => {
        const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
        setFormData(prev => ({...prev, groups: selectedOptions}));
    };
    return (
        <form onSubmit={(e) => { e.preventDefault(); onSave(formData); }} className="space-y-4">
            <input name="username" value={formData.username} onChange={handleChange} placeholder="Nombre de usuario" className="p-2 border rounded-lg w-full" required />
            <input name="password" type="password" value={formData.password} onChange={handleChange} placeholder="Contraseña" className="p-2 border rounded-lg w-full" required />
            <div>
                <label className="block mb-2 text-sm font-medium text-gray-900">Grupo(s)</label>
                <select multiple value={formData.groups} onChange={handleGroupChange} className="p-2 border rounded-lg w-full" required>
                    {groups.map(g => <option key={g.name} value={g.name}>{g.name}</option>)}
                </select>
                <p className="text-xs text-gray-500 mt-1">Mantén Ctrl (o Cmd en Mac) para seleccionar más de uno.</p>
            </div>
            <div className="flex justify-end gap-3 pt-4"><Button type="button" onClick={onCancel} variant="secondary">Cancelar</Button><Button type="submit" variant="primary">Crear Usuario</Button></div>
        </form>
    );
};

// --- AuthProvider ---
const AuthProvider = ({ children }) => {
    const [authTokens, setAuthTokens] = useState(() => localStorage.getItem('authTokens') ? JSON.parse(localStorage.getItem('authTokens')) : null);
    const [user, setUser] = useState(() => {
        const token = localStorage.getItem('authTokens');
        if (!token) return null;
        try {
            return decodeToken(JSON.parse(token).access);
        } catch (e) {
            return null;
        }
    });
    const [loading, setLoading] = useState(true);
    const loginUser = async (username, password) => {
        const response = await fetch(`${API_URL}/token/`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ username, password }) });
        const data = await response.json();
        if (response.ok) { 
            const decodedUser = decodeToken(data.access);
            setAuthTokens(data); 
            setUser(decodedUser); 
            localStorage.setItem('authTokens', JSON.stringify(data)); 
        } else { alert('Usuario o contraseña incorrectos.'); }
    };
    const logoutUser = useCallback(() => { setAuthTokens(null); setUser(null); localStorage.removeItem('authTokens'); }, []);
    const updateToken = useCallback(async () => {
        const currentTokens = JSON.parse(localStorage.getItem('authTokens'));
        if (!currentTokens?.refresh) { logoutUser(); return; }
        try {
            const response = await fetch(`${API_URL}/token/refresh/`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ 'refresh': currentTokens.refresh }) });
            const data = await response.json();
            if (response.ok) {
                const newTokens = { ...currentTokens, access: data.access };
                setAuthTokens(newTokens); 
                setUser(decodeToken(data.access));
                localStorage.setItem('authTokens', JSON.stringify(newTokens));
            } else { logoutUser(); }
        } catch { logoutUser(); }
    }, [logoutUser]);
    useEffect(() => { const interval = setInterval(() => { if (authTokens) updateToken(); }, 1000 * 60 * 4); return () => clearInterval(interval); }, [authTokens, updateToken]);
    useEffect(() => { setLoading(false); }, []);
    return (<AuthContext.Provider value={{ user, authTokens, loginUser, logoutUser }}>{loading ? <div className="min-h-screen flex items-center justify-center">Cargando...</div> : children}</AuthContext.Provider>);
};

// --- Componente Principal ---
export default function AppWrapper() { return (<AuthProvider><App /></AuthProvider>); }

function App() {
    const { user, authTokens, logoutUser } = useContext(AuthContext);
    const [view, setView] = useState('pos');
    const [adminView, setAdminView] = useState('dashboard');
    const [products, setProducts] = useState([]);
    const [sales, setSales] = useState([]);
    const [providers, setProviders] = useState([]);
    const [clients, setClients] = useState([]);
    const [categories, setCategories] = useState([]);
    const [allUsers, setAllUsers] = useState([]);
    const [groups, setGroups] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const isAdmin = user?.groups?.includes('Administradores');

    const fetchData = useCallback(async () => {
        if (!authTokens) return;
        setLoading(true);
        let endpoints = ['products', 'sales'];
        if (isAdmin) {
            endpoints.push('providers', 'clients', 'categories', 'users', 'groups');
        }
        try {
            const requests = endpoints.map(endpoint => 
                fetch(`${API_URL}/${endpoint}/`, { headers: { 'Authorization': 'Bearer ' + String(authTokens.access) } }).then(res => {
                    if(res.status === 401) { logoutUser(); return Promise.reject(new Error("Token inválido")); }
                    if (!res.ok) return Promise.resolve([]);
                    return res.json()
                })
            );
            const data = await Promise.all(requests);
            const dataMap = Object.fromEntries(endpoints.map((e, i) => [e, data[i]]));

            setProducts(dataMap.products || []); setSales(dataMap.sales || []);
            if(isAdmin) {
                setProviders(dataMap.providers || []); setClients(dataMap.clients || []); 
                setCategories(dataMap.categories || []); setAllUsers(dataMap.users || []);
                setGroups(dataMap.groups || []);
            }
            setError(null);
        } catch (err) {
            if (err.message !== "Token inválido") { setError("No se pudo conectar con el servidor."); }
        } finally { setLoading(false); }
    }, [authTokens, logoutUser, isAdmin]); 

    useEffect(() => { fetchData(); }, [fetchData]);
    useEffect(() => { if (user && !isAdmin) setView('pos'); }, [user, isAdmin]);

    const handleSaleComplete = async (cart, total) => {
        const saleData = { total_amount: total.toFixed(2), details: cart.map(item => ({ product_id: item.id, quantity: item.quantity, unit_price: item.price.toFixed(2) })) };
        try {
            const response = await fetch(`${API_URL}/sales/`, { method: 'POST', headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(authTokens.access) }, body: JSON.stringify(saleData) });
            if (!response.ok) { const errorData = await response.json(); throw new Error(JSON.stringify(errorData)); }
            alert('Venta registrada.'); fetchData();
        } catch (err) { alert(`Error: ${err.message}`); }
    };

    const renderAdminView = () => {
        switch (adminView) {
            case 'dashboard': return <AdminDashboard products={products} sales={sales} />;
            case 'products': return <GestionProductos products={products} providers={providers} categories={categories} fetchData={fetchData} />;
            case 'sales': return <GestionVentas sales={sales} />;
            case 'providers': return <GestionProveedores providers={providers} fetchData={fetchData} />;
            case 'clients': return <GestionClientes clients={clients} fetchData={fetchData} />;
            case 'reports': return <ReportesYEstadisticas />;
            case 'users': return <GestionUsuarios allUsers={allUsers} groups={groups} fetchData={fetchData} />;
            default: return <AdminDashboard products={products} sales={sales}/>;
        }
    };

    const NavItem = ({ icon: Icon, children, onClick, active }) => (<li onClick={onClick} className={`flex items-center gap-3 px-4 py-3 cursor-pointer rounded-lg ${active ? 'bg-indigo-600 text-white' : 'hover:bg-gray-200'}`}><Icon size={22} /><span className="font-medium">{children}</span></li>);

    if (!user) return <LoginPage />;
    if (loading) return <div className="min-h-screen flex items-center justify-center">Cargando datos...</div>
    if (error) return <div className="min-h-screen flex items-center justify-center text-red-500">{error}</div>

    return (
        <div className="flex h-screen bg-gray-100 font-sans">
            {isAdmin && view === 'admin' && (
                <aside className="w-64 bg-white shadow-xl flex flex-col p-4">
                    <h1 className="text-2xl font-bold text-indigo-600 px-4 mb-6">MiNegocio<span className="font-light">PRO</span></h1>
                    <nav className="flex-grow"><ul className="space-y-2">
                        <NavItem icon={BarChart2} active={adminView === 'dashboard'} onClick={() => setAdminView('dashboard')}>Dashboard</NavItem>
                        <NavItem icon={Package} active={adminView === 'products'} onClick={() => setAdminView('products')}>Productos</NavItem>
                        <NavItem icon={DollarSign} active={adminView === 'sales'} onClick={() => setAdminView('sales')}>Ventas</NavItem>
                        <NavItem icon={Truck} active={adminView === 'providers'} onClick={() => setAdminView('providers')}>Proveedores</NavItem>
                        <NavItem icon={Users} active={adminView === 'clients'} onClick={() => setAdminView('clients')}>Clientes</NavItem>
                        <NavItem icon={BarChart} active={adminView === 'reports'} onClick={() => setAdminView('reports')}>Reportes</NavItem>
                        <NavItem icon={UserPlus} active={adminView === 'users'} onClick={() => setAdminView('users')}>Usuarios</NavItem>
                    </ul></nav>
                    <div className="mt-auto"><NavItem icon={ShoppingCart} onClick={() => setView('pos')}>Ir al Punto de Venta</NavItem></div>
                </aside>
            )}
            <main className="flex-1 flex flex-col">
                <header className="bg-white shadow-sm p-4 flex justify-between items-center z-10">
                    <div>{isAdmin && view === 'pos' && <Button onClick={() => setView('admin')} variant="secondary">Volver al Panel</Button>}</div>
                    <div className="flex items-center gap-3"><span className="font-semibold text-gray-700">{user.username}</span><button onClick={logoutUser} className="text-gray-500 hover:text-red-600"><LogOut size={20} /></button></div>
                </header>
                <div className="flex-1 p-6 overflow-y-auto">{view === 'pos' || !isAdmin ? <PuntoDeVenta products={products} onSaleComplete={handleSaleComplete} /> : renderAdminView()}</div>
            </main>
        </div>
    );
}
