import React, { useState, useEffect, useContext, createContext, useCallback } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Search, PlusCircle, Trash2, Edit, ShoppingCart, DollarSign, Package, Truck, BarChart2, UserPlus, LogOut, CheckCircle, Eye, EyeOff, Users, Activity, TrendingUp, Archive, UploadCloud, CreditCard } from 'lucide-react';

// Importamos nuestros módulos refactorizados
import { ContextoAuth } from './context/AuthContext';
import { Card, Button, Modal, Table, StatCard } from './components/ui/ComponentesUI';

const API_URL = 'http://127.0.0.1:8000/api';

// --- Componentes específicos de la aplicación ---

const PaginaLogin = () => {
    const { iniciarSesion } = useContext(ContextoAuth);
    const [passwordVisible, setPasswordVisible] = useState(false);
    const handleSubmit = (e) => { e.preventDefault(); iniciarSesion(e.target.username.value, e.target.password.value); };
    return (
        <div className="bg-gray-100 min-h-screen flex items-center justify-center">
            <Card className="text-center w-full max-w-sm">
                <h1 className="text-3xl font-bold mb-2 text-indigo-600">MiNegocio<span className="font-light">PRO</span></h1>
                <p className="text-gray-500 mb-6">Inicia sesión para continuar</p>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input type="text" name="username" required className="w-full p-3 border rounded-lg" placeholder="Nombre de usuario" />
                    <div className="relative">
                        <input type={passwordVisible ? "text" : "password"} name="password" required className="w-full p-3 border rounded-lg pr-10" placeholder="Contraseña" />
                        <span onClick={() => setPasswordVisible(!passwordVisible)} className="absolute top-1/2 right-3 -translate-y-1/2 cursor-pointer text-gray-400 hover:text-gray-600">
                            {passwordVisible ? <EyeOff size={20} /> : <Eye size={20} />}
                        </span>
                    </div>
                    <Button type="submit" className="w-full text-lg" variant="primary">Ingresar</Button>
                </form>
            </Card>
        </div>
    );
};

const PuntoDeVenta = ({ productos, metodosDePago, onVentaCompleta }) => {
    const [carrito, setCarrito] = useState([]);
    const [terminoBusqueda, setTerminoBusqueda] = useState('');
    const [modalPagoAbierto, setModalPagoAbierto] = useState(false);
    const [efectivoRecibido, setEfectivoRecibido] = useState(0);
    const [idMetodoPago, setIdMetodoPago] = useState(metodosDePago[0]?.id || '');

    const productosFiltrados = productos.filter(p => p.name.toLowerCase().includes(terminoBusqueda.toLowerCase()) || p.sku.toLowerCase().includes(terminoBusqueda.toLowerCase()));

    const agregarACarrito = (producto) => {
        if (producto.stock <= 0) { alert(`El producto "${producto.name}" no tiene stock.`); return; }
        setCarrito(carritoActual => {
            const itemExistente = carritoActual.find(item => item.id === producto.id);
            if (itemExistente) {
                if (itemExistente.quantity >= producto.stock) { alert(`No puedes agregar más unidades.`); return carritoActual; }
                return carritoActual.map(item => item.id === producto.id ? { ...item, quantity: item.quantity + 1 } : item);
            }
            return [...carritoActual, { ...producto, quantity: 1, price: parseFloat(producto.sale_price) }];
        });
    };

    const procesarPago = () => {
        if (carrito.length === 0) { alert("El carrito está vacío."); return; }
        if (!idMetodoPago) { alert("Por favor, selecciona un método de pago."); return; }
        onVentaCompleta(carrito, subtotal, idMetodoPago);
        setCarrito([]); setTerminoBusqueda(''); setModalPagoAbierto(false); setEfectivoRecibido(0);
    };

    const subtotal = carrito.reduce((sum, item) => sum + item.price * item.quantity, 0);

    const metodoSeleccionado = metodosDePago.find(pm => pm.id === parseInt(idMetodoPago));
    const porcentajeAjuste = metodoSeleccionado ? parseFloat(metodoSeleccionado.adjustment_percentage) : 0;
    const totalFinal = subtotal * (1 + porcentajeAjuste / 100);
    const vuelto = efectivoRecibido > totalFinal ? efectivoRecibido - totalFinal : 0;

    return (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[calc(100vh-100px)]">
            <div className="lg:col-span-2 bg-gray-50 p-4 rounded-xl"><div className="relative mb-4"><Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={20}/><input type="text" placeholder="Buscar producto por nombre o SKU..." className="w-full pl-10 pr-4 py-2 border rounded-lg" value={terminoBusqueda} onChange={(e) => setTerminoBusqueda(e.target.value)} /></div><div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 overflow-y-auto h-[calc(100%-60px)] pr-2">{productosFiltrados.map(producto => (<div key={producto.id} onClick={() => agregarACarrito(producto)} className={`p-3 rounded-lg shadow-sm border-2 text-center ${producto.stock > 0 ? 'cursor-pointer hover:shadow-lg bg-white' : 'bg-gray-200 opacity-60'}`}><p className="font-bold text-gray-800 truncate">{producto.name}</p><p className="text-sm text-gray-600">${parseFloat(producto.sale_price).toFixed(2)}</p><p className={`text-xs ${producto.stock <= 5 ? 'text-red-500 font-bold' : 'text-gray-500'}`}>Stock: {producto.stock}</p></div>))}</div></div>
            <Card className="flex flex-col"><h2 className="text-2xl font-bold text-gray-800 mb-4 flex items-center gap-2"><ShoppingCart size={24}/> Venta Actual</h2><div className="flex-grow overflow-y-auto border-t border-b py-2">{carrito.length === 0 ? <p className="text-gray-500 text-center py-10">El carrito está vacío</p> : carrito.map(item => <div key={item.id} className="flex justify-between items-center mb-2 p-2 rounded-md hover:bg-gray-100"><div><p className="font-semibold">{item.name}</p><p className="text-sm text-gray-600">${item.price.toFixed(2)} x {item.quantity}</p></div><div className="flex items-center gap-2"><p className="font-bold">${(item.price * item.quantity).toFixed(2)}</p><button onClick={() => setCarrito(carrito.filter(c => c.id !== item.id))} className="text-red-500 hover:text-red-700"><Trash2 size={18} /></button></div></div>)}</div><div className="mt-auto pt-4"><div className="flex justify-between text-2xl font-bold mb-4"><span>SUBTOTAL:</span><span>${subtotal.toFixed(2)}</span></div><Button onClick={() => setModalPagoAbierto(true)} className="w-full text-lg" variant="success" icon={CheckCircle}>Cobrar</Button></div></Card>
            <Modal isOpen={modalPagoAbierto} onClose={() => setModalPagoAbierto(false)} title="Procesar Pago">
                <div className="space-y-4">
                    <div className="text-right text-lg">Subtotal: ${subtotal.toFixed(2)}</div>
                    <div><label className="font-semibold text-gray-700">Método de Pago</label><select value={idMetodoPago} onChange={(e) => setIdMetodoPago(e.target.value)} className="w-full mt-1 p-2 border rounded-lg">{metodosDePago.map(pm => <option key={pm.id} value={pm.id}>{pm.name} ({pm.adjustment_percentage}%)</option>)}</select></div>
                    {porcentajeAjuste !== 0 && (<div className={`text-right text-lg ${porcentajeAjuste < 0 ? 'text-green-600' : 'text-red-600'}`}>Ajuste: {porcentajeAjuste > 0 ? '+' : ''}{porcentajeAjuste.toFixed(2)}%</div>)}
                    <div className="text-center text-4xl font-bold text-indigo-600 mb-4">${totalFinal.toFixed(2)}</div>
                    {metodoSeleccionado?.name === 'Efectivo' && (<div><label className="font-semibold text-gray-700">Efectivo Recibido</label><input type="number" value={efectivoRecibido} onChange={(e) => setEfectivoRecibido(parseFloat(e.target.value) || 0)} className="w-full mt-1 p-2 border rounded-lg" placeholder="0.00" />{efectivoRecibido > totalFinal && <p className="text-lg font-semibold mt-2 text-green-600">Vuelto: ${vuelto.toFixed(2)}</p>}</div>)}
                    <div className="flex justify-end gap-3 pt-4"><Button onClick={() => setModalPagoAbierto(false)} variant="secondary">Cancelar</Button><Button onClick={procesarPago} variant="success">Finalizar Venta</Button></div>
                </div>
            </Modal>
        </div>
    );
};
const GestionProductos = ({ productos, proveedores, categorias, obtenerDatos }) => {
    const [modalAbierto, setModalAbierto] = useState(false);
    const [productoEditando, setProductoEditando] = useState(null);
    const { tokensAuth } = useContext(ContextoAuth);
    const abrirModalNuevo = () => { setProductoEditando({ name: '', sku: '', description: '', cost_price: '', sale_price: '', stock: '', category: '', provider: null }); setModalAbierto(true); };
    const abrirModalEditar = (producto) => { setProductoEditando(producto); setModalAbierto(true); };
    const guardarProducto = async (datosProducto) => {
        const esEditando = !!datosProducto.id;
        const url = esEditando ? `${API_URL}/products/${datosProducto.id}/` : `${API_URL}/products/`;
        const method = esEditando ? 'PUT' : 'POST';
        if (datosProducto.provider === '') datosProducto.provider = null;
        try {
            const response = await fetch(url, { method: method, headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) }, body: JSON.stringify(datosProducto) });
            if (!response.ok) { const errorData = await response.json(); throw new Error(JSON.stringify(errorData)); }
            alert(`Producto ${esEditando ? 'actualizado' : 'creado'}.`); setModalAbierto(false); obtenerDatos();
        } catch (err) { alert(`Error: ${err.message}`); }
    };
    const borrarProducto = async (idProducto) => {
        if (window.confirm("¿Estás seguro de que quieres eliminar este producto?")) {
            try {
                await fetch(`${API_URL}/products/${idProducto}/`, { method: 'DELETE', headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) }});
                alert('Producto eliminado.'); obtenerDatos();
            } catch (err) { alert(`Error: ${err.message}`); }
        }
    };
    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center"><h1 className="text-3xl font-bold text-gray-800">Gestión de Productos</h1><Button onClick={abrirModalNuevo} icon={PlusCircle}>Nuevo Producto</Button></div>
            <Table headers={['SKU', 'Nombre', 'Precio Venta', 'Stock', 'Categoría', 'Acciones']} data={productos} renderRow={(p) => (<tr key={p.id} className="bg-white border-b hover:bg-gray-50"><td className="px-6 py-4">{p.sku}</td><td className="px-6 py-4">{p.name}</td><td className="px-6 py-4">${parseFloat(p.sale_price).toFixed(2)}</td><td className="px-6 py-4"><span className={`px-2 py-1 rounded-full text-xs ${p.stock <= 5 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>{p.stock}</span></td><td className="px-6 py-4">{p.category_name}</td><td className="px-6 py-4 flex gap-2"><button onClick={() => abrirModalEditar(p)} className="text-indigo-600"><Edit size={20}/></button><button onClick={() => borrarProducto(p.id)} className="text-red-600"><Trash2 size={20}/></button></td></tr>)}/>
            <Modal isOpen={modalAbierto} onClose={() => setModalAbierto(false)} title={productoEditando?.id ? 'Editar Producto' : 'Nuevo Producto'}>{productoEditando && <FormularioProducto producto={productoEditando} onSave={guardarProducto} onCancel={() => setModalAbierto(false)} categories={categorias} providers={proveedores}/>}</Modal>
        </div>
    );
};
const FormularioProducto = ({ producto, onSave, onCancel, categories, providers }) => {
    const [formData, setFormData] = useState(producto);
    const handleChange = (e) => { const { name, value } = e.target; setFormData(prev => ({ ...prev, [name]: value })); };
    return (
        <form onSubmit={(e) => { e.preventDefault(); onSave(formData); }} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4"><input name="name" value={formData.name} onChange={handleChange} placeholder="Nombre del producto" className="p-2 border rounded-lg w-full" required/><input name="sku" value={formData.sku} onChange={handleChange} placeholder="SKU" className="p-2 border rounded-lg w-full" required/></div><textarea name="description" value={formData.description || ''} onChange={handleChange} placeholder="Descripción" className="p-2 border rounded-lg w-full min-h-[80px]"/><div className="grid grid-cols-1 md:grid-cols-3 gap-4"><input name="cost_price" type="number" step="0.01" value={formData.cost_price} onChange={handleChange} placeholder="Costo" className="p-2 border rounded-lg w-full" required/><input name="sale_price" type="number" step="0.01" value={formData.sale_price} onChange={handleChange} placeholder="Venta" className="p-2 border rounded-lg w-full" required/><input name="stock" type="number" value={formData.stock} onChange={handleChange} placeholder="Stock" className="p-2 border rounded-lg w-full" required/></div><div className="grid grid-cols-1 md:grid-cols-2 gap-4"><select name="category" value={formData.category} onChange={handleChange} className="p-2 border rounded-lg w-full" required><option value="">Categoría</option>{categories.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}</select><select name="provider" value={formData.provider || ''} onChange={handleChange} className="p-2 border rounded-lg w-full"><option value="">Proveedor</option>{providers.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}</select></div><div className="flex justify-end gap-3 pt-4"><Button type="button" onClick={onCancel} variant="secondary">Cancelar</Button><Button type="submit" variant="primary">Guardar</Button></div>
        </form>
    )
};
const DashboardAdmin = ({ productos, ventas }) => {
    const ventasTotales = ventas.reduce((sum, sale) => sum + parseFloat(sale.final_amount), 0);
    const datosGrafico = ventas.reduce((acc, sale) => { const date = new Date(sale.date_time).toLocaleDateString('es-AR', { weekday: 'short' }); acc[date] = (acc[date] || 0) + parseFloat(sale.final_amount); return acc; }, {});
    const datosFormateados = Object.keys(datosGrafico).map(day => ({ name: day, Ventas: datosGrafico[day] }));
    return <div className="space-y-6"><h1 className="text-3xl font-bold text-gray-800">Dashboard</h1><div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6"><StatCard title="Ventas Totales" value={`$${ventasTotales.toFixed(2)}`} icon={DollarSign} color="bg-green-500" /><StatCard title="Transacciones" value={ventas.length} icon={ShoppingCart} color="bg-blue-500" /><StatCard title="Productos Activos" value={productos.length} icon={Package} color="bg-purple-500" /></div><Card><h2 className="text-xl font-bold text-gray-800 mb-4">Ventas de la Semana</h2><ResponsiveContainer width="100%" height={300}><BarChart data={datosFormateados}><CartesianGrid strokeDasharray="3 3" /><XAxis dataKey="name" /><YAxis /><Tooltip formatter={(value) => `$${value.toFixed(2)}`} /><Legend /><Bar dataKey="Ventas" fill="#4f46e5" /></BarChart></ResponsiveContainer></Card></div>;
};
const GestionVentas = ({ sales, fetchData }) => {
    const [viewingSale, setViewingSale] = useState(null);
    const { tokensAuth } = useContext(ContextoAuth);

    const handleCancelSale = async (saleId) => {
        if (window.confirm("¿Estás seguro de que quieres cancelar esta venta? El stock de los productos será restaurado.")) {
            try {
                const response = await fetch(`${API_URL}/sales/${saleId}/`, {
                    method: 'DELETE',
                    headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) }
                });
                if (!response.ok) {
                    throw new Error("No se pudo cancelar la venta.");
                }
                alert('Venta cancelada con éxito.');
                fetchData();
            } catch (err) {
                alert(`Error: ${err.message}`);
            }
        }
    };

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Historial de Ventas</h1>
            <Table
                headers={['ID', 'Fecha', 'Usuario', 'Items', 'Subtotal', 'Total Final', 'Acciones']}
                data={sales}
                renderRow={(s) => (
                    <tr key={s.id} className="bg-white border-b hover:bg-gray-50">
                        <td className="px-6 py-4">#{s.id}</td>
                        <td className="px-6 py-4">{new Date(s.date_time).toLocaleString('es-AR')}</td>
                        <td className="px-6 py-4">{s.user}</td>
                        <td className="px-6 py-4">{s.details.length}</td>
                        <td className="px-6 py-4">${parseFloat(s.total_amount).toFixed(2)}</td>
                        <td className="px-6 py-4 font-bold">${parseFloat(s.final_amount).toFixed(2)}</td>
                        <td className="px-6 py-4 flex gap-2">
                            <Button onClick={() => setViewingSale(s)} variant="secondary" icon={Eye} />
                            <Button onClick={() => handleCancelSale(s.id)} variant="danger" icon={Trash2} />
                        </td>
                    </tr>
                )}
            />
            {viewingSale && (
