const GestionUsuarios = ({ usuarios, grupos, obtenerDatos }) => {
    const [modalAbierto, setModalAbierto] = useState(false);
    const { tokensAuth, usuario: usuarioActual } = useContext(ContextoAuth);

    const guardarUsuario = async (datosUsuario) => {
        try {
            const response = await fetch(`${API_URL}/users/`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) },
                body: JSON.stringify(datosUsuario),
            });
            if (!response.ok) { const errorData = await response.json(); throw new Error(JSON.stringify(errorData)); }
            alert('Usuario creado con éxito.'); setModalAbierto(false); obtenerDatos();
        } catch (err) { alert(`Error al crear usuario: ${err.message}`); }
    };

    const borrarUsuario = async (idUsuario) => {
        if (idUsuario === usuarioActual.user_id) { alert("No puedes eliminar tu propia cuenta."); return; }
        if (window.confirm("¿Estás seguro de que quieres eliminar este usuario?")) {
            try {
                await fetch(`${API_URL}/users/${idUsuario}/`, { method: 'DELETE', headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) }});
                alert('Usuario eliminado con éxito.'); obtenerDatos();
            } catch (err) { alert(`Error al eliminar: ${err.message}`); }
        }
    };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center"><h1 className="text-3xl font-bold text-gray-800">Gestión de Usuarios</h1><Button onClick={() => setModalAbierto(true)} icon={UserPlus}>Nuevo Usuario</Button></div>
            <Table headers={['ID', 'Username', 'Grupos', 'Acciones']} data={usuarios} renderRow={(user) => (<tr key={user.id} className="bg-white border-b hover:bg-gray-50"><td className="px-6 py-4">{user.id}</td><td className="px-6 py-4">{user.username}</td><td className="px-6 py-4">{user.groups.join(', ')}</td><td className="px-6 py-4"><Button onClick={() => borrarUsuario(user.id)} variant="danger" disabled={user.id === usuarioActual.user_id} icon={Trash2}/></td></tr>)}/>
            <Modal isOpen={modalAbierto} onClose={() => setModalAbierto(false)} title="Crear Nuevo Usuario"><UserForm onSave={guardarUsuario} onCancel={() => setModalAbierto(false)} groups={grupos} /></Modal>
