const CierreCaja = () => {
    const [data, setData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [countedAmount, setCountedAmount] = useState('');
    const [error, setError] = useState('');
    const { tokensAuth } = useContext(ContextoAuth);

    const fetchData = useCallback(async () => {
        setLoading(true);
        setError('');
        try {
            const response = await fetch(`${API_URL}/cash-count/`, { headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) } });
            const responseData = await response.json();

            if (!response.ok) {
                 if (response.status === 409) {
                     setError(responseData.message);
                     setData({ history: responseData.history || [] }); 
                 } else {
                    throw new Error(responseData.detail || 'No se pudieron cargar los datos del cierre de caja.');
                 }
            } else {
                setData(responseData);
            }

        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    }, [tokensAuth]);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleSaveClose = async () => {
        if(countedAmount === '' || isNaN(parseFloat(countedAmount))) {
            alert("Por favor, ingresa un monto contado válido.");
            return;
        }
        try {
            const response = await fetch(`${API_URL}/cash-count/`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) },
                body: JSON.stringify({
                    counted_amount: parseFloat(countedAmount),
                    expected_amount: data.expected_amount
                })
            });
            if (!response.ok) { throw new Error('No se pudo guardar el cierre.'); }
            alert('¡Cierre de caja guardado con éxito!');
            fetchData();
        } catch (err) {
            alert(`Error: ${err.message}`);
        }
    };

    const difference = data?.expected_amount != null && countedAmount !== '' ? parseFloat(countedAmount) - data.expected_amount : null;

    if (loading) return <div className="p-6">Cargando...</div>;

    return (
        <div className="space-y-8">
            <h1 className="text-3xl font-bold text-gray-800">Cierre de Caja Diario</h1>

            <Card>
                <h2 className="text-xl font-bold text-gray-700 mb-4">Cierre del Día ({new Date().toLocaleDateString('es-AR')})</h2>
                {error && !data?.expected_amount ? (
                    <div className="text-center p-8 bg-yellow-50 rounded-lg">
                        <p className="text-lg font-semibold text-yellow-700">{error}</p>
                    </div>
                ) : (
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 items-center">
                        <div className="text-center p-4 bg-blue-50 rounded-lg">
                            <h3 className="text-sm font-semibold text-blue-800 uppercase">Monto Esperado (Sistema)</h3>
                            <p className="text-4xl font-bold text-blue-900">${parseFloat(data?.expected_amount || 0).toFixed(2)}</p>
                        </div>
                        <div className="p-4">
                             <label className="block text-sm font-medium text-gray-700 mb-1">Monto Contado en Caja</label>
                             <input 
                                type="number"
                                value={countedAmount}
                                onChange={(e) => setCountedAmount(e.target.value)}
                                placeholder="0.00"
                                className="w-full p-3 text-2xl text-center border-2 rounded-lg focus:ring-indigo-500 focus:border-indigo-500"
                             />
                        </div>
                         <div className={`text-center p-4 rounded-lg ${difference === null ? 'bg-gray-100' : difference === 0 ? 'bg-green-100' : 'bg-red-100'}`}>
                            <h3 className={`text-sm font-semibold uppercase ${difference === null ? 'text-gray-800' : difference === 0 ? 'text-green-800' : 'text-red-800'}`}>Diferencia</h3>
                            <p className={`text-4xl font-bold ${difference === null ? 'text-gray-900' : difference === 0 ? 'text-green-900' : 'text-red-900'}`}>{difference !== null ? `$${difference.toFixed(2)}` : '-'}</p>
                        </div>
                        <div className="md:col-span-3 text-center">
                            <Button onClick={handleSaveClose} disabled={difference === null || !!error} variant="primary" icon={Archive}>Guardar Cierre de Caja</Button>
                        </div>
                    </div>
                )}
            </Card>

            <div>
                 <h2 className="text-xl font-bold text-gray-700 mb-4">Historial de Cierres</h2>
                 <Table
                    headers={['Fecha', 'Monto Esperado', 'Monto Contado', 'Diferencia', 'Usuario']}
                    data={data?.history || []}
                    renderRow={(c) => (
                        <tr key={c.id} className="bg-white border-b hover:bg-gray-50">
                            <td className="px-6 py-4">{new Date(c.date + 'T00:00:00').toLocaleDateString('es-AR')}</td>
                            <td className="px-6 py-4">${parseFloat(c.expected_amount).toFixed(2)}</td>
                            <td className="px-6 py-4">${parseFloat(c.counted_amount).toFixed(2)}</td>
                            <td className={`px-6 py-4 font-bold ${c.difference > 0 ? 'text-green-600' : c.difference < 0 ? 'text-red-600' : 'text-gray-800'}`}>
                                {c.difference > 0 ? `+${parseFloat(c.difference).toFixed(2)}` : parseFloat(c.difference).toFixed(2)}
                            </td>
                            <td className="px-6 py-4">{c.user}</td>
                        </tr>
                    )}
                 />
            </div>
        </div>
    );
};
const ActualizacionMasivaPrecios = ({ products, providers, fetchData }) => {
    const [selectedProvider, setSelectedProvider] = useState('');
    const [selectedProducts, setSelectedProducts] = useState(new Set());
    const [percentage, setPercentage] = useState('');
    const [updateTarget, setUpdateTarget] = useState('both');
    const { tokensAuth } = useContext(ContextoAuth);

    const handleSelectAll = (filteredProducts) => {
        if (selectedProducts.size === filteredProducts.length) {
            setSelectedProducts(new Set());
        } else {
            setSelectedProducts(new Set(filteredProducts.map(p => p.id)));
        }
    };

    const handleSelectProduct = (productId) => {
        const newSelection = new Set(selectedProducts);
        if (newSelection.has(productId)) {
            newSelection.delete(productId);
        } else {
            newSelection.add(productId);
        }
        setSelectedProducts(newSelection);
    };

    const handleUpdate = async () => {
        if(selectedProducts.size === 0 || !percentage || !updateTarget) {
            alert("Por favor, selecciona productos, un porcentaje y un objetivo de actualización.");
            return;
        }

        const payload = {
            product_ids: Array.from(selectedProducts),
            percentage: percentage,
            update_target: updateTarget
        };

        if(window.confirm(`¿Seguro que quieres actualizar ${selectedProducts.size} productos con un ${percentage}% de aumento?`)) {
            try {
                const response = await fetch(`${API_URL}/bulk-price-update/`, {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) },
                    body: JSON.stringify(payload)
                });
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(JSON.stringify(errorData));
                }
                alert('Precios actualizados con éxito.');
                fetchData();
                setSelectedProducts(new Set());
                setPercentage('');
            } catch (err) {
                alert(`Error al actualizar precios: ${err.message}`);
            }
        }
    };

    const filteredProducts = selectedProvider ? products.filter(p => p.provider === parseInt(selectedProvider)) : [];

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Actualización Masiva de Precios</h1>
            <Card>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 items-end">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Filtrar por Proveedor</label>
                        <select value={selectedProvider} onChange={(e) => setSelectedProvider(e.target.value)} className="p-2 border rounded-lg w-full mt-1">
                            <option value="">Seleccionar Proveedor</option>
                            {providers.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
                        </select>
                    </div>
                    <div>
                         <label className="block text-sm font-medium text-gray-700">Porcentaje de Aumento (%)</label>
                         <input type="number" value={percentage} onChange={(e) => setPercentage(e.target.value)} placeholder="Ej: 15" className="p-2 border rounded-lg w-full mt-1"/>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Actualizar Precio de:</label>
                        <div className="flex gap-4 mt-2">
                           <label className="flex items-center gap-2"><input type="radio" name="target" value="cost" checked={updateTarget === 'cost'} onChange={e => setUpdateTarget(e.target.value)}/> Costo</label>
                           <label className="flex items-center gap-2"><input type="radio" name="target" value="sale" checked={updateTarget === 'sale'} onChange={e => setUpdateTarget(e.target.value)}/> Venta</label>
                           <label className="flex items-center gap-2"><input type="radio" name="target" value="both" checked={updateTarget === 'both'} onChange={e => setUpdateTarget(e.target.value)}/> Ambos</label>
                        </div>
                    </div>
                    <Button onClick={handleUpdate} disabled={selectedProducts.size === 0 || !percentage}>Actualizar {selectedProducts.size} Productos</Button>
                </div>
            </Card>
            <Table 
                headers={[
                    <input type="checkbox" onChange={() => handleSelectAll(filteredProducts)} checked={selectedProducts.size > 0 && filteredProducts.length > 0 && selectedProducts.size === filteredProducts.length}/>, 
                    'SKU', 'Nombre', 'Costo Actual', 'Venta Actual'
                ]} 
                data={filteredProducts} 
                renderRow={(p) => (
                    <tr key={p.id} className="bg-white border-b hover:bg-gray-50">
                        <td className="px-6 py-4"><input type="checkbox" checked={selectedProducts.has(p.id)} onChange={() => handleSelectProduct(p.id)}/></td>
                        <td className="px-6 py-4">{p.sku}</td>
                        <td className="px-6 py-4">{p.name}</td>
                        <td className="px-6 py-4">${parseFloat(p.cost_price).toFixed(2)}</td>
                        <td className="px-6 py-4">${parseFloat(p.sale_price).toFixed(2)}</td>
                    </tr>
                )}
            />
        </div>
    );
};
const GestionMetodosDePago = ({ paymentMethods, fetchData }) => {
    const [isModalOpen, setModalOpen] = useState(false);
    const [editingMethod, setEditingMethod] = useState(null);
    const { tokensAuth } = useContext(ContextoAuth);

    const openModalForNew = () => {
        setEditingMethod({ name: '', adjustment_percentage: '0.00', is_active: true });
        setModalOpen(true);
    };

    const openModalForEdit = (method) => {
        setEditingMethod(method);
        setModalOpen(true);
    };

    const handleSave = async (methodData) => {
        const isEditing = !!methodData.id;
        const url = isEditing ? `${API_URL}/admin/payment-methods/${methodData.id}/` : `${API_URL}/admin/payment-methods/`;
        const method = isEditing ? 'PUT' : 'POST';

        try {
            const response = await fetch(url, {
                method: method,
                headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) },
                body: JSON.stringify(methodData)
            });
            if (!response.ok) { const errorData = await response.json(); throw new Error(JSON.stringify(errorData)); }
            alert(`Método de pago ${isEditing ? 'actualizado' : 'creado'} con éxito.`);
            setModalOpen(false);
            fetchData();
        } catch (err) {
            alert(`Error: ${err.message}`);
        }
    };

    const handleDelete = async (methodId) => {
        if (window.confirm("¿Estás seguro de que quieres eliminar este método de pago?")) {
            try {
                const response = await fetch(`${API_URL}/admin/payment-methods/${methodId}/`, {
                    method: 'DELETE',
                    headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) }
                });
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.detail || "No se pudo eliminar el método de pago.");
                }
                alert('Método de pago eliminado con éxito.');
                fetchData();
            } catch (err) {
                alert(`Error al eliminar: ${err.message}`);
            }
        }
    };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center">
                <h1 className="text-3xl font-bold text-gray-800">Gestión de Métodos de Pago</h1>
                <Button onClick={openModalForNew} icon={PlusCircle}>Nuevo Método</Button>
            </div>
            <Table
                headers={['Nombre', 'Ajuste (%)', 'Estado', 'Acciones']}
                data={paymentMethods}
                renderRow={(pm) => (
                    <tr key={pm.id} className="bg-white border-b hover:bg-gray-50">
                        <td className="px-6 py-4">{pm.name}</td>
                        <td className={`px-6 py-4 font-semibold ${pm.adjustment_percentage < 0 ? 'text-green-600' : pm.adjustment_percentage > 0 ? 'text-red-600' : 'text-gray-700'}`}>
                            {pm.adjustment_percentage}%
                        </td>
                        <td className="px-6 py-4">
                           <span className={`px-2 py-1 rounded-full text-xs font-semibold ${pm.is_active ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800'}`}>
                             {pm.is_active ? 'Activo' : 'Inactivo'}
                           </span>
                        </td>
                        <td className="px-6 py-4 flex gap-2">
                            <Button onClick={() => openModalForEdit(pm)} variant="secondary" icon={Edit} />
                            <Button onClick={() => handleDelete(pm.id)} variant="danger" icon={Trash2} />
                        </td>
                    </tr>
                )}
            />
            <Modal isOpen={isModalOpen} onClose={() => setModalOpen(false)} title={editingMethod?.id ? 'Editar Método de Pago' : 'Nuevo Método de Pago'}>
                {editingMethod && <PaymentMethodForm method={editingMethod} onSave={handleSave} onCancel={() => setModalOpen(false)} />}
            </Modal>
        </div>
    );
};
const PaymentMethodForm = ({ method, onSave, onCancel }) => {
    const [formData, setFormData] = useState(method);
    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
    };

    return (
        <form onSubmit={(e) => { e.preventDefault(); onSave(formData); }} className="space-y-4">
            <input name="name" value={formData.name} onChange={handleChange} placeholder="Nombre (Ej: Efectivo, Tarjeta Naranja)" className="p-2 border rounded-lg w-full" required />
            <input name="adjustment_percentage" type="number" step="0.01" value={formData.adjustment_percentage} onChange={handleChange} placeholder="Porcentaje de ajuste" className="p-2 border rounded-lg w-full" required />
            <p className="text-xs text-gray-500">Usa números negativos para descuentos (ej: -10) y positivos para recargos (ej: 8.5).</p>
            <label className="flex items-center gap-2">
                <input type="checkbox" name="is_active" checked={formData.is_active} onChange={handleChange} />
                Activo
            </label>
            <div className="flex justify-end gap-3 pt-4">
                <Button type="button" onClick={onCancel} variant="secondary">Cancelar</Button>
                <Button type="submit" variant="primary">Guardar</Button>
            </div>
        </form>
    );
};

// --- AuthProvider ---
const AuthProvider = ({ children }) => {
    const [tokensAuth, setTokensAuth] = useState(() => localStorage.getItem('tokensAuth') ? JSON.parse(localStorage.getItem('tokensAuth')) : null);
    const [usuario, setUsuario] = useState(() => {
        const token = localStorage.getItem('tokensAuth');
        if (!token) return null;
        try {
            return decodificarToken(JSON.parse(token).access);
        } catch (e) {
            return null;
        }
    });
    const [cargando, setCargando] = useState(true);
    const iniciarSesion = async (username, password) => {
        const response = await fetch(`${API_URL}/token/`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ username, password }) });
        const data = await response.json();
        if (response.ok) { 
            const usuarioDecodificado = decodificarToken(data.access);
            setTokensAuth(data); 
            setUsuario(usuarioDecodificado); 
            localStorage.setItem('tokensAuth', JSON.stringify(data)); 
        } else { alert('Usuario o contraseña incorrectos.'); }
    };
    const cerrarSesion = useCallback(() => { setTokensAuth(null); setUsuario(null); localStorage.removeItem('tokensAuth'); }, []);
    const actualizarToken = useCallback(async () => {
        const tokensActuales = JSON.parse(localStorage.getItem('tokensAuth'));
        if (!tokensActuales?.refresh) { cerrarSesion(); return; }
        try {
            const response = await fetch(`${API_URL}/token/refresh/`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ 'refresh': tokensActuales.refresh }) });
            const data = await response.json();
            if (response.ok) {
                const nuevosTokens = { ...tokensActuales, access: data.access };
                setTokensAuth(nuevosTokens); 
                setUsuario(decodificarToken(data.access));
                localStorage.setItem('tokensAuth', JSON.stringify(nuevosTokens));
            } else { cerrarSesion(); }
        } catch { cerrarSesion(); }
    }, [cerrarSesion]);
    useEffect(() => { const interval = setInterval(() => { if (tokensAuth) actualizarToken(); }, 1000 * 60 * 4); return () => clearInterval(interval); }, [tokensAuth, actualizarToken]);
    useEffect(() => { setCargando(false); }, []);
    return (<ContextoAuth.Provider value={{ usuario, tokensAuth, iniciarSesion, cerrarSesion }}>{cargando ? <div className="min-h-screen flex items-center justify-center">Cargando...</div> : children}</ContextoAuth.Provider>);
};

// --- Componente Principal ---
export default function AppWrapper() { return (<AuthProvider><App /></AuthProvider>); }

function App() {
    const { usuario, tokensAuth, cerrarSesion } = useContext(ContextoAuth);
    const [vista, setVista] = useState('pos');
    const [vistaAdmin, setVistaAdmin] = useState('dashboard');
    const [productos, setProductos] = useState([]);
    const [ventas, setVentas] = useState([]);
    const [proveedores, setProveedores] = useState([]);
    const [clientes, setClientes] = useState([]);
    const [categorias, setCategorias] = useState([]);
    const [todosLosUsuarios, setTodosLosUsuarios] = useState([]);
    const [grupos, setGrupos] = useState([]);
    const [cargando, setCargando] = useState(true);
    const [error, setError] = useState(null);
    const [metodosDePago, setMetodosDePago] = useState([]);
    const [metodosDePagoAdmin, setMetodosDePagoAdmin] = useState([]);

    const esAdmin = usuario?.groups?.includes('Administradores');

    const obtenerDatos = useCallback(async () => {
        if (!tokensAuth) return;
        setCargando(true);
        let endpoints = ['products', 'sales', 'payment-methods'];
        if (esAdmin) {
            endpoints.push('providers', 'clients', 'categories', 'users', 'groups', 'admin/payment-methods');
        }
        try {
            const requests = endpoints.map(endpoint => 
                fetch(`${API_URL}/${endpoint}/`, { headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) } }).then(res => {
                    if(res.status === 401) { cerrarSesion(); return Promise.reject(new Error("Token inválido")); }
                    if (!res.ok) return Promise.resolve([]);
                    return res.json()
                })
            );
            const data = await Promise.all(requests);
            const dataMap = Object.fromEntries(endpoints.map((e, i) => [e, data[i]]));

            setProductos(dataMap.products || []); 
            setVentas(dataMap.sales || []);
            setMetodosDePago(dataMap['payment-methods'] || []);
            if(esAdmin) {
                setProveedores(dataMap.providers || []); 
                setClientes(dataMap.clients || []); 
                setCategorias(dataMap.categories || []); 
                setTodosLosUsuarios(dataMap.users || []);
                setGrupos(dataMap.groups || []);
                setMetodosDePagoAdmin(dataMap['admin/payment-methods'] || []);
            }
            setError(null);
        } catch (err) {
            if (err.message !== "Token inválido") { setError("No se pudo conectar con el servidor."); }
        } finally { setCargando(false); }
    }, [tokensAuth, cerrarSesion, esAdmin]); 

    useEffect(() => { obtenerDatos(); }, [obtenerDatos]);
    useEffect(() => { if (usuario && !esAdmin) setVista('pos'); }, [usuario, esAdmin]);

    const handleVentaCompleta = async (carrito, total, idMetodoPago) => {
        const datosVenta = { total_amount: total.toFixed(2), details: carrito.map(item => ({ product_id: item.id, quantity: item.quantity, unit_price: item.price.toFixed(2) })), payment_method_id: idMetodoPago };
        try {
            const response = await fetch(`${API_URL}/sales/`, { method: 'POST', headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) }, body: JSON.stringify(datosVenta) });
            if (!response.ok) { const errorData = await response.json(); throw new Error(JSON.stringify(errorData)); }
            alert('Venta registrada.'); obtenerDatos();
        } catch (err) { alert(`Error: ${err.message}`); }
    };

    const renderizarVistaAdmin = () => {
        switch (vistaAdmin) {
            case 'dashboard': return <DashboardAdmin products={productos} sales={ventas} />;
            case 'products': return <GestionProductos products={productos} providers={proveedores} categories={categorias} fetchData={obtenerDatos} />;
            case 'sales': return <GestionVentas sales={ventas} fetchData={obtenerDatos}/>;
            case 'providers': return <GestionProveedores providers={proveedores} fetchData={obtenerDatos} />;
            case 'clients': return <GestionClientes clients={clientes} fetchData={obtenerDatos} />;
            case 'reports': return <ReportesYEstadisticas />;
            case 'users': return <GestionUsuarios usuarios={todosLosUsuarios} grupos={grupos} obtenerDatos={obtenerDatos} />;
            case 'cash-count': return <CierreCaja />;
            case 'bulk-price-update': return <ActualizacionMasivaPrecios products={productos} providers={proveedores} fetchData={obtenerDatos} />;
            case 'payment-methods': return <GestionMetodosDePago paymentMethods={metodosDePagoAdmin} fetchData={obtenerDatos} />;
            default: return <DashboardAdmin products={productos} sales={ventas}/>;
        }
    };

    const NavItem = ({ icon: Icon, children, onClick, active }) => (<li onClick={onClick} className={`flex items-center gap-3 px-4 py-3 cursor-pointer rounded-lg ${active ? 'bg-indigo-600 text-white' : 'hover:bg-gray-200'}`}><Icon size={22} /><span className="font-medium">{children}</span></li>);

    if (!usuario) return <PaginaLogin />;
    if (cargando) return <div className="min-h-screen flex items-center justify-center">Cargando datos...</div>
    if (error) return <div className="min-h-screen flex items-center justify-center text-red-500">{error}</div>

    return (
        <div className="flex h-screen bg-gray-100 font-sans">
            {esAdmin && vista === 'admin' && (
                <aside className="w-64 bg-white shadow-xl flex flex-col p-4">
                    <h1 className="text-2xl font-bold text-indigo-600 px-4 mb-6">MiNegocio<span className="font-light">PRO</span></h1>
                    <nav className="flex-grow"><ul className="space-y-2">
                        <NavItem icon={BarChart2} active={vistaAdmin === 'dashboard'} onClick={() => setVistaAdmin('dashboard')}>Dashboard</NavItem>
                        <NavItem icon={Package} active={vistaAdmin === 'products'} onClick={() => setVistaAdmin('products')}>Productos</NavItem>
                        <NavItem icon={UploadCloud} active={vistaAdmin === 'bulk-price-update'} onClick={() => setVistaAdmin('bulk-price-update')}>Actualizar Precios</NavItem>
                        <NavItem icon={DollarSign} active={vistaAdmin === 'sales'} onClick={() => setVistaAdmin('sales')}>Ventas</NavItem>
                        <NavItem icon={Archive} active={vistaAdmin === 'cash-count'} onClick={() => setVistaAdmin('cash-count')}>Cierre de Caja</NavItem>
                        <NavItem icon={CreditCard} active={vistaAdmin === 'payment-methods'} onClick={() => setVistaAdmin('payment-methods')}>Métodos de Pago</NavItem>
                        <NavItem icon={Truck} active={vistaAdmin === 'providers'} onClick={() => setVistaAdmin('providers')}>Proveedores</NavItem>
                        <NavItem icon={Users} active={vistaAdmin === 'clients'} onClick={() => setVistaAdmin('clients')}>Clientes</NavItem>
                        <NavItem icon={BarChart} active={vistaAdmin === 'reports'} onClick={() => setVistaAdmin('reports')}>Reportes</NavItem>
                        <NavItem icon={UserPlus} active={vistaAdmin === 'users'} onClick={() => setVistaAdmin('users')}>Usuarios</NavItem>
                    </ul></nav>
                    <div className="mt-auto"><NavItem icon={ShoppingCart} onClick={() => setVista('pos')}>Ir al Punto de Venta</NavItem></div>
                </aside>
            )}
            <main className="flex-1 flex flex-col">
                <header className="bg-white shadow-sm p-4 flex justify-between items-center z-10">
                    <div>{esAdmin && vista === 'pos' && <Button onClick={() => setVista('admin')} variant="secondary">Volver al Panel</Button>}</div>
                    <div className="flex items-center gap-3"><span className="font-semibold text-gray-700">{usuario.username}</span><button onClick={cerrarSesion} className="text-gray-500 hover:text-red-600"><LogOut size={20} /></button></div>
                </header>
                <div className="flex-1 p-6 overflow-y-auto">{vista === 'pos' || !esAdmin ? <PuntoDeVenta productos={productos} metodosDePago={metodosDePago} onVentaCompleta={handleVentaCompleta} /> : renderizarVistaAdmin()}</div>
            </main>
        </div>
    );
}
