import React, { useState, useContext } from 'react';
import { Eye, EyeOff, LoaderCircle } from 'lucide-react';
import { ContextoAuth } from '../../context/AuthContext';
import { Card, Button } from '../../components/ui/ComponentesUI';
import { toast } from 'sonner';

const PaginaLogin = () => {
    const { iniciarSesion } = useContext(ContextoAuth);
    
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [passwordVisible, setPasswordVisible] = useState(false);
    const [cargando, setCargando] = useState(false); // Estado para el indicador de carga

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!username || !password) {
            toast.error("Por favor, completa ambos campos.");
            return;
        }
        
        setCargando(true); // Activamos la carga

        // Usamos toast.promise para manejar la promesa de iniciarSesion
        const promesaLogin = iniciarSesion(username, password);

        toast.promise(promesaLogin, {
            loading: 'Iniciando sesión...',
            success: () => {
                // El éxito lo maneja el cambio de contexto, aquí solo devolvemos el mensaje.
                return '¡Bienvenido!';
            },
            error: (err) => {
                // Mostramos el mensaje de error que viene del contexto.
                return err.message || 'Error en el inicio de sesión.';
            },
            finally: () => {
                setCargando(false); // Desactivamos la carga al finalizar
            }
        });
    };

    return (
        <div className="bg-gray-100 min-h-screen flex items-center justify-center">
            <Card className="text-center w-full max-w-sm">
                <h1 className="text-3xl font-bold mb-2 text-indigo-600">MiNegocio<span className="font-light">PRO</span></h1>
                <p className="text-gray-500 mb-6">Inicia sesión para continuar</p>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input 
                        type="text" 
                        name="username" 
                        required 
                        className="w-full p-3 border rounded-lg" 
                        placeholder="Nombre de usuario"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <div className="relative">
                        <input 
                            type={passwordVisible ? "text" : "password"} 
                            name="password" 
                            required 
                            className="w-full p-3 border rounded-lg pr-10" 
                            placeholder="Contraseña"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <span 
                            onClick={() => setPasswordVisible(!passwordVisible)} 
                            className="absolute top-1/2 right-3 -translate-y-1/2 cursor-pointer text-gray-400 hover:text-gray-600"
                        >
                            {passwordVisible ? <EyeOff size={20} /> : <Eye size={20} />}
                        </span>
                    </div>
                    <Button type="submit" className="w-full text-lg flex items-center justify-center" variant="primary" disabled={cargando}>
                        {cargando ? <LoaderCircle className="animate-spin" /> : 'Ingresar'}
                    </Button>
                </form>
            </Card>
        </div>
    );
};

export default PaginaLogin;
