import React from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { DollarSign, AlertTriangle, Package, Users, Trophy, BarChart2 } from 'lucide-react';
import { StatCard, Card } from '../../components/ui/ComponentesUI';

// Un pequeño componente auxiliar para los items del ranking
const RankingItem = ({ rank, name, value }) => {
    const colors = {
        1: 'bg-amber-400 text-amber-900',
        2: 'bg-gray-300 text-gray-800',
        3: 'bg-yellow-600 text-yellow-900'
    };
    const rankColor = colors[rank] || 'bg-gray-200 text-gray-700';

    return (
        <div className="flex items-center gap-4 py-2">
            <span className={`flex-shrink-0 w-8 h-8 flex items-center justify-center font-bold rounded-full ${rankColor}`}>
                {rank}
            </span>
            <span className="font-medium text-gray-800 truncate" title={name}>{name}</span>
            <span className="ml-auto font-semibold text-gray-600">{value}</span>
        </div>
    );
};

const DashboardAdmin = ({ dashboardData }) => {
    // Si no hay datos, mostramos un estado de carga o vacío.
    if (!dashboardData) {
        return <div>Cargando datos del dashboard...</div>;
    }

    const { kpis, low_stock_products, rankings } = dashboardData;
    const topSoldProducts = rankings?.mas_vendidos || [];
    
    // Asumimos que los datos para el gráfico vienen en esta estructura.
    // AJUSTA ESTO a los datos reales que te envía tu API.
    const chartData = rankings?.ventas_por_categoria || [
        { name: 'Cafetería', total: 4300 },
        { name: 'Lácteos', total: 2100 },
        { name: 'Bebidas', total: 3200 },
        { name: 'Limpieza', total: 1500 },
        { name: 'Snacks', total: 1800 },
    ];

    return (
        <div className="space-y-8">
            {/* SECCIÓN 1: Título y Contexto */}
            <div>
                <h1 className="text-3xl font-bold text-gray-800">Dashboard</h1>
                <p className="text-lg text-gray-500 mt-1">
                    Un resumen general de la actividad y rendimiento de tu negocio al día de hoy, 
                    <span className="font-semibold text-blue-600"> {new Date().toLocaleDateString('es-ES', { day: '2-digit', month: 'long', year: 'numeric' })}</span>.
                </p>
            </div>

            {/* SECCIÓN 2: Indicadores Principales (KPIs) */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <StatCard title="Ventas del Día" value={`$${(kpis.ventas_del_dia || 0).toFixed(2)}`} icon={DollarSign} color="bg-green-500" />
                <StatCard title="Ganancia Bruta del Día" value={`$${(kpis.ganancia_bruta_del_dia || 0).toFixed(2)}`} icon={DollarSign} color="bg-sky-500" />
                <StatCard title="Ticket Promedio" value={`$${(kpis.ticket_promedio || 0).toFixed(2)}`} icon={Users} color="bg-blue-500" />
                <StatCard title="Productos Vendidos Hoy" value={kpis.productos_vendidos || 0} icon={Package} color="bg-purple-500" />
            </div>

            {/* SECCIÓN 3: Gráfico y Rankings */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* NUEVA SECCIÓN: Gráfico de Ventas */}
                <Card className="lg:col-span-2">
                    <div className="flex items-center gap-3 mb-4">
                        <BarChart2 className="text-blue-600" size={24} />
                        <h2 className="text-xl font-bold text-gray-800">Ventas por Categoría (Hoy)</h2>
                    </div>
                    <div style={{ width: '100%', height: 400 }}>
                        <ResponsiveContainer>
                            <BarChart data={chartData} margin={{ top: 5, right: 20, left: -10, bottom: 5 }}>
                                <CartesianGrid strokeDasharray="3 3" vertical={false} />
                                <XAxis dataKey="name" tick={{ fontSize: 12 }} />
                                <YAxis tick={{ fontSize: 12 }} tickFormatter={(value) => `$${value}`} />
                                <Tooltip cursor={{ fill: 'rgba(79, 70, 229, 0.1)' }} formatter={(value) => [`$${value.toFixed(2)}`, "Total Ventas"]}/>
                                <Bar dataKey="total" fill="#4338ca" radius={[4, 4, 0, 0]} />
                            </BarChart>
                        </ResponsiveContainer>
                    </div>
                </Card>

                {/* LISTA MEJORADA VISUALMENTE: Top 5 Productos */}
                <Card>
                    <div className="flex items-center gap-3 mb-4">
                        <Trophy className="text-amber-500" size={24} />
                        <h2 className="text-xl font-bold text-gray-800">Top Productos</h2>
                    </div>
                    {topSoldProducts && topSoldProducts.length > 0 ? (
                        <div className="space-y-2">
                            {topSoldProducts.slice(0, 5).map((product, index) => (
                                <RankingItem 
                                    key={product.product__id}
                                    rank={index + 1}
                                    name={product.product__name}
                                    value={`${product.value} vendidos`}
                                />
                            ))}
                        </div>
                    ) : (
                        <p className="text-sm text-gray-500 pt-4">No hay datos de ventas recientes.</p>
                    )}
                </Card>
            </div>

            {/* SECCIÓN 4: Alertas y Avisos */}
            <Card>
                <div className="flex items-center gap-3 mb-4">
                    <AlertTriangle className="text-yellow-500" size={24} />
                    <h2 className="text-xl font-bold text-gray-800">Productos con Bajo Stock</h2>
                </div>
                {low_stock_products && low_stock_products.length > 0 ? (
                    <ul className="space-y-3">
                        {low_stock_products.map(product => (
                            <li key={product.id} className="flex justify-between items-center text-sm p-2 rounded-md hover:bg-gray-50">
                                <span className="text-gray-800 font-medium">{product.name}</span>
                                <span className="font-bold text-red-600 bg-red-100 px-2 py-1 rounded-full">
                                    Quedan {product.stock}
                                </span>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p className="text-sm text-gray-500">¡Felicidades! No hay productos con bajo stock.</p>
                )}
            </Card>
        </div>
    );
};

export default DashboardAdmin;
