import React, { useState, useEffect } from 'react';
import { DollarSign, AlertTriangle, Package, Users, Activity } from 'lucide-react';

// Asumo que tu componente Card viene de un archivo de UI
// Si no es así, puedes reemplazarlo por un `div` con los estilos correspondientes.
const Card = ({ children, className }) => (
    <div className={`bg-white rounded-lg shadow-md p-6 ${className || ''}`}>
        {children}
    </div>
);


const DashboardAdmin = ({ dashboardData }) => {
    // Estado para guardar los datos de la cotización del dólar
    const [dolarData, setDolarData] = useState(null);
    const [loadingDolar, setLoadingDolar] = useState(true);

    useEffect(() => {
        // --- PASO 1: Fetch de datos del Dólar desde TU BACKEND ---
        // Ahora hacemos dos llamadas a APIs distintas para tener toda la data.
        // Lo ideal sigue siendo que estas llamadas se hagan desde tu backend.
        const fetchDolarData = async () => {
            setLoadingDolar(true);
            try {
                // Usamos Promise.all para ejecutar ambas peticiones en paralelo
                const [bluelyticsRes, dolarApiRes] = await Promise.all([
                    fetch('https://api.bluelytics.com.ar/v2/latest'),
                    fetch('https://dolarapi.com/v1/dolares')
                ]);

                if (!bluelyticsRes.ok || !dolarApiRes.ok) {
                    throw new Error('Error al obtener los datos de cotizaciones');
                }
                
                const bluelyticsData = await bluelyticsRes.json();
                const dolarApiData = await dolarApiRes.json();

                // Buscamos el Dólar MEP en la respuesta de la segunda API
                const dolarMep = dolarApiData.find(d => d.casa === 'bolsa');

                // Consolidamos toda la información en un solo objeto de estado
                setDolarData({
                    blue: bluelyticsData.blue,
                    oficial: bluelyticsData.oficial,
                    mep: dolarMep ? { value_buy: dolarMep.compra, value_sell: dolarMep.venta } : null,
                    lastUpdate: new Date(bluelyticsData.last_update)
                });

            } catch (error) {
                console.error("No se pudo cargar la cotización del dólar:", error);
                setDolarData(null); 
            } finally {
                setLoadingDolar(false);
            }
        };

        fetchDolarData();
    }, []); // El array vacío asegura que esto se ejecute solo una vez.


    if (!dashboardData) {
        return (
            <div className="space-y-6 animate-pulse">
                <div className="h-8 bg-gray-200 rounded w-1/3"></div>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                    <div className="h-24 bg-gray-200 rounded-lg"></div>
                    <div className="h-24 bg-gray-200 rounded-lg"></div>
                    <div className="h-24 bg-gray-200 rounded-lg"></div>
                    <div className="h-24 bg-gray-200 rounded-lg"></div>
                </div>
                <p className="text-lg text-gray-500">Cargando datos...</p>
            </div>
        );
    }

    const { kpis, low_stock_products } = dashboardData;

    // Componente interno para las tarjetas de Dólar para no repetir código
    const DolarCard = ({ type, data, isLoading }) => {
        let title, borderColor, bgColor, textColor;

        switch(type) {
            case 'blue':
                title = 'Dólar Blue';
                borderColor = 'border-cyan-500';
                bgColor = 'bg-cyan-100';
                textColor = 'text-cyan-600';
                break;
            case 'oficial':
                title = 'Dólar Oficial';
                borderColor = 'border-teal-500';
                bgColor = 'bg-teal-100';
                textColor = 'text-teal-600';
                break;
            case 'mep':
                title = 'Dólar MEP';
                borderColor = 'border-indigo-500';
                bgColor = 'bg-indigo-100';
                textColor = 'text-indigo-600';
                break;
            default:
                title = 'Dólar';
                borderColor = 'border-gray-500';
                bgColor = 'bg-gray-100';
                textColor = 'text-gray-600';
        }
        
        return (
             <div className={`bg-white rounded-lg shadow-md flex flex-col`}>
                <div className={`border-b-4 ${borderColor} rounded-t-lg`}></div>
                <div className="p-5 flex-grow flex flex-col">
                     <div className="flex items-center gap-3 mb-4">
                         <div className={`flex-shrink-0 p-3 rounded-full ${bgColor} ${textColor}`}>
                            <Activity size={22} />
                        </div>
                        <h2 className="text-lg font-semibold text-gray-800">{title}</h2>
                    </div>
                    {isLoading ? (
                        <div className="text-center text-sm text-gray-400 py-4">Cargando...</div>
                    ) : data ? (
                        <div className="text-center flex-grow flex flex-col justify-center">
                            <div className="flex justify-around items-center my-2">
                                <div>
                                    <span className="text-sm text-gray-500">Compra</span>
                                    <p className="text-2xl lg:text-3xl font-bold text-gray-800">${data.value_buy}</p>
                                </div>
                                <div className="border-l h-12 border-gray-200"></div>
                                <div>
                                    <span className="text-sm text-gray-500">Venta</span>
                                    <p className="text-2xl lg:text-3xl font-bold text-gray-800">${data.value_sell}</p>
                                </div>
                            </div>
                        </div>
                    ) : (
                        <div className="text-center text-sm text-red-500 py-4">No disponible</div>
                    )}
                </div>
             </div>
        )
    }

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Dashboard</h1>
            
            {/* Fila de KPIs principales del negocio */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <div className="bg-white border-l-4 border-green-500 rounded-r-lg shadow-md p-5 flex items-center gap-5">
                    <div className="flex-shrink-0 p-3 rounded-full bg-green-100 text-green-600"><DollarSign size={28} /></div>
                    <div>
                        <p className="text-sm text-gray-500 font-medium">Ventas del Día</p>
                        <p className="text-2xl font-bold text-gray-800">${(kpis.ventas_del_dia || 0).toFixed(2)}</p>
                    </div>
                </div>
                <div className="bg-white border-l-4 border-sky-500 rounded-r-lg shadow-md p-5 flex items-center gap-5">
                    <div className="flex-shrink-0 p-3 rounded-full bg-sky-100 text-sky-600"><DollarSign size={28} /></div>
                    <div>
                        <p className="text-sm text-gray-500 font-medium">Ganancia Bruta</p>
                        <p className="text-2xl font-bold text-gray-800">${(kpis.ganancia_bruta_del_dia || 0).toFixed(2)}</p>
                    </div>
                </div>
                <div className="bg-white border-l-4 border-blue-500 rounded-r-lg shadow-md p-5 flex items-center gap-5">
                    <div className="flex-shrink-0 p-3 rounded-full bg-blue-100 text-blue-600"><Users size={28} /></div>
                    <div>
                        <p className="text-sm text-gray-500 font-medium">Ticket Promedio</p>
                        <p className="text-2xl font-bold text-gray-800">${(kpis.ticket_promedio || 0).toFixed(2)}</p>
                    </div>
                </div>
                <div className="bg-white border-l-4 border-purple-500 rounded-r-lg shadow-md p-5 flex items-center gap-5">
                    <div className="flex-shrink-0 p-3 rounded-full bg-purple-100 text-purple-600"><Package size={28} /></div>
                    <div>
                        <p className="text-sm text-gray-500 font-medium">Productos Vendidos</p>
                        <p className="text-2xl font-bold text-gray-800">{kpis.productos_vendidos || 0}</p>
                    </div>
                </div>
            </div>
            
            {/* --- SECCIÓN INFERIOR: STOCK Y COTIZACIONES --- */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <Card>
                    <div className="flex items-center gap-3 mb-4 border-b border-gray-200 pb-3">
                        <AlertTriangle className="text-yellow-500" size={22} />
                        <h2 className="text-lg font-semibold text-gray-800">Productos con Bajo Stock</h2>
                    </div>
                    {low_stock_products && low_stock_products.length > 0 ? (
                        <ul className="space-y-2 max-h-[26rem] overflow-y-auto pr-2">
                            {low_stock_products.map(product => (
                                <li key={product.id} className="p-2 flex justify-between items-center text-sm hover:bg-gray-50 rounded-md">
                                    <span className="text-gray-700">{product.name}</span>
                                    <span className="font-bold text-red-600 bg-red-100 px-2.5 py-1 rounded-full">
                                        {product.stock} unid.
                                    </span>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p className="text-sm text-gray-500 py-4 text-center">¡Felicidades! No hay productos con bajo stock.</p>
                    )}
                </Card>
                
                <div className="space-y-6">
                    <DolarCard type="blue" data={dolarData?.blue} isLoading={loadingDolar} />
                    <DolarCard type="oficial" data={dolarData?.oficial} isLoading={loadingDolar} />
                    <DolarCard type="mep" data={dolarData?.mep} isLoading={loadingDolar} />

                    {dolarData && (
                        <p className="text-xs text-gray-400 text-center -mt-4">
                            Última actualización: {dolarData.lastUpdate.toLocaleString('es-AR')}
                        </p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default DashboardAdmin;
