import React, { useState, useEffect } from 'react';
import { DollarSign, AlertTriangle, Package, Users, Trophy, TrendingUp } from 'lucide-react';

// Asumo que tu componente Card viene de un archivo de UI
// Si no es así, puedes reemplazarlo por un `div` con los estilos correspondientes.
const Card = ({ children, className }) => (
    <div className={`bg-white rounded-lg shadow-md p-6 ${className || ''}`}>
        {children}
    </div>
);


const DashboardAdmin = ({ dashboardData }) => {
    // Estado para guardar los datos de la cotización del dólar
    const [dolarData, setDolarData] = useState(null);
    const [loadingDolar, setLoadingDolar] = useState(true);

    useEffect(() => {
        // --- PASO 1: Fetch de datos del Dólar desde TU BACKEND ---
        // Esta función se ejecutará cuando el componente se monte.
        // Debes crear un endpoint en tu backend Django (ej: /api/dolar)
        // que a su vez llame a la API externa (ej: https://api.bluelytics.com.ar/v2/latest).
        const fetchDolarData = async () => {
            setLoadingDolar(true);
            try {
                // Reemplaza esta URL con el endpoint de tu backend Django
                const response = await fetch('https://api.bluelytics.com.ar/v2/latest');
                
                if (!response.ok) {
                    throw new Error('Error al obtener los datos del dólar');
                }
                
                const data = await response.json();
                // Guardamos la información del dólar blue y oficial en el estado.
                setDolarData({
                    blue: data.blue,
                    oficial: data.oficial,
                    lastUpdate: new Date(data.last_update)
                });

            } catch (error) {
                console.error("No se pudo cargar la cotización del dólar:", error);
                // Opcional: podrías setear un estado de error para mostrarlo en la UI
                setDolarData(null); 
            } finally {
                setLoadingDolar(false);
            }
        };

        fetchDolarData();
    }, []); // El array vacío asegura que esto se ejecute solo una vez.


    if (!dashboardData) {
        return (
            <div className="space-y-6 animate-pulse">
                <div className="h-8 bg-gray-200 rounded w-1/3"></div>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6">
                    <div className="h-24 bg-gray-200 rounded-lg"></div>
                    <div className="h-24 bg-gray-200 rounded-lg"></div>
                    <div className="h-24 bg-gray-200 rounded-lg"></div>
                    <div className="h-24 bg-gray-200 rounded-lg"></div>
                    <div className="h-24 bg-gray-200 rounded-lg"></div>
                </div>
                <p className="text-lg text-gray-500">Cargando datos...</p>
            </div>
        );
    }

    const { kpis, low_stock_products, rankings } = dashboardData;
    const topSoldProducts = rankings?.mas_vendidos || [];

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Dashboard</h1>
            
            {/* --- PASO 2: Mostrar la tarjeta en la UI --- */}
            {/* He modificado el grid para que acepte 5 elementos en pantallas grandes */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6">
                
                {/* Nueva Tarjeta: Cotización Dólar Blue */}
                <div className="bg-white border-l-4 border-cyan-500 rounded-r-lg shadow-md p-5 flex flex-col justify-center gap-2">
                    <div className="flex items-center gap-3">
                         <div className="flex-shrink-0 p-3 rounded-full bg-cyan-100 text-cyan-600">
                            <TrendingUp size={24} />
                        </div>
                        <p className="text-sm text-gray-500 font-medium">Dólar Blue</p>
                    </div>
                    {loadingDolar ? (
                        <div className="text-center text-sm text-gray-400">Cargando...</div>
                    ) : dolarData ? (
                        <div className="text-center">
                            <div className="flex justify-around items-center">
                                <div>
                                    <span className="text-xs text-gray-400">Compra</span>
                                    <p className="text-xl font-bold text-gray-800">${dolarData.blue.value_buy}</p>
                                </div>
                                <div className="border-l h-8 border-gray-200"></div>
                                <div>
                                    <span className="text-xs text-gray-400">Venta</span>
                                    <p className="text-xl font-bold text-gray-800">${dolarData.blue.value_sell}</p>
                                </div>
                            </div>
                            <p className="text-xs text-gray-400 mt-2">
                                Act. {dolarData.lastUpdate.toLocaleTimeString('es-AR', { hour: '2-digit', minute: '2-digit' })}
                            </p>
                        </div>
                    ) : (
                        <div className="text-center text-sm text-red-500">Error</div>
                    )}
                </div>

                {/* Tus Tarjetas de KPIs existentes */}
                <div className="bg-white border-l-4 border-green-500 rounded-r-lg shadow-md p-5 flex items-center gap-5">
                    <div className="flex-shrink-0 p-3 rounded-full bg-green-100 text-green-600"><DollarSign size={28} /></div>
                    <div>
                        <p className="text-sm text-gray-500 font-medium">Ventas del Día</p>
                        <p className="text-2xl font-bold text-gray-800">${(kpis.ventas_del_dia || 0).toFixed(2)}</p>
                    </div>
                </div>

                <div className="bg-white border-l-4 border-sky-500 rounded-r-lg shadow-md p-5 flex items-center gap-5">
                    <div className="flex-shrink-0 p-3 rounded-full bg-sky-100 text-sky-600"><DollarSign size={28} /></div>
                    <div>
                        <p className="text-sm text-gray-500 font-medium">Ganancia Bruta</p>
                        <p className="text-2xl font-bold text-gray-800">${(kpis.ganancia_bruta_del_dia || 0).toFixed(2)}</p>
                    </div>
                </div>

                <div className="bg-white border-l-4 border-blue-500 rounded-r-lg shadow-md p-5 flex items-center gap-5">
                    <div className="flex-shrink-0 p-3 rounded-full bg-blue-100 text-blue-600"><Users size={28} /></div>
                    <div>
                        <p className="text-sm text-gray-500 font-medium">Ticket Promedio</p>
                        <p className="text-2xl font-bold text-gray-800">${(kpis.ticket_promedio || 0).toFixed(2)}</p>
                    </div>
                </div>

                <div className="bg-white border-l-4 border-purple-500 rounded-r-lg shadow-md p-5 flex items-center gap-5">
                    <div className="flex-shrink-0 p-3 rounded-full bg-purple-100 text-purple-600"><Package size={28} /></div>
                    <div>
                        <p className="text-sm text-gray-500 font-medium">Productos Vendidos</p>
                        <p className="text-2xl font-bold text-gray-800">{kpis.productos_vendidos || 0}</p>
                    </div>
                </div>
            </div>
            
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* 1. Lista: Productos con Bajo Stock */}
                <Card>
                    <div className="flex items-center gap-3 mb-4 border-b border-gray-200 pb-3">
                        <AlertTriangle className="text-yellow-500" size={22} />
                        <h2 className="text-lg font-semibold text-gray-800">Productos con Bajo Stock</h2>
                    </div>
                    {low_stock_products && low_stock_products.length > 0 ? (
                        <ul className="space-y-2 max-h-60 overflow-y-auto pr-2">
                            {low_stock_products.map(product => (
                                <li key={product.id} className="p-2 flex justify-between items-center text-sm hover:bg-gray-50 rounded-md">
                                    <span className="text-gray-700">{product.name}</span>
                                    <span className="font-bold text-red-600 bg-red-100 px-2.5 py-1 rounded-full">
                                        {product.stock} unid.
                                    </span>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p className="text-sm text-gray-500 py-4 text-center">¡Felicidades! No hay productos con bajo stock.</p>
                    )}
                </Card>
                
                {/* 2. Lista: Top 5 Productos Vendidos */}
                <Card>
                    <div className="flex items-center gap-3 mb-4 border-b border-gray-200 pb-3">
                        <Trophy className="text-amber-500" size={22} />
                        <h2 className="text-lg font-semibold text-gray-800">Top 5 Productos Vendidos</h2>
                    </div>
                    {topSoldProducts && topSoldProducts.length > 0 ? (
                         <ul className="space-y-2 max-h-60 overflow-y-auto pr-2">
                            {topSoldProducts.slice(0, 5).map((product, index) => (
                                <li key={index} className="p-2 flex justify-between items-center text-sm hover:bg-gray-50 rounded-md">
                                    <div className="flex items-center gap-3">
                                        <span className="font-bold text-gray-500 w-5 text-center">{index + 1}.</span>
                                        <span className="text-gray-700">{product.product__name}</span>
                                    </div>
                                    <span className="font-semibold text-gray-600 bg-gray-100 px-2.5 py-1 rounded-full">
                                        {product.value} vendidos
                                    </span>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p className="text-sm text-gray-500 py-4 text-center">No hay datos de ventas recientes.</p>
                    )}
                </Card>
            </div>
        </div>
    );
};

export default DashboardAdmin;
