import React, { useState, useContext } from 'react';
// AÃ±adimos LayoutGrid y List para los iconos del selector de vista
import { PlusCircle, Edit, Trash2, PackagePlus, LayoutGrid, List } from 'lucide-react';
import { ContextoAuth } from '../../context/AuthContext';
import { Button, Modal, Table } from '../../components/ui/ComponentesUI';
import { useFiltrosYBusqueda } from '../../hooks/useFiltrosYBusqueda';
import { toast } from 'sonner';

// Los componentes de formulario no cambian
const API_URL = 'http://127.0.0.1:8000/api';

const FormularioProducto = ({ producto, onGuardar, onCancelar, categorias, proveedores }) => {
Â  Â  const [formData, setFormData] = useState({ 
Â  Â  Â  Â  ...producto, 
Â  Â  Â  Â  category: producto.category || '', 
Â  Â  Â  Â  provider: producto.provider || null,
Â  Â  Â  Â  estado: producto.estado || 'activo'
Â  Â  });
Â  Â  
Â  Â  const handleChange = (e) => {
Â  Â  Â  Â  const { name, value, type, checked } = e.target;
Â  Â  Â  Â  if (name === 'estado') {
Â  Â  Â  Â  Â  Â  setFormData(prev => ({ ...prev, estado: checked ? 'activo' : 'inactivo' }));
Â  Â  Â  Â  } else {
Â  Â  Â  Â  Â  Â  setFormData(prev => ({ ...prev, [name]: value }));
Â  Â  Â  Â  }
Â  Â  };

Â  Â  return (
Â  Â  Â  Â  <form onSubmit={(e) => { e.preventDefault(); onGuardar(formData); }} className="space-y-4">
Â  Â  Â  Â  Â  Â  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
Â  Â  Â  Â  Â  Â  Â  Â  <div>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-1">Nombre del Producto</label>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <input id="name" name="name" value={formData.name} onChange={handleChange} placeholder="Ej: Coca-Cola 1.5L" className="p-2 border rounded-lg w-full" required/>
Â  Â  Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  Â  Â  <div>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <label htmlFor="sku" className="block text-sm font-medium text-gray-700 mb-1">SKU / CÃ³digo de Barras</label>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <input id="sku" name="sku" value={formData.sku} onChange={handleChange} placeholder="Ej: 7790895000991" className="p-2 border rounded-lg w-full" required/>
Â  Â  Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  <div>
Â  Â  Â  Â  Â  Â  Â  Â  <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">DescripciÃ³n (opcional)</label>
Â  Â  Â  Â  Â  Â  Â  Â  <textarea id="description" name="description" value={formData.description || ''} onChange={handleChange} placeholder="Cualquier detalle adicional..." className="p-2 border rounded-lg w-full min-h-[80px]"/>
Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
Â  Â  Â  Â  Â  Â  Â  Â  <div>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <label htmlFor="cost_price" className="block text-sm font-medium text-gray-700 mb-1">Precio de Costo</label>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <input id="cost_price" name="cost_price" type="number" step="0.01" value={formData.cost_price} onChange={handleChange} placeholder="0.00" className="p-2 border rounded-lg w-full" required/>
Â  Â  Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  Â  Â  <div>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <label htmlFor="sale_price" className="block text-sm font-medium text-gray-700 mb-1">Precio de Venta</label>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <input id="sale_price" name="sale_price" type="number" step="0.01" value={formData.sale_price} onChange={handleChange} placeholder="0.00" className="p-2 border rounded-lg w-full" required/>
Â  Â  Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  Â  Â  <div>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <label htmlFor="stock" className="block text-sm font-medium text-gray-700 mb-1">Stock Actual</label>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <input id="stock" name="stock" type="number" value={formData.stock} onChange={handleChange} placeholder="0" className="p-2 border rounded-lg w-full" required/>
Â  Â  Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
Â  Â  Â  Â  Â  Â  Â  Â  <div>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">CategorÃ­a</label>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <select id="category" name="category" value={formData.category || ''} onChange={handleChange} className="p-2 border rounded-lg w-full" required>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <option value="">Seleccionar CategorÃ­a</option>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  {categorias.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  </select>
Â  Â  Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  Â  Â  <div>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <label htmlFor="provider" className="block text-sm font-medium text-gray-700 mb-1">Proveedor (opcional)</label>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <select id="provider" name="provider" value={formData.provider || ''} onChange={handleChange} className="p-2 border rounded-lg w-full">
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <option value="">Seleccionar Proveedor</option>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  {proveedores.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  </select>
Â  Â  Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  </div>
