// --- COMPONENTE PRINCIPAL ---
const GestionProductos = ({ productos, proveedores, categorias, obtenerDatos, roles }) => {
    const [modalAbierto, setModalAbierto] = useState(false);
    const [stockModalAbierto, setStockModalAbierto] = useState(false);
    const [productoSeleccionado, setProductoSeleccionado] = useState(null);
    const { tokensAuth } = useContext(ContextoAuth);
    const puedeGestionar = roles?.esAdmin || roles?.esSuperAdmin;

    // --- NUEVO ESTADO PARA CONTROLAR LA VISTA (table o cards) ---
    const [viewMode, setViewMode] = useState('table');

    const { datosPaginados, FiltrosUI, PaginacionUI } = useFiltrosYBusqueda({
        items: productos,
        itemsPorPagina: viewMode === 'table' ? 10 : 9, // Menos items por pÃ¡gina en la vista de tarjetas
        logicaDeFiltro: logicaFiltroProductos,
        ComponenteFiltros: FiltroBusquedaProducto,
        filtrosIniciales: { busqueda: '' }
    });

    // El resto de la lÃ³gica (abrir modales, guardar, borrar) no necesita cambios
    const abrirModalNuevo = () => { /* ...sin cambios... */ 
        setProductoSeleccionado({ 
            name: '', sku: '', description: '', cost_price: '', sale_price: '', 
            stock: 1, category: '', provider: null, estado: 'activo'
        });
        setModalAbierto(true); 
    };
    const abrirModalEditar = (producto) => { /* ...sin cambios... */ 
        setProductoSeleccionado({ ...producto, provider: producto.provider || null }); 
        setModalAbierto(true); 
    };
    const abrirModalStock = (producto) => { /* ...sin cambios... */
        setProductoSeleccionado(producto);
        setStockModalAbierto(true);
    };
    const guardarProducto = async (datosProducto) => { /* ...sin cambios... */ 
        const esEditando = !!datosProducto.id;
        const url = esEditando ? `${API_URL}/products/${datosProducto.id}/` : `${API_URL}/products/`;
        const method = esEditando ? 'PUT' : 'POST';
        const payload = { ...datosProducto };
        delete payload.provider_name;
        delete payload.category_name;
        if (payload.provider === '') payload.provider = null;
        
        const promesaDeGuardado = fetch(url, { method, headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) }, body: JSON.stringify(payload) })
            .then(res => {
                if (!res.ok) return res.json().then(err => Promise.reject(err));
                return res.json();
            });

        toast.promise(promesaDeGuardado, {
            loading: 'Guardando producto...',
            success: () => {
                setModalAbierto(false);
                obtenerDatos();
                return `Producto ${esEditando ? 'actualizado' : 'creado'} con Ã©xito.`;
            },
            error: (err) => `Error al guardar: ${JSON.stringify(err)}`
        });
    };
    const guardarStock = async (datosStock) => { /* ...sin cambios... */ 
        if (!productoSeleccionado) return;
        const url = `${API_URL}/products/${productoSeleccionado.id}/update-stock/`;
        
        const promesaDeStock = fetch(url, { 
            method: 'PATCH', 
            headers: { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + String(tokensAuth.access) }, 
            body: JSON.stringify({ stock: datosStock.stock }) 
        }).then(res => {
            if (!res.ok) return res.json().then(err => Promise.reject(err));
            return res.json();
        });

        toast.promise(promesaDeStock, {
            loading: 'AÃ±adiendo stock...',
            success: () => {
                setStockModalAbierto(false);
                obtenerDatos();
                return 'Stock aÃ±adido con Ã©xito.';
            },
            error: (err) => `Error: ${JSON.stringify(err)}`
        });
    };
    const borrarProducto = async (idProducto) => { /* ...sin cambios... */ 
        const promesaDeBorrado = new Promise(async (resolve, reject) => {
            try {
                const response = await fetch(`${API_URL}/products/${idProducto}/`, { 
                    method: 'DELETE', 
                    headers: { 'Authorization': 'Bearer ' + String(tokensAuth.access) }
                });

                if (response.status === 204) {
                    resolve("Producto eliminado con Ã©xito.");
                } else if (response.ok) {
                    const data = await response.json();
                    resolve(data.detail);
                } else {
                    const errorData = await response.json();
                    reject(new Error(JSON.stringify(errorData)));
                }
            } catch (err) {
                reject(err);
            }
        });

        toast.promise(promesaDeBorrado, {
            loading: 'Eliminando producto...',
            success: (mensaje) => {
                obtenerDatos();
                return mensaje;
            },
            error: (err) => `Error al eliminar: ${err.message}`
        });
    };


    return (
        <div className="space-y-6">
            <div className="flex justify-between items-start">
                <h1 className="text-3xl font-bold text-gray-800">GestiÃ³n de Productos</h1>
                {puedeGestionar && (
                    <Button onClick={abrirModalNuevo} icon={PlusCircle}>Nuevo Producto</Button>
                )}
            </div>
            
            <div className="flex justify-between items-center">
                {FiltrosUI}
                {/* --- SELECTOR DE VISTA --- */}
                <div className="flex gap-2 items-center">
                    <span className='text-sm font-medium text-gray-600'>Vista:</span>
                    <button onClick={() => setViewMode('table')} title="Vista de Tabla" className={`p-2 rounded-lg ${viewMode === 'table' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-600 hover:bg-gray-300'}`}>
                        <List size={20} />
                    </button>
                    <button onClick={() => setViewMode('cards')} title="Vista de Tarjetas" className={`p-2 rounded-lg ${viewMode === 'cards' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-600 hover:bg-gray-300'}`}>
                        <LayoutGrid size={20} />
                    </button>
                </div>
            </div>

            {datosPaginados.length > 0 ? (
                <>
                    {/* --- RENDERIZADO CONDICIONAL DE LA VISTA --- */}
                    {viewMode === 'table' ? (
                        // --- VISTA DE TABLA OPTIMIZADA (SoluciÃ³n 3) ---
                        <Table 
                            headers={['SKU', 'Nombre', 'Precio Costo', 'Precio Venta', 'Stock', 'CategorÃ­a', 'Estado', 'Acciones']} 
                            data={datosPaginados} 
                            renderRow={(p) => (
                                <tr key={p.id} className={`border-b hover:bg-gray-50 ${p.estado === 'inactivo' ? 'bg-gray-100 text-gray-500' : 'bg-white'}`}>
                                    {/* Padding reducido a px-4 py-3 */}
                                    <td className="px-4 py-3">{p.sku}</td>
                                    {/* Texto truncado con tooltip */}
                                    <td className="px-4 py-3 font-medium truncate max-w-sm" title={p.name}>{p.name}</td>
                                    <td className="px-4 py-3 text-orange-600 font-medium">${parseFloat(p.cost_price).toFixed(2)}</td>
                                    <td className="px-4 py-3 text-green-600 font-bold">${parseFloat(p.sale_price).toFixed(2)}</td>
                                    <td className="px-4 py-3">
                                        <span className={`px-2 py-1 rounded-full text-xs font-semibold ${p.stock <= 5 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
                                            {p.stock}
                                        </span>
                                    </td>
                                    <td className="px-4 py-3">{p.category_name}</td>
                                    <td className="px-4 py-3">
                                        <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${p.estado === 'activo' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}`}>
                                            {p.estado === 'activo' ? 'Activo' : 'Inactivo'}
                                        </span>
                                    </td>
                                    <td className="px-4 py-3 flex gap-2">
                                        {/* Botones con texto para claridad */}
                                        <Button onClick={() => abrirModalStock(p)} variant="secondary" size="sm" icon={PackagePlus} title="AÃ±adir Stock" />
                                        {puedeGestionar && (
                                            <>
                                                {/* Botones solo con icono para ahorrar espacio */}
                                                <Button onClick={() => abrirModalEditar(p)} variant="secondary" size="sm" icon={Edit} title="Editar Producto"/>
                                                <Button onClick={() => borrarProducto(p.id)} variant="danger" size="sm" icon={Trash2} title="Borrar Producto"/>
                                            </>
                                        )}
                                    </td>
                                </tr>
                            )}
                        />
                    ) : (
                        // --- VISTA DE TARJETAS (SoluciÃ³n 2) ---
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                            {datosPaginados.map(p => (
                                <div key={p.id} className={`bg-white rounded-lg shadow-md p-4 flex flex-col ${p.estado === 'inactivo' ? 'opacity-60' : ''}`}>
                                    <div className="flex-grow mb-4">
                                        <div className="flex justify-between items-start">
                                            <h3 className="font-bold text-lg text-gray-800 pr-2">{p.name}</h3>
                                            <span className={`flex-shrink-0 px-2 py-1 rounded-full text-xs font-semibold ${p.stock <= 5 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
                                                Stock: {p.stock}
                                            </span>
                                        </div>
                                        <p className="text-sm text-gray-500">{p.category_name}</p>
                                        <div className='flex justify-between items-end mt-2'>
                                            <div className='text-sm'>
                                                <span className='text-gray-500'>Costo: </span>
                                                <span className='text-orange-600 font-medium'>${parseFloat(p.cost_price).toFixed(2)}</span>
                                            </div>
                                            <p className="text-xl font-bold text-green-600">${parseFloat(p.sale_price).toFixed(2)}</p>
                                        </div>
                                    </div>
                                    <div className="pt-3 border-t flex gap-2">
                                        <Button onClick={() => abrirModalStock(p)} variant="secondary" size="sm" className="flex-1">AÃ±adir Stock</Button>
                                        {puedeGestionar && (
                                            <>
                                                <Button onClick={() => abrirModalEditar(p)} variant="secondary" size="sm" icon={Edit} />
                                                <Button onClick={() => borrarProducto(p.id)} variant="danger" size="sm" icon={Trash2} />
                                            </>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                    {PaginacionUI}
                </>
            ) : (
                <div className="text-center py-16 px-6 bg-white rounded-lg shadow-md">
                    <h3 className="text-lg font-semibold text-gray-700">No se encontraron productos</h3>
                    <p className="text-gray-500 mt-1">Intenta ajustar los tÃ©rminos de tu bÃºsqueda o crea un producto nuevo.</p>
                </div>
            )}

            {/* Los modales no cambian */}
            {puedeGestionar && (
                <Modal isOpen={modalAbierto} onClose={() => setModalAbierto(false)} title={productoSeleccionado?.id ? 'Editar Producto' : 'Nuevo Producto'}>
                    {productoSeleccionado && <FormularioProducto producto={productoSeleccionado} onGuardar={guardarProducto} onCancelar={() => setModalAbierto(false)} categorias={categorias} proveedores={proveedores}/>}
                </Modal>
            )}
            <Modal isOpen={stockModalAbierto} onClose={() => setStockModalAbierto(false)} title={`AÃ±adir Stock a: ${productoSeleccionado?.name}`}>
                {productoSeleccionado && <FormularioStock onGuardar={guardarStock} onCancelar={() => setStockModalAbierto(false)} />}
            </Modal>
        </div>
    );
};

export default GestionProductos;
