import React, { useState, useMemo } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Search, PlusCircle, Trash2, Edit, Users, ShoppingCart, DollarSign, Package, Truck, BarChart2, AlertCircle, UserPlus, LogOut, ChevronDown, CheckCircle } from 'lucide-react';

// --- Mock Data (Simulando la base de datos de Django) ---
const initialProducts = [
  { id: 1, sku: 'PROD001', name: 'Café Grano Molido 1kg', cost: 8.50, price: 15.00, stock: 50, category: 'Cafetería', providerId: 1 },
  { id: 2, sku: 'PROD002', name: 'Leche Entera 1L', cost: 1.00, price: 1.80, stock: 100, category: 'Lácteos', providerId: 2 },
  { id: 3, sku: 'PROD003', name: 'Croissant de Mantequilla', cost: 0.50, price: 1.20, stock: 4, category: 'Panadería', providerId: 1 },
  { id: 4, sku: 'PROD004', name: 'Jugo de Naranja 500ml', cost: 1.20, price: 2.50, stock: 30, category: 'Bebidas', providerId: 3 },
  { id: 5, sku: 'PROD005', name: 'Sandwich de Jamón y Queso', cost: 2.00, price: 4.50, stock: 25, category: 'Comida Rápida', providerId: 1 },
  { id: 6, sku: 'PROD006', name: 'Té Verde en Saquitos (25u)', cost: 2.50, price: 4.00, stock: 8, category: 'Cafetería', providerId: 3 },
];

const initialProviders = [
  { id: 1, name: 'Distribuidora Central', contact: 'Juan Pérez', phone: '11-1234-5678' },
  { id: 2, name: 'Lácteos del Sur S.A.', contact: 'Ana Gómez', phone: '11-2345-6789' },
  { id: 3, name: 'Bebidas Frescas SRL', contact: 'Carlos López', phone: '11-3456-7890' },
];

const initialClients = [
    { id: 1, name: 'Doña Mabel', email: 'mabel@email.com', phone: '11-4567-8901', totalPurchases: 15, birthday: '1965-05-20' },
    { id: 2, name: 'Estudio Contable Diaz', email: 'contacto@diazcont.com', phone: '11-5678-9012', totalPurchases: 8 },
    { id: 3, name: 'Carlos Rodriguez', email: 'carlos.r@email.com', phone: '11-6789-0123', totalPurchases: 22 },
