const GestionProductos = ({ products, setProducts, providers }) => {
    const [isModalOpen, setModalOpen] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);

    const openModalForNew = () => {
        setEditingProduct({ name: '', sku: '', cost: '', price: '', stock: '', category: '', providerId: '' });
        setModalOpen(true);
    };

    const openModalForEdit = (product) => {
        setEditingProduct(product);
        setModalOpen(true);
    };

    const handleSave = (productData) => {
        // Lógica para guardar (Crear o Actualizar)
        // TODO: Reemplazar con llamada a la API de Django
        if (productData.id) {
            setProducts(products.map(p => p.id === productData.id ? productData : p));
        } else {
            setProducts([...products, { ...productData, id: Date.now() }]); // Simular nuevo ID
        }
        setModalOpen(false);
    };

    const handleDelete = (id) => {
        if(window.confirm("¿Estás seguro de que quieres eliminar este producto?")){
            // TODO: Reemplazar con llamada a la API de Django
            setProducts(products.filter(p => p.id !== id));
        }
    }

    const ProductForm = ({ product, onSave, onCancel }) => {
        const [formData, setFormData] = useState(product);
        const handleChange = (e) => {
            const { name, value } = e.target;
            setFormData(prev => ({ ...prev, [name]: value }));
        };

        return (
            <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <input name="name" value={formData.name} onChange={handleChange} placeholder="Nombre del producto" className="p-2 border rounded-lg w-full"/>
                    <input name="sku" value={formData.sku} onChange={handleChange} placeholder="SKU / Código de Barras" className="p-2 border rounded-lg w-full"/>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <input name="cost" type="number" value={formData.cost} onChange={handleChange} placeholder="Precio de costo" className="p-2 border rounded-lg w-full"/>
                    <input name="price" type="number" value={formData.price} onChange={handleChange} placeholder="Precio de venta" className="p-2 border rounded-lg w-full"/>
                    <input name="stock" type="number" value={formData.stock} onChange={handleChange} placeholder="Stock actual" className="p-2 border rounded-lg w-full"/>
                </div>
                 <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <input name="category" value={formData.category} onChange={handleChange} placeholder="Categoría" className="p-2 border rounded-lg w-full"/>
                    <select name="providerId" value={formData.providerId} onChange={handleChange} className="p-2 border rounded-lg w-full">
                        <option value="">Seleccionar Proveedor</option>
                        {providers.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
                    </select>
                </div>
                <div className="flex justify-end gap-3 pt-4">
                    <Button onClick={onCancel} variant="secondary">Cancelar</Button>
                    <Button onClick={() => onSave(formData)} variant="primary">Guardar Producto</Button>
                </div>
            </div>
        )
    };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center">
                <h1 className="text-3xl font-bold text-gray-800">Gestión de Productos</h1>
                <Button onClick={openModalForNew} icon={PlusCircle}>Nuevo Producto</Button>
            </div>

            <Table
                headers={['SKU', 'Nombre', 'Precio Venta', 'Stock', 'Categoría', 'Acciones']}
                data={products}
                renderRow={(product) => (
                    <tr key={product.id} className="bg-white border-b hover:bg-gray-50">
                        <td className="px-6 py-4 font-medium text-gray-900">{product.sku}</td>
                        <td className="px-6 py-4">{product.name}</td>
                        <td className="px-6 py-4">${product.price.toFixed(2)}</td>
                        <td className="px-6 py-4">
                            <span className={`px-2 py-1 rounded-full text-xs font-semibold ${product.stock <= 5 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
                                {product.stock}
