const ReportesYEstadisticas = ({ products, sales }) => {
    const mostSoldProduct = useMemo(() => {
        const itemCounts = sales.flatMap(s => s.items).reduce((acc, item) => {
            acc[item.name] = (acc[item.name] || 0) + item.quantity;
            return acc;
        }, {});
        return Object.entries(itemCounts).sort((a, b) => b[1] - a[1])[0] || ['N/A', 0];
    }, [sales]);

    const mostProfitableProduct = useMemo(() => {
        return products.map(p => ({
            name: p.name,
            profit: p.price - p.cost
        })).sort((a,b) => b.profit - a.profit)[0] || {name: 'N/A', profit: 0};
    }, [products]);

    const peakHours = useMemo(() => {
        const hours = sales.map(s => new Date(s.date).getHours());
        const hourCounts = hours.reduce((acc, hour) => {
            acc[hour] = (acc[hour] || 0) + 1;
            return acc;
        }, {});
        return Object.entries(hourCounts).sort((a,b) => b[1] - a[1])[0] || ['N/A', 0];
    }, [sales]);

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Reportes y Estadísticas</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                <Card>
                    <h3 className="font-bold text-lg mb-2">Producto Más Vendido</h3>
                    <p className="text-2xl text-indigo-600">{mostSoldProduct[0]}</p>
                    <p className="text-gray-500">{mostSoldProduct[1]} unidades</p>
                </Card>
                <Card>
                    <h3 className="font-bold text-lg mb-2">Producto Más Rentable</h3>
                    <p className="text-2xl text-green-600">{mostProfitableProduct.name}</p>
                    <p className="text-gray-500">Ganancia de ${mostProfitableProduct.profit.toFixed(2)} por unidad</p>
                </Card>
                 <Card>
                    <h3 className="font-bold text-lg mb-2">Hora Pico de Ventas</h3>
                    <p className="text-2xl text-amber-600">{peakHours[0]}:00 - {parseInt(peakHours[0])+1}:00</p>
                    <p className="text-gray-500">{peakHours[1]} ventas</p>
                </Card>
            </div>
             {/* Aquí irían más gráficos y análisis, como productos que se venden juntos, etc. */}
        </div>
    );
};
